#!/usr/bin/env node

/**
 * Check Available Databases Script
 * Lists all databases on the PostgreSQL server
 */

import { Pool } from 'pg';

// Connect to PostgreSQL server (default postgres database)
const SERVER_URL = "postgresql://mydreamv50:123456@139.59.19.202:5432/postgres";

async function checkAvailableDatabases() {
  console.log('🔍 Checking available databases...');

  const pool = new Pool({ 
    connectionString: SERVER_URL,
    ssl: false,
    connectionTimeoutMillis: 10000,
  });

  try {
    console.log('⚡ Connecting to PostgreSQL server...');
    const client = await pool.connect();

    console.log('✅ Connected to PostgreSQL server!');

    // List all databases
    const result = await client.query(`
      SELECT datname 
      FROM pg_database 
      WHERE datistemplate = false
      ORDER BY datname
    `);

    console.log(`📋 Available databases (${result.rows.length}):`);
    result.rows.forEach((row, index) => {
      console.log(`   ${index + 1}. ${row.datname}`);
    });

    // Check if mydreamv50 database exists
    const mydreamExists = result.rows.some(row => row.datname === 'mydreamv50');

    if (mydreamExists) {
      console.log('✅ Database "mydreamv50" exists!');
    } else {
      console.log('❌ Database "mydreamv50" does not exist');
      console.log('💡 Will attempt to create it...');

      // Create the database
      await client.query('CREATE DATABASE mydreamv50');
      console.log('✅ Database "mydreamv50" created successfully!');
    }

    client.release();
    await pool.end();

    console.log('🎉 Database check completed!');

  } catch (error) {
    console.error('❌ Database check failed:');
    console.error('   Error type:', error.code || 'UNKNOWN');
    console.error('   Message:', error.message);

    if (error.code === '42P04') {
      console.log('✅ Database "mydreamv50" already exists!');
    }

    await pool.end();
  }
}

// Run the check
checkAvailableDatabases().catch(console.error);

check-database-data.js.content:
#!/usr/bin/env node

/**
 * Check Database Data Script
 * Checks what tables and data are available in the database
 */

import { Pool } from 'pg';

const DATABASE_URL = "postgresql://mydreamv50:123456@139.59.19.202:5432/postgres";

async function checkDatabaseData() {
  console.log('🔍 Checking database data...');

  const pool = new Pool({ 
    connectionString: DATABASE_URL,
    ssl: false,
    connectionTimeoutMillis: 10000,
  });

  try {
    const client = await pool.connect();
    console.log('✅ Connected to database');

    // Check current user and permissions
    const userResult = await client.query('SELECT current_user, current_database()');
    console.log('👤 Current user:', userResult.rows[0].current_user);
    console.log('🗄️ Current database:', userResult.rows[0].current_database);

    // Check what schemas exist
    const schemasResult = await client.query(`
      SELECT schema_name 
      FROM information_schema.schemata 
      WHERE schema_name NOT IN ('information_schema', 'pg_catalog', 'pg_toast')
      ORDER BY schema_name
    `);
    console.log('📁 Available schemas:', schemasResult.rows.map(row => row.schema_name));

    // Check all tables across all schemas
    const allTablesResult = await client.query(`
      SELECT table_schema, table_name, table_type
      FROM information_schema.tables 
      WHERE table_schema NOT IN ('information_schema', 'pg_catalog', 'pg_toast')
      ORDER BY table_schema, table_name
    `);

    console.log(`📋 Total tables found: ${allTablesResult.rows.length}`);

    if (allTablesResult.rows.length > 0) {
      console.log('📄 Tables by schema:');
      const tablesBySchema = {};
      allTablesResult.rows.forEach(row => {
        if (!tablesBySchema[row.table_schema]) {
          tablesBySchema[row.table_schema] = [];
        }
        tablesBySchema[row.table_schema].push(row.table_name);
      });

      for (const [schema, tables] of Object.entries(tablesBySchema)) {
        console.log(`  ${schema}: ${tables.join(', ')}`);
      }

      // Check data in existing tables
      for (const row of allTablesResult.rows) {
        try {
          const countResult = await client.query(`SELECT COUNT(*) FROM "${row.table_schema}"."${row.table_name}"`);
          const count = countResult.rows[0].count;
          console.log(`📊 ${row.table_schema}.${row.table_name}: ${count} rows`);

          if (count > 0 && count <= 10) {
            // Show sample data for small tables
            const sampleResult = await client.query(`SELECT * FROM "${row.table_schema}"."${row.table_name}" LIMIT 3`);
            if (sampleResult.rows.length > 0) {
              console.log(`   Sample data:`, JSON.stringify(sampleResult.rows[0], null, 2));
            }
          }
        } catch (error) {
          console.log(`⚠️ Cannot access ${row.table_schema}.${row.table_name}: ${error.message}`);
        }
      }
    } else {
      console.log('📭 No tables found in the database');
    }

    // Check permissions on public schema
    try {
      const permResult = await client.query(`
        SELECT grantee, privilege_type 
        FROM information_schema.schema_privileges 
        WHERE schema_name = 'public' AND grantee = current_user
      `);
      console.log('🔐 Current user permissions on public schema:', 
        permResult.rows.map(row => row.privilege_type));
    } catch (error) {
      console.log('⚠️ Cannot check permissions:', error.message);
    }

    client.release();
    await pool.end();

    console.log('🎉 Database data check completed!');

  } catch (error) {
    console.error('❌ Database check failed:');
    console.error('   Error:', error.message);
    await pool.end();
  }
}

checkDatabaseData().catch(console.error);

check-db-permissions.js.content:
import { Pool } from 'pg';

const pool = new Pool({ 
  connectionString: 'postgresql://mydreamv50:123456@139.59.19.202:5432/mydreamv50',
  ssl: false
});

async function checkDatabase() {
  try {
    const client = await pool.connect();

    // Check if tables exist
    const tablesResult = await client.query(`
      SELECT table_name, table_schema 
      FROM information_schema.tables 
      WHERE table_schema = 'public' 
      ORDER BY table_name
    `);

    console.log('Tables in database:');
    tablesResult.rows.forEach(row => {
      console.log('-', row.table_name);
    });

    // Check permissions
    console.log('\nChecking permissions...');
    try {
      const permResult = await client.query(`
        SELECT grantee, table_name, privilege_type 
        FROM information_schema.table_privileges 
        WHERE table_schema = 'public' AND grantee = 'mydreamv50'
        ORDER BY table_name
      `);

      if (permResult.rows.length > 0) {
        console.log('User permissions:');
        permResult.rows.forEach(row => {
          console.log('-', row.table_name, ':', row.privilege_type);
        });
      } else {
        console.log('No specific permissions found for mydreamv50 user');
      }
    } catch (err) {
      console.log('Permission check error:', err.message);
    }

    client.release();
  } catch (err) {
    console.log('Database check failed:', err.message);
  } finally {
    await pool.end();
  }
}

checkDatabase();

check-delivery-partners.js.content:
/**
 * Quick Delivery Partner Status Check
 * Prevents notification issues by checking partner approval status
 */

console.log('🔍 Checking Delivery Partner Status...\n');

fetch('http://localhost:5000/api/admin/delivery-partners')
  .then(response => response.json())
  .then(partners => {
    if (!partners || partners.length === 0) {
      console.log('❌ No delivery partners found');
      return;
    }

    console.log(`📊 Found ${partners.length} delivery partner(s)\n`);

    let issuesFound = 0;
    let healthyPartners = 0;

    partners.forEach(partner => {
      console.log(`👤 ${partner.fullName} (@${partner.username})`);
      console.log(`   User ID: ${partner.userId} | Partner ID: ${partner.id}`);

      const issues = [];

      // Check critical issues
      if (partner.status === 'pending') {
        issues.push('🚨 CRITICAL: Status is "pending" - cannot receive notifications');
      } else if (partner.status === 'rejected') {
        issues.push('❌ Status is "rejected" - notifications disabled');
      } else if (partner.status === 'approved') {
        console.log('   ✅ Status: approved');
      }

      if (!partner.isAvailable) {
        issues.push('⚠️  Not available for deliveries');
      } else {
        console.log('   ✅ Available for deliveries');
      }

      if (issues.length > 0) {
        issuesFound++;
        console.log('   📋 Issues found:');
        issues.forEach(issue => console.log(`      ${issue}`));

        if (partner.status === 'pending') {
          console.log('   🔧 Fix: Go to Admin Dashboard → Delivery Partners → Approve');
        }
      } else {
        healthyPartners++;
        console.log('   ✅ Ready to receive notifications');
      }

      console.log('');
    });

    console.log('📈 SUMMARY:');
    console.log(`   ✅ Ready partners: ${healthyPartners}`);
    console.log(`   ⚠️  Partners needing attention: ${issuesFound}`);

    if (issuesFound === 0) {
      console.log('\n🎉 All delivery partners can receive notifications!');
    } else {
      console.log('\n🔧 Action needed: Check admin dashboard to approve pending partners');
    }
  })
  .catch(error => {
    console.error('❌ Check failed:', error.message);
    console.log('Make sure the server is running on localhost:5000');
  });

