#!/usr/bin/env node

/**
 * Safe Database Migration Script
 * Helps migrate existing data to your new PostgreSQL database
 */

const { Pool } = require('pg');
const fs = require('fs');

// Database URLs
// This script is now configured for your PostgreSQL database only
// const OLD_DATABASE_URL = ""; // No longer needed - using PostgreSQL exclusively
const NEW_DATABASE_URL = "postgresql://mydreamv50:123456@139.59.19.202:5432/mydreamv50";

async function createBackup() {
  console.log('📦 Creating backup of existing data...');

  const oldPool = new Pool({ connectionString: OLD_DATABASE_URL });

  try {
    const client = await oldPool.connect();

    // List of tables to backup (based on your schema)
    const tables = [
      'users', 'admin_users', 'stores', 'categories', 'products', 
      'carts', 'cart_items', 'orders', 'order_items', 'reviews',
      'delivery_partners', 'deliveries', 'notifications', 'coupons',
      'store_analytics', 'inventory_logs', 'support_tickets'
    ];

    const backupData = {};

    for (const table of tables) {
      try {
        const result = await client.query(`SELECT * FROM ${table}`);
        backupData[table] = result.rows;
        console.log(`✅ Backed up ${result.rows.length} records from ${table}`);
      } catch (error) {
        console.log(`⚠️  Table ${table} not found or empty - skipping`);
        backupData[table] = [];
      }
    }

    // Save backup to file
    const backupFile = `backup_${new Date().toISOString().split('T')[0]}.json`;
    fs.writeFileSync(backupFile, JSON.stringify(backupData, null, 2));
    console.log(`💾 Backup saved to ${backupFile}`);

    client.release();
    await oldPool.end();

    return backupFile;

  } catch (error) {
    console.error('❌ Backup failed:', error.message);
    await oldPool.end();
    return null;
  }
}

async function setupNewDatabase() {
  console.log('🔧 Setting up new database schema...');

  const newPool = new Pool({ connectionString: NEW_DATABASE_URL });

  try {
    const client = await newPool.connect();

    // Run the table creation script
    console.log('📋 Creating tables...');

    // Import and run create-tables.js logic here
    // This will create all the necessary tables in your new database

    console.log('✅ Database schema created successfully');

    client.release();
    await newPool.end();

    return true;

  } catch (error) {
    console.error('❌ Schema setup failed:', error.message);
    await newPool.end();
    return false;
  }
}

async function restoreData(backupFile) {
  console.log('📤 Restoring data to new database...');

  if (!fs.existsSync(backupFile)) {
    console.error('❌ Backup file not found');
    return false;
  }

  const backupData = JSON.parse(fs.readFileSync(backupFile, 'utf8'));
  const newPool = new Pool({ connectionString: NEW_DATABASE_URL });

  try {
    const client = await newPool.connect();

    // Restore data table by table
    for (const [table, rows] of Object.entries(backupData)) {
      if (rows.length === 0) continue;

      console.log(`📥 Restoring ${rows.length} records to ${table}...`);

      // Insert data (this is a simplified version - you may need to handle foreign keys)
      for (const row of rows) {
        const columns = Object.keys(row);
        const values = Object.values(row);
        const placeholders = values.map((_, i) => `$${i + 1}`).join(', ');

        try {
          await client.query(
            `INSERT INTO ${table} (${columns.join(', ')}) VALUES (${placeholders})`,
            values
          );
        } catch (error) {
          console.log(`⚠️  Failed to insert record in ${table}:`, error.message);
        }
      }
    }

    console.log('✅ Data restoration completed');

    client.release();
    await newPool.end();

    return true;

  } catch (error) {
    console.error('❌ Data restoration failed:', error.message);
    await newPool.end();
    return false;
  }
}

async function runMigration() {
  console.log('🚀 Starting safe database migration...');
  console.log('📊 This process will:');
  console.log('   1. Backup existing data from current database');
  console.log('   2. Set up schema in your new database');
  console.log('   3. Restore data to new database');
  console.log('   4. Verify data integrity');
  console.log('');

  // Step 1: Create backup
  const backupFile = await createBackup();
  if (!backupFile) {
    console.error('💥 Migration aborted - backup failed');
    return;
  }

  // Step 2: Setup new database
  const schemaSetup = await setupNewDatabase();
  if (!schemaSetup) {
    console.error('💥 Migration aborted - schema setup failed');
    return;
  }

  // Step 3: Restore data
  const dataRestored = await restoreData(backupFile);
  if (!dataRestored) {
    console.error('💥 Migration failed - data restoration incomplete');
    return;
  }

  console.log('🎉 Migration completed successfully!');
  console.log('💡 Next steps:');
  console.log('   • Test your application with the new database');
  console.log('   • Verify all data is present and correct');
  console.log('   • Keep the backup file safe for emergency rollback');
}

// Command line arguments
const command = process.argv[2];

if (command === 'backup') {
  createBackup().then(() => process.exit(0));
} else if (command === 'test') {
  // Test connection to new database
  const pool = new Pool({ connectionString: NEW_DATABASE_URL });
  pool.query('SELECT 1').then(() => {
    console.log('✅ New database connection successful');
    pool.end();
  }).catch(error => {
    console.error('❌ New database connection failed:', error.message);
    pool.end();
  });
} else {
  runMigration().then(() => process.exit(0));
}

prevent-database-crashes.js.content:
#!/usr/bin/env node

/**
 * Siraha Bazaar Database Crash Prevention System
 * Implements automatic safeguards to prevent PostgreSQL crashes
 */

import { Pool } from 'pg';

const DATABASE_URL = process.env.DATABASE_URL;

class CrashPrevention {
  constructor() {
    this.isRunning = false;
    this.safeguards = {
      connectionLimit: true,
      queryTimeout: true,
      memoryMonitoring: true,
      autoCleanup: true,
    };
  }

  // Enhanced connection pool with crash prevention
  createSafePool() {
    return new Pool({
      connectionString: DATABASE_URL,
      max: 15,                    // Limit connections to prevent overload
      min: 2,                     // Keep minimum connections alive
      idleTimeoutMillis: 20000,   // Close idle connections quickly
      connectionTimeoutMillis: 3000, // Fast timeout to prevent hanging
      acquireTimeoutMillis: 5000, // Timeout for acquiring connections
      createTimeoutMillis: 3000,  // Timeout for creating connections
      destroyTimeoutMillis: 5000, // Timeout for destroying connections
      reapIntervalMillis: 1000,   // Check for idle connections frequently
      createRetryIntervalMillis: 200, // Retry connection creation

      // Advanced crash prevention settings
      statement_timeout: 30000,   // Kill queries after 30 seconds
      idle_in_transaction_session_timeout: 10000, // Kill idle transactions
    });
  }

  // Automatic connection cleanup
  async cleanupConnections(pool) {
    try {
      const client = await pool.connect();

      // Kill idle connections older than 5 minutes
      await client.query(`
        SELECT pg_terminate_backend(pid)
        FROM pg_stat_activity
        WHERE state = 'idle'
        AND now() - state_change > interval '5 minutes'
        AND pid != pg_backend_pid()
      `);

      // Kill long-running queries over 2 minutes
      await client.query(`
        SELECT pg_terminate_backend(pid)
        FROM pg_stat_activity
        WHERE state = 'active'
        AND now() - query_start > interval '2 minutes'
        AND pid != pg_backend_pid()
      `);

      client.release();
      console.log('✅ Connection cleanup completed');

    } catch (error) {
      console.error('⚠️ Cleanup failed:', error.message);
    }
  }

  // Memory pressure monitoring
  async checkMemoryPressure(pool) {
    try {
      const client = await pool.connect();

      // Check active connections vs limit
      const result = await client.query(`
        SELECT 
          count(*) as current_connections,
          setting::int as max_connections
        FROM pg_stat_activity, pg_settings 
        WHERE pg_settings.name = 'max_connections'
      `);

      const { current_connections, max_connections } = result.rows[0];
      const usage_percent = (current_connections / max_connections) * 100;

      if (usage_percent > 80) {
        console.warn(`🚨 HIGH CONNECTION USAGE: ${usage_percent}% (${current_connections}/${max_connections})`);
        await this.cleanupConnections(pool);
      }

      client.release();

    } catch (error) {
      console.error('Memory check failed:', error.message);
    }
  }

  // Query performance monitoring
  async monitorSlowQueries(pool) {
    try {
      const client = await pool.connect();

      const result = await client.query(`
        SELECT count(*) as slow_queries
        FROM pg_stat_activity
        WHERE state = 'active'
        AND now() - query_start > interval '10 seconds'
      `);

      const slowQueries = parseInt(result.rows[0].slow_queries);

      if (slowQueries > 3) {
        console.warn(`⚠️ ${slowQueries} slow queries detected - potential performance issue`);
      }

      client.release();

    } catch (error) {
      console.error('Query monitoring failed:', error.message);
    }
  }

  // Disk space monitoring
  async checkDiskSpace(pool) {
    try {
      const client = await pool.connect();

      const result = await client.query(`
        SELECT pg_size_pretty(pg_database_size(current_database())) as db_size,
               pg_database_size(current_database()) as db_size_bytes
      `);

      const { db_size, db_size_bytes } = result.rows[0];
      const sizeGB = db_size_bytes / (1024 * 1024 * 1024);

      if (sizeGB > 1) { // Alert if database > 1GB
        console.warn(`📊 Database size: ${db_size} - Monitor disk space`);
      }

      client.release();

    } catch (error) {
      console.error('Disk space check failed:', error.message);
    }
  }

  // Emergency recovery procedures
  async emergencyRecovery(pool) {
    console.log('🚨 EMERGENCY RECOVERY MODE ACTIVATED');

    try {
      // Force close all idle connections
      await this.cleanupConnections(pool);

      // Reduce memory usage
      const client = await pool.connect();
      await client.query('SET work_mem = "2MB"');
      await client.query('SET shared_buffers = "128MB"');
      client.release();

      console.log('✅ Emergency recovery completed');

    } catch (error) {
      console.error('❌ Emergency recovery failed:', error.message);
      console.log('💡 Manual intervention required - restart PostgreSQL service');
    }
  }

  // Main crash prevention loop
  async startPrevention() {
    if (this.isRunning) {
      console.log('⚠️ Crash prevention already running');
      return;
    }

    this.isRunning = true;
    console.log('🛡️ Starting database crash prevention system...');

    const pool = this.createSafePool();

    // Set up error handlers
    pool.on('error', async (err) => {
      console.error('🚨 Pool error detected:', err.message);

      if (err.code === '53300') { // Too many connections
        await this.emergencyRecovery(pool);
      }
    });

    // Main monitoring loop
    const monitoringInterval = setInterval(async () => {
      try {
        await this.checkMemoryPressure(pool);
        await this.monitorSlowQueries(pool);
        await this.checkDiskSpace(pool);

      } catch (error) {
        console.error('Monitoring error:', error.message);

        if (error.code === 'ECONNREFUSED') {
          console.error('💥 DATABASE CONNECTION LOST');
          await this.emergencyRecovery(pool);
        }
      }
    }, 10000); // Check every 10 seconds

    // Cleanup interval
    const cleanupInterval = setInterval(async () => {
      await this.cleanupConnections(pool);
    }, 60000); // Cleanup every minute

    // Graceful shutdown
    process.on('SIGINT', async () => {
      console.log('\n🛑 Stopping crash prevention...');
      clearInterval(monitoringInterval);
      clearInterval(cleanupInterval);
      await pool.end();
      this.isRunning = false;
      process.exit(0);
    });

    console.log('✅ Crash prevention system is active');
    console.log('📊 Monitoring every 10 seconds, cleanup every minute');

    return pool;
  }

  // Test database resilience
  async testResilience() {
    console.log('🧪 Testing database resilience...');

    const pool = this.createSafePool();

    try {
      // Test connection timeout
      const startTime = Date.now();
      const client = await pool.connect();
      const connectTime = Date.now() - startTime;

      console.log(`⏱️ Connection time: ${connectTime}ms`);

      if (connectTime > 2000) {
        console.warn('⚠️ Slow connection detected');
      }

      // Test query execution
      const queryStart = Date.now();
      await client.query('SELECT version()');
      const queryTime = Date.now() - queryStart;

      console.log(`⚡ Query time: ${queryTime}ms`);

      client.release();
      await pool.end();

      console.log('✅ Resilience test completed');

    } catch (error) {
      console.error('❌ Resilience test failed:', error.message);
      return false;
    }

    return true;
  }
}

// Command line interface
if (import.meta.url === `file://${process.argv[1]}`) {
  const prevention = new CrashPrevention();

  const command = process.argv[2];

  switch (command) {
    case 'start':
      prevention.startPrevention();
      break;

    case 'test':
      prevention.testResilience();
      break;

    default:
      console.log('🛡️ Siraha Bazaar Database Crash Prevention');
      console.log('');
      console.log('Commands:');
      console.log('  start  - Start crash prevention monitoring');
      console.log('  test   - Test database resilience');
      console.log('');
      console.log('Example:');
      console.log('  node prevent-database-crashes.js start');
      break;
  }
}

export default CrashPrevention;

