/**
 * Email Service for Password Reset
 * Provides fallback email functionality when Firebase is not available
 */

import nodemailer from 'nodemailer';

export interface PasswordResetEmail {
  to: string;
  resetToken: string;
  userName: string;
}

export class EmailService {
  private static transporter: any = null;

  static async initialize() {
    try {
      // Try SendGrid first if API key is available
      if (process.env.SENDGRID_API_KEY) {
        this.transporter = nodemailer.createTransport({
          host: 'smtp.sendgrid.net',
          port: 587,
          secure: false,
          auth: {
            user: 'apikey',
            pass: process.env.SENDGRID_API_KEY
          }
        });
        console.log('✅ Email service initialized with SendGrid');
        return true;
      }

      // Fallback to Gmail SMTP if credentials are available
      if (process.env.GMAIL_USER && process.env.GMAIL_APP_PASSWORD) {
        this.transporter = nodemailer.createTransport({
          service: 'gmail',
          auth: {
            user: process.env.GMAIL_USER,
            pass: process.env.GMAIL_APP_PASSWORD
          }
        });
        console.log('✅ Email service initialized with Gmail');
        return true;
      }

      // Try using a temporary Gmail configuration for testing
      const tempGmailUser = process.env.TEMP_GMAIL_USER;
      const tempGmailPass = process.env.TEMP_GMAIL_PASS;
      if (tempGmailUser && tempGmailPass) {
        this.transporter = nodemailer.createTransport({
          host: 'smtp.gmail.com',
          port: 587,
          secure: false,
          auth: {
            user: tempGmailUser,
            pass: tempGmailPass
          }
        });
        console.log('✅ Email service initialized with temporary Gmail configuration');
        return true;
      }

      // Test configuration using Ethereal Email for actual email testing
      try {
        const testAccount = await nodemailer.createTestAccount();
        this.transporter = nodemailer.createTransport({
          host: 'smtp.ethereal.email',
          port: 587,
          secure: false,
          auth: {
            user: testAccount.user,
            pass: testAccount.pass
          }
        });
        console.log('✅ Email service initialized with Ethereal Email for testing');
        console.log('📧 Test emails will be visible at: https://ethereal.email');
        return true;
      } catch (etherealError) {
        console.warn('Ethereal Email setup failed:', etherealError);
      }

      // Final fallback - log emails instead of sending
      this.transporter = nodemailer.createTransport({
        streamTransport: true,
        newline: 'unix',
        buffer: true
      });
      console.log('⚠️ Email service running in development mode - emails will be logged only');
      return true;

    } catch (error) {
      console.warn('Email service initialization failed:', error);
      return false;
    }
  }

  /**
   * Send password reset email with custom template
   */
  static async sendPasswordResetEmail(emailData: PasswordResetEmail): Promise<boolean> {
    try {
      if (!this.transporter) {
        const initialized = await this.initialize();
        if (!initialized) {
          throw new Error('Email service not available');
        }
      }

      const resetLink = `${process.env.FRONTEND_URL || 'http://localhost:5000'}/reset-password?token=${emailData.resetToken}`;

      const htmlContent = `
        <!DOCTYPE html>
        <html>
        <head>
          <meta charset="utf-8">
          <title>Password Reset - Siraha Bazaar</title>
          <style>
            .container { max-width: 600px; margin: 0 auto; font-family: Arial, sans-serif; }
            .header { background: #f8f9fa; padding: 20px; text-align: center; }
            .content { padding: 30px 20px; }
            .button { display: inline-block; padding: 12px 24px; background: #007bff; color: white; text-decoration: none; border-radius: 5px; margin: 20px 0; }
            .footer { background: #f8f9fa; padding: 20px; text-align: center; font-size: 12px; color: #666; }
          </style>
        </head>
        <body>
          <div class="container">
            <div class="header">
              <h1>Siraha Bazaar</h1>
            </div>
            <div class="content">
              <h2>Password Reset Request</h2>
              <p>Hello ${emailData.userName},</p>
              <p>We received a request to reset your password for your Siraha Bazaar account.</p>
              <p>Click the button below to reset your password:</p>
              <a href="${resetLink}" class="button">Reset Password</a>
              <p>If the button doesn't work, copy and paste this link into your browser:</p>
              <p><a href="${resetLink}">${resetLink}</a></p>
              <p>This link will expire in 1 hour for security reasons.</p>
              <p>If you didn't request this password reset, please ignore this email.</p>
            </div>
            <div class="footer">
              <p>© 2025 Siraha Bazaar. All rights reserved.</p>
              <p>Contact us: sirahabazzar@gmail.com | +9779805916598</p>
            </div>
          </div>
        </body>
        </html>
      `;

      const mailOptions = {
        from: '"Siraha Bazaar" <noreply@sirahabazaar.com>',
        to: emailData.to,
        subject: 'Reset Your Password - Siraha Bazaar',
        html: htmlContent,
        text: `
          Password Reset Request - Siraha Bazaar

          Hello ${emailData.userName},

          We received a request to reset your password for your Siraha Bazaar account.

          Please visit this link to reset your password:
          ${resetLink}

          This link will expire in 1 hour for security reasons.

          If you didn't request this password reset, please ignore this email.

          Best regards,
          Siraha Bazaar Team
        `
      };

      if (this.transporter.options && this.transporter.options.streamTransport) {
        // Development mode - just log the email content
        console.log('📧 [DEV MODE] Password reset email (not actually sent):');
        console.log(`To: ${emailData.to}`);
        console.log(`Subject: Reset Your Password - Siraha Bazaar`);
        console.log(`Reset Link: ${resetLink}`);
        console.log('Password reset email logged successfully');
        return true;
      } else {
        // Actually send the email
        const info = await this.transporter.sendMail(mailOptions);
        console.log('Password reset email sent successfully to:', emailData.to);

        // If using Ethereal email, provide preview URL
        if (info.messageId && this.transporter.options && this.transporter.options.host === 'smtp.ethereal.email') {
          const previewURL = nodemailer.getTestMessageUrl(info);
          console.log('📧 Email preview URL:', previewURL);
          console.log('📧 You can view the sent email at: https://ethereal.email');
        }

        return true;
      }
    } catch (error) {
      console.error('Failed to send password reset email:', error);
      return false;
    }
  }

  /**
   * Check if email service is available
   */
  static isAvailable(): boolean {
    return this.transporter !== null;
  }
}

server/firebaseService.ts.content:
import admin from 'firebase-admin';
import { storage } from './storage';

export interface FirebaseNotificationPayload {
  title: string;
  body: string;
  data?: Record<string, string>;
  imageUrl?: string;
}

export interface FirebaseNotificationOptions {
  priority?: 'high' | 'normal';
  timeToLive?: number;
  collapseKey?: string;
  badge?: number;
  sound?: string;
}

export class FirebaseService {
  private static initialized = false;

  static initialize() {
    if (this.initialized) return;

    try {
      // Check if Firebase is already initialized
      if (admin.apps.length > 0) {
        console.log('✅ Firebase already initialized');
        this.initialized = true;
        return;
      }

      // Try to load service account from local file first (more reliable)
      let serviceAccount = null;

      try {
        const fs = require('fs');
        const path = require('path');
        const serviceAccountPath = path.join(process.cwd(), 'firebase-service-account.json');
        if (fs.existsSync(serviceAccountPath)) {
          serviceAccount = fs.readFileSync(serviceAccountPath, 'utf8');
          console.log('✅ Found local Firebase service account file');
        }
      } catch (fileError) {
        console.log('No local service account file found');
      }

      // Fallback to environment variable if local file not found
      if (!serviceAccount) {
        serviceAccount = process.env.FIREBASE_SERVICE_ACCOUNT;
      }

      if (!serviceAccount || serviceAccount.trim() === '') {
        console.log('Firebase service account not configured. Using minimal configuration.');
        // Initialize with minimal config for development
        admin.initializeApp({
          projectId: 'myweb-1c1f37b3',
        });
        this.initialized = true;
        return;
      }

      // Clean and parse service account
      const cleanServiceAccount = serviceAccount.trim();
      let serviceAccountObj;

      try {
        serviceAccountObj = JSON.parse(cleanServiceAccount);
        console.log('✅ Successfully parsed service account JSON');
      } catch (parseError) {
        console.error('Failed to parse Firebase service account JSON:', parseError);
        console.log('Using minimal configuration instead');
        admin.initializeApp({
          projectId: 'myweb-1c1f37b3',
        });
        this.initialized = true;
        return;
      }

      // Initialize Firebase Admin SDK with your Android project configuration
      admin.initializeApp({
        credential: admin.credential.cert(serviceAccountObj),
        projectId: serviceAccountObj.project_id || 'myweb-1c1f37b3',
        databaseURL: `https://myweb-1c1f37b3-default-rtdb.firebaseio.com/`,
        storageBucket: 'myweb-1c1f37b3.firebasestorage.app'
      });

      this.initialized = true;
      console.log('✅ Firebase service initialized successfully with full credentials');
    } catch (error) {
      console.error('Failed to initialize Firebase service:', error);
      // Initialize with minimal config as fallback
      try {
        if (admin.apps.length === 0) {
          admin.initializeApp({
            projectId: 'myweb-1c1f37b3',
          });
        }
        this.initialized = true;
        console.log('✅ Firebase initialized with minimal configuration');
      } catch (fallbackError) {
        console.error('Failed to initialize Firebase with fallback:', fallbackError);
      }
    }
  }

  static isConfigured(): boolean {
    return this.initialized;
  }

  /**
   * Send notification to a specific device token
   */
  static async sendToDevice(
    token: string,
    payload: FirebaseNotificationPayload,
    options: FirebaseNotificationOptions = {}
  ): Promise<boolean> {
    if (!this.isConfigured()) {
      console.warn('Firebase not configured. Cannot send push notification.');
      return false;
    }

    try {
      const message = {
        token,
        notification: {
          title: payload.title,
          body: payload.body,
          imageUrl: payload.imageUrl,
        },
        data: payload.data || {},
        android: {
          priority: options.priority || 'high',
          ttl: options.timeToLive || 3600000,
          collapseKey: options.collapseKey,
          notification: {
            sound: options.sound || 'default',
            clickAction: 'FCM_PLUGIN_ACTIVITY',
            channelId: 'siraha_bazaar_notifications',
            icon: 'ic_notification',
            color: '#0079F2',
          },
        },
        apns: {
          payload: {
            aps: {
              sound: options.sound || 'default',
              badge: options.badge || 1,
              alert: {
                title: payload.title,
                body: payload.body,
              },
            },
          },
          headers: {
            'apns-priority': '10',
            'apns-push-type': 'alert',
          },
        },
        webpush: {
          headers: {
            'Urgency': 'high',
            'TTL': '86400',
          },
          notification: {
            title: payload.title,
            body: payload.body,
            icon: '/favicon.ico',
            badge: '/favicon.ico',
            data: payload.data,
            requireInteraction: false,
            silent: false,
            tag: payload.data?.type || 'general',
          },
        },
      };

      const response = await admin.messaging().send(message);
      console.log('Successfully sent message:', response);
      return true;
    } catch (error) {
      console.error('Error sending message:', error);
      return false;
    }
  }

  /**
   * Send notification to multiple devices
   */
  static async sendToMultipleDevices(
    tokens: string[],
    payload: FirebaseNotificationPayload,
    options: FirebaseNotificationOptions = {}
  ): Promise<{ successCount: number; failureCount: number }> {
    if (!this.isConfigured()) {
      console.warn('Firebase not configured. Cannot send push notifications.');
      return { successCount: 0, failureCount: tokens.length };
    }

    if (tokens.length === 0) {
      return { successCount: 0, failureCount: 0 };
    }

    try {
      const message = {
        notification: {
          title: payload.title,
          body: payload.body,
          imageUrl: payload.imageUrl,
        },
        data: payload.data || {},
        android: {
          priority: options.priority || 'high',
          ttl: options.timeToLive || 3600000,
          collapseKey: options.collapseKey,
          notification: {
            sound: options.sound || 'default',
            clickAction: 'FCM_PLUGIN_ACTIVITY',
            channelId: 'siraha_bazaar_notifications',
            icon: 'ic_notification',
            color: '#0079F2',
          },
        },
        apns: {
          payload: {
            aps: {
              sound: options.sound || 'default',
              badge: options.badge || 1,
              alert: {
                title: payload.title,
                body: payload.body,
              },
            },
          },
          headers: {
            'apns-priority': '10',
            'apns-push-type': 'alert',
          },
        },
        webpush: {
          headers: {
            'Urgency': 'high',
            'TTL': '86400',
          },
          notification: {
            title: payload.title,
            body: payload.body,
            icon: '/favicon.ico',
            badge: '/favicon.ico',
            data: payload.data,
            requireInteraction: false,
            silent: false,
            tag: payload.data?.type || 'general',
          },
        },
        tokens,
      };

      const response = await admin.messaging().sendEachForMulticast(message);
      console.log(`Successfully sent ${response.successCount} messages`);
      console.log(`Failed to send ${response.failureCount} messages`);

      return {
        successCount: response.successCount,
        failureCount: response.failureCount,
      };
    } catch (error) {
      console.error('Error sending messages:', error);
      return { successCount: 0, failureCount: tokens.length };
    }
  }

  /**
   * Send notification to users by role
   */
  static async sendToUsersByRole(
    role: string,
    payload: FirebaseNotificationPayload,
    options: FirebaseNotificationOptions = {}
  ): Promise<{ successCount: number; failureCount: number }> {
    try {
      // Get all device tokens for users with the specified role
      const tokens = await storage.getDeviceTokensByRole(role);

      if (tokens.length === 0) {
        console.log(`No device tokens found for role: ${role}`);
        return { successCount: 0, failureCount: 0 };
      }

      return await this.sendToMultipleDevices(tokens, payload, options);
    } catch (error) {
      console.error('Error sending notifications by role:', error);
      return { successCount: 0, failureCount: 0 };
    }
  }

  /**
   * Send order notification to customer
   */
  static async sendOrderNotification(
    userId: number,
    orderId: number,
    status: string,
    customMessage?: string
  ): Promise<boolean> {
    const statusMessages = {
      pending: 'Your order has been placed successfully!',
      processing: 'Your order is being prepared',
      shipped: 'Your order has been shipped',
      out_for_delivery: 'Your order is out for delivery',
      delivered: 'Your order has been delivered',
      cancelled: 'Your order has been cancelled',
    };

    const payload: FirebaseNotificationPayload = {
      title: 'Order Update',
      body: customMessage || statusMessages[status as keyof typeof statusMessages] || `Order status: ${status}`,
      data: {
        type: 'order_update',
        orderId: orderId.toString(),
        status,
        userId: userId.toString(),
      },
    };

    try {
      const tokens = await storage.getDeviceTokensByUserId(userId);

      if (tokens.length === 0) {
        console.log(`No device tokens found for user: ${userId}`);
        return false;
      }

      const result = await this.sendToMultipleDevices(tokens, payload, { priority: 'high' });
      return result.successCount > 0;
    } catch (error) {
      console.error('Error sending order notification:', error);
      return false;
    }
  }

  /**
   * Send delivery assignment notification
   */
  static async sendDeliveryAssignmentNotification(
    deliveryPartnerId: number,
    orderId: number,
    pickupAddress: string,
    deliveryAddress: string
  ): Promise<boolean> {
    const payload: FirebaseNotificationPayload = {
      title: 'New Delivery Assignment',
      body: `New delivery from ${pickupAddress} to ${deliveryAddress}`,
      data: {
        type: 'delivery_assignment',
        orderId: orderId.toString(),
        deliveryPartnerId: deliveryPartnerId.toString(),
        pickupAddress,
        deliveryAddress,
      },
    };

    try {
      const tokens = await storage.getDeviceTokensByUserId(deliveryPartnerId);

      if (tokens.length === 0) {
        console.log(`No device tokens found for delivery partner: ${deliveryPartnerId}`);
        return false;
      }

      const result = await this.sendToMultipleDevices(tokens, payload, { 
        priority: 'high',
        sound: 'delivery_alert',
      });
      return result.successCount > 0;
    } catch (error) {
      console.error('Error sending delivery assignment notification:', error);
      return false;
    }
  }

  /**
   * Send promotional notification to customers
   */
  static async sendPromotionalNotification(
    title: string,
    message: string,
    imageUrl?: string,
    targetUserIds?: number[]
  ): Promise<{ successCount: number; failureCount: number }> {
    const payload: FirebaseNotificationPayload = {
      title,
      body: message,
      imageUrl,
      data: {
        type: 'promotion',
        timestamp: Date.now().toString(),
      },
    };

    try {
      let tokens: string[];

      if (targetUserIds && targetUserIds.length > 0) {
        // Send to specific users
        tokens = await storage.getDeviceTokensByUserIds(targetUserIds);
      } else {
        // Send to all customers
        tokens = await storage.getDeviceTokensByRole('customer');
      }

      if (tokens.length === 0) {
        console.log('No device tokens found for promotional notification');
        return { successCount: 0, failureCount: 0 };
      }

      return await this.sendToMultipleDevices(tokens, payload, { priority: 'normal' });
    } catch (error) {
      console.error('Error sending promotional notification:', error);
      return { successCount: 0, failureCount: 0 };
    }
  }

  /**
   * Subscribe device token to topic
   */
  static async subscribeToTopic(tokens: string[], topic: string): Promise<boolean> {
    if (!this.isConfigured()) {
      return false;
    }

    try {
      const response = await admin.messaging().subscribeToTopic(tokens, topic);
      console.log(`Successfully subscribed ${response.successCount} tokens to topic: ${topic}`);
      return response.successCount > 0;
    } catch (error) {
      console.error('Error subscribing to topic:', error);
      return false;
    }
  }

  /**
   * Send notification to topic
   */
  static async sendToTopic(
    topic: string,
    payload: FirebaseNotificationPayload,
    options: FirebaseNotificationOptions = {}
  ): Promise<boolean> {
    if (!this.isConfigured()) {
      return false;
    }

    try {
      const message = {
        topic,
        notification: {
          title: payload.title,
          body: payload.body,
          imageUrl: payload.imageUrl,
        },
        data: payload.data || {},
        android: {
          priority: options.priority || 'high',
          ttl: options.timeToLive || 3600000,
          notification: {
            sound: options.sound || 'default',
            clickAction: 'FLUTTER_NOTIFICATION_CLICK',
            channelId: 'siraha_bazaar_notifications',
          },
        },
        apns: {
          payload: {
            aps: {
              sound: options.sound || 'default',
              badge: options.badge || 1,
            },
          },
        },
      };

      const response = await admin.messaging().send(message);
      console.log('Successfully sent topic message:', response);
      return true;
    } catch (error) {
      console.error('Error sending topic message:', error);
      return false;
    }
  }
}

// Initialize Firebase service
FirebaseService.initialize();

export default FirebaseService;

