  // Comprehensive user deletion for admin purposes
  async deleteUserCompletely(userId: number, adminId: number): Promise<{ success: boolean; deletedData: any }> {
    const deletedData = {
      user: null,
      stores: [],
      products: [],
      orders: [],
      orderItems: [],
      cartItems: [],
      wishlistItems: [],
      notifications: [],
      reviews: [],
      deliveryPartner: null,
      deviceTokens: []
    };

    try {
      console.log(`🗑️ Admin ${adminId} initiating complete deletion of user ${userId}`);

      // Get user data before deletion
      const user = await this.getUser(userId);
      if (!user) {
        return { success: false, deletedData: null };
      }
      deletedData.user = user;

      // 1. Get and delete user's stores and all associated data
      const userStores = await db.select().from(stores).where(eq(stores.ownerId, userId));
      deletedData.stores = userStores;

      for (const store of userStores) {
        // Get store products
        const storeProducts = await db.select().from(products).where(eq(products.storeId, store.id));
        deletedData.products.push(...storeProducts);

        // Delete product-related data for each product
        for (const product of storeProducts) {
          // Delete product reviews and review likes
          await db.delete(reviewLikes).where(eq(reviewLikes.reviewId, sql`(SELECT id FROM ${productReviews} WHERE ${productReviews.productId} = ${product.id})`));
          await db.delete(productReviews).where(eq(productReviews.productId, product.id));

          // Delete product attributes
          await db.delete(productAttributes).where(eq(productAttributes.productId, product.id));

          // Delete cart items containing this product
          await db.delete(cartItems).where(eq(cartItems.productId, product.id));

          // Delete wishlist items containing this product
          await db.delete(wishlistItems).where(eq(wishlistItems.productId, product.id));
        }

        // Delete all products for this store
        await db.delete(products).where(eq(products.storeId, store.id));

        // Delete store analytics
        await db.delete(storeAnalytics).where(eq(storeAnalytics.storeId, store.id));

        // Delete store reviews and likes
        await db.delete(storeReviewLikes).where(eq(storeReviewLikes.reviewId, sql`(SELECT id FROM ${storeReviews} WHERE ${storeReviews.storeId} = ${store.id})`));
        await db.delete(storeReviews).where(eq(storeReviews.storeId, store.id));

        // Delete store promotions
        await db.delete(promotions).where(eq(promotions.storeId, store.id));

        // Delete store advertisements
        await db.delete(advertisements).where(eq(advertisements.storeId, store.id));

        // Delete inventory logs
        await db.delete(inventoryLogs).where(eq(inventoryLogs.storeId, store.id));

        // Delete order items for this store
        const storeOrderItems = await db.select().from(orderItems).where(eq(orderItems.storeId, store.id));
        deletedData.orderItems.push(...storeOrderItems);
        await db.delete(orderItems).where(eq(orderItems.storeId, store.id));
      }

      // Delete all stores owned by user
      await db.delete(stores).where(eq(stores.ownerId, userId));

      // 2. Handle delivery partner data if user is a delivery partner
      const deliveryPartner = await db.select().from(deliveryPartners).where(eq(deliveryPartners.userId, userId));
      if (deliveryPartner.length > 0) {
        deletedData.deliveryPartner = deliveryPartner[0];

        // Delete delivery assignments and related data
        await db.delete(deliveries).where(eq(deliveries.deliveryPartnerId, deliveryPartner[0].id));

        // Delete delivery partner record
        await db.delete(deliveryPartners).where(eq(deliveryPartners.userId, userId));
      }

      // 3. Get and delete user's orders (as customer)
      const userOrders = await db.select().from(orders).where(eq(orders.customerId, userId));
      deletedData.orders = userOrders;

      for (const order of userOrders) {
        // Delete order tracking
        await db.delete(orderTracking).where(eq(orderTracking.orderId, order.id));

        // Delete order items
        const orderItemsForOrder = await db.select().from(orderItems).where(eq(orderItems.orderId, order.id));
        deletedData.orderItems.push(...orderItemsForOrder);
        await db.delete(orderItems).where(eq(orderItems.orderId, order.id));

        // Delete deliveries for this order
        await db.delete(deliveries).where(eq(deliveries.orderId, order.id));

        // Delete payment transactions
        await db.delete(paymentTransactions).where(sql`${paymentTransactions.data}->>'orderId' = ${order.id.toString()}`);
      }

      // Delete all orders by user
      await db.delete(orders).where(eq(orders.customerId, userId));

      // 4. Delete user's personal data
      // Delete cart items
      const userCartItems = await db.select().from(cartItems).where(eq(cartItems.userId, userId));
      deletedData.cartItems = userCartItems;
      await db.delete(cartItems).where(eq(cartItems.userId, userId));

      // Delete wishlist items
      const userWishlistItems = await db.select().from(wishlistItems).where(eq(wishlistItems.userId, userId));
      deletedData.wishlistItems = userWishlistItems;
      await db.delete(wishlistItems).where(eq(wishlistItems.userId, userId));

      // Delete user's reviews and review likes
      const userReviews = await db.select().from(productReviews).where(eq(productReviews.userId, userId));
      deletedData.reviews = userReviews;

      for (const review of userReviews) {
        await db.delete(reviewLikes).where(eq(reviewLikes.reviewId, review.id));
      }
      await db.delete(productReviews).where(eq(productReviews.userId, userId));
      await db.delete(storeReviews).where(eq(storeReviews.userId, userId));

      // Delete user's review likes on other reviews
      await db.delete(reviewLikes).where(eq(reviewLikes.userId, userId));
      await db.delete(storeReviewLikes).where(eq(storeReviewLikes.userId, userId));

      // Delete notifications
      const userNotifications = await db.select().from(notifications).where(eq(notifications.userId, userId));
      deletedData.notifications = userNotifications;
      await db.delete(notifications).where(eq(notifications.userId, userId));

      // Delete push notification tokens
      const userTokens = await db.select().from(pushNotificationTokens).where(eq(pushNotificationTokens.userId, userId));
      deletedData.deviceTokens = userTokens;
      await db.delete(pushNotificationTokens).where(eq(pushNotificationTokens.userId, userId));

      // Delete password reset tokens
      await db.delete(passwordResetTokens).where(eq(passwordResetTokens.userId, userId));

      // Delete website visits
      await db.delete(websiteVisits).where(eq(websiteVisits.userId, userId));

      // Delete support tickets
      await db.delete(supportTickets).where(eq(supportTickets.userId, userId));

      // 5. Finally delete the user account
      await db.delete(users).where(eq(users.id, userId));

      // Log the deletion for audit purposes
      await this.createAdminLog({
        adminId,
        action: 'USER_COMPLETE_DELETE',
        description: `Completely deleted user ${user.email} (ID: ${userId}) and all associated data`,
        metadata: JSON.stringify({
          deletedUserId: userId,
          deletedUserEmail: user.email,
          storesDeleted: deletedData.stores.length,
          productsDeleted: deletedData.products.length,
          ordersDeleted: deletedData.orders.length
        })
      });

      console.log(`✅ Successfully deleted user ${userId} and all associated data`);
      return { success: true, deletedData };

    } catch (error) {
      console.error(`❌ Error deleting user ${userId} completely:`, error);
      return { success: false, deletedData: null };
    }
  }
}

import { MemoryStorage } from "./memory-storage";

// Function to test database connectivity
async function testDatabaseConnection(): Promise<boolean> {
  try {
    const testStorage = new DatabaseStorage();
    await testStorage.getAllCategories();
    return true;
  } catch (error) {
    console.error('Database connection test failed:', error);
    return false;
  }
}

// Create and export the storage instance with fallback
async function createStorage(): Promise<IStorage> {
  const isConnected = await testDatabaseConnection();

  if (isConnected) {
    console.log('✅ Using DatabaseStorage');
    return new DatabaseStorage();
  } else {
    console.log('⚠️ Database connection failed, falling back to MemoryStorage with sample data');
    return new MemoryStorage();
  }
}

// Export storage promise that resolves to the appropriate storage
export const storagePromise = createStorage();

// Use database storage directly
export const storage = new DatabaseStorage();

server/trackingService.ts.content:
import { db } from './db';
import { deliveryLocationTracking, deliveryRoutes, deliveryStatusHistory, deliveries, orders, orderItems, users, stores } from '@shared/schema';
import { eq, and, desc } from 'drizzle-orm';
import { leafletMapService } from './services/leafletMapService';
import { NotificationService } from './notificationService';

export interface LocationUpdate {
  deliveryId: number;
  latitude: number;
  longitude: number;
  heading?: number;
  speed?: number;
  accuracy?: number;
}

export interface TrackingData {
  delivery: any;
  currentLocation: {
    latitude: number;
    longitude: number;
    timestamp: Date;
  };
  route: {
    pickupLocation: { lat: number; lng: number };
    deliveryLocation: { lat: number; lng: number };
    polyline: string;
    distance: number;
    estimatedDuration: number;
  };
  statusHistory: any[];
}

export class TrackingService {
  /**
   * Initialize delivery tracking when order is assigned to delivery partner
   */
  async initializeDeliveryTracking(deliveryId: number) {
    try {
      // Get delivery details
      const delivery = await db
        .select()
        .from(deliveries)
        .where(eq(deliveries.id, deliveryId))
        .limit(1);

      if (!delivery.length) {
        throw new Error('Delivery not found');
      }

      const deliveryData = delivery[0];

      // Get store location (pickup) - get first store from order items
      const orderItemsQuery = await db
        .select({ storeId: orderItems.storeId })
        .from(orderItems)
        .where(eq(orderItems.orderId, deliveryData.orderId))
        .limit(1);

      if (!orderItemsQuery.length) {
        throw new Error('No order items found');
      }

      const storeQuery = await db
        .select({
          id: stores.id,
          name: stores.name,
          latitude: stores.latitude,
          longitude: stores.longitude,
          address: stores.address
        })
        .from(stores)
        .where(eq(stores.id, orderItemsQuery[0].storeId))
        .limit(1);

      if (!storeQuery.length) {
        throw new Error('Store not found for delivery');
      }

      const store = storeQuery[0];

      // Get customer location (delivery)
      const order = await db
        .select({
          latitude: orders.latitude,
          longitude: orders.longitude,
          shippingAddress: orders.shippingAddress
        })
        .from(orders)
        .where(eq(orders.id, deliveryData.orderId))
        .limit(1);

      if (!order.length || !order[0].latitude || !order[0].longitude) {
        throw new Error('Customer location not available');
      }

      const customerLocation = order[0];

      // Calculate route using Leaflet Maps service
      const routeInfo = await leafletMapService.calculateRoute({
        origin: { lat: Number(store.latitude), lng: Number(store.longitude) },
        destination: { lat: Number(customerLocation.latitude), lng: Number(customerLocation.longitude) },
        mode: 'cycling'
      });

      // Store route information
      await db.insert(deliveryRoutes).values({
        deliveryId: deliveryId,
        pickupLatitude: store.latitude,
        pickupLongitude: store.longitude,
        deliveryLatitude: customerLocation.latitude,
        deliveryLongitude: customerLocation.longitude,
        routeGeometry: routeInfo.polyline,
        distanceMeters: routeInfo.distance,
        estimatedDurationSeconds: routeInfo.duration,
        hereRouteId: null // Not using HERE Maps anymore
      });

      // Add initial status history
      await this.updateDeliveryStatus(deliveryId, 'assigned', 'Delivery partner assigned', {
        lat: Number(store.latitude),
        lng: Number(store.longitude)
      });

      // Send notification to delivery partner
      if (deliveryData.deliveryPartnerId) {
        await NotificationService.sendDeliveryAssignmentNotification(
          deliveryData.deliveryPartnerId,
          deliveryData.orderId,
          store.address,
          customerLocation.shippingAddress
        );
      }

      return {
        success: true,
        routeInfo,
        pickupLocation: { lat: Number(store.latitude), lng: Number(store.longitude) },
        deliveryLocation: { lat: Number(customerLocation.latitude), lng: Number(customerLocation.longitude) }
      };

    } catch (error) {
      console.error('Error initializing delivery tracking:', error);
      throw error;
    }
  }

  /**
   * Update delivery partner's real-time location
   */
  async updateDeliveryLocation(locationUpdate: LocationUpdate) {
    try {
      // Insert location tracking record
      await db.insert(deliveryLocationTracking).values({
        deliveryId: locationUpdate.deliveryId,
        deliveryPartnerId: await this.getDeliveryPartnerId(locationUpdate.deliveryId),
        currentLatitude: locationUpdate.latitude.toString(),
        currentLongitude: locationUpdate.longitude.toString(),
        heading: locationUpdate.heading?.toString(),
        speed: locationUpdate.speed?.toString(),
        accuracy: locationUpdate.accuracy?.toString(),
        isActive: true
      });

      // Broadcast location update via WebSocket
      await this.broadcastLocationUpdate(locationUpdate);

      return { success: true };
    } catch (error) {
      console.error('Error updating delivery location:', error);
      throw error;
    }
  }

  /**
   * Update delivery status with location and notification
   */
  async updateDeliveryStatus(
    deliveryId: number, 
    status: string, 
    description?: string, 
    location?: { lat: number; lng: number },
    updatedBy?: number
  ) {
    try {
      // Update delivery status
      await db
        .update(deliveries)
        .set({ status })
        .where(eq(deliveries.id, deliveryId));

      // Add to status history
      await db.insert(deliveryStatusHistory).values({
        deliveryId,
        status,
        description,
        latitude: location?.lat.toString(),
        longitude: location?.lng.toString(),
        updatedBy
      });

      // Get delivery and order info for notifications
      const deliveryInfo = await db
        .select({
          orderId: deliveries.orderId,
          customerId: orders.customerId,
          deliveryPartnerId: deliveries.deliveryPartnerId
        })
        .from(deliveries)
        .innerJoin(orders, eq(orders.id, deliveries.orderId))
        .where(eq(deliveries.id, deliveryId))
        .limit(1);

      if (deliveryInfo.length) {
        const info = deliveryInfo[0];

        // Send notification to customer
        await NotificationService.sendOrderStatusUpdateToCustomer(
          info.customerId,
          info.orderId,
          status,
          description
        );

        // Broadcast status update via WebSocket
        await this.broadcastStatusUpdate(deliveryId, status, description, location);
      }

      return { success: true };
    } catch (error) {
      console.error('Error updating delivery status:', error);
      throw error;
    }
  }

  /**
   * Get comprehensive tracking data for a delivery
   */
  async getTrackingData(deliveryId: number): Promise<TrackingData> {
    try {
      // Get delivery information
      const delivery = await db
        .select()
        .from(deliveries)
        .where(eq(deliveries.id, deliveryId))
        .limit(1);

      if (!delivery.length) {
        throw new Error('Delivery not found');
      }

      // Get current location
      const currentLocation = await db
        .select()
        .from(deliveryLocationTracking)
        .where(and(
          eq(deliveryLocationTracking.deliveryId, deliveryId),
          eq(deliveryLocationTracking.isActive, true)
        ))
        .orderBy(desc(deliveryLocationTracking.timestamp))
        .limit(1);

      // Get route information
      const route = await db
        .select()
        .from(deliveryRoutes)
        .where(eq(deliveryRoutes.deliveryId, deliveryId))
        .limit(1);

      // Get status history
      const statusHistory = await db
        .select()
        .from(deliveryStatusHistory)
        .where(eq(deliveryStatusHistory.deliveryId, deliveryId))
        .orderBy(desc(deliveryStatusHistory.timestamp));

      return {
        delivery: delivery[0],
        currentLocation: currentLocation.length ? {
          latitude: Number(currentLocation[0].currentLatitude),
          longitude: Number(currentLocation[0].currentLongitude),
          timestamp: currentLocation[0].timestamp
        } : null,
        route: route.length ? {
          pickupLocation: {
            lat: Number(route[0].pickupLatitude),
            lng: Number(route[0].pickupLongitude)
          },
          deliveryLocation: {
            lat: Number(route[0].deliveryLatitude),
            lng: Number(route[0].deliveryLongitude)
          },
          polyline: route[0].routeGeometry || '',
          distance: route[0].distanceMeters || 0,
          estimatedDuration: route[0].estimatedDurationSeconds || 0
        } : null,
        statusHistory: statusHistory
      };
    } catch (error) {
      console.error('Error getting tracking data:', error);
      throw error;
    }
  }

  /**
   * Get delivery partner ID for a delivery
   */
  private async getDeliveryPartnerId(deliveryId: number): Promise<number> {
    const delivery = await db
      .select({ deliveryPartnerId: deliveries.deliveryPartnerId })
      .from(deliveries)
      .where(eq(deliveries.id, deliveryId))
      .limit(1);

    if (!delivery.length || !delivery[0].deliveryPartnerId) {
      throw new Error('Delivery partner not found');
    }

    return delivery[0].deliveryPartnerId;
  }

  /**
   * Broadcast location update to connected clients
   */
  private async broadcastLocationUpdate(locationUpdate: LocationUpdate) {
    // This will be implemented with WebSocket integration
    // For now, we'll just log the update
    console.log('Broadcasting location update:', locationUpdate);
  }

  /**
   * Broadcast status update to connected clients
   */
  private async broadcastStatusUpdate(
    deliveryId: number, 
    status: string, 
    description?: string, 
    location?: { lat: number; lng: number }
  ) {
    // This will be implemented with WebSocket integration
    console.log('Broadcasting status update:', { deliveryId, status, description, location });
  }

  /**
   * Get deliveries for a delivery partner
   */
  async getDeliveryPartnerDeliveries(deliveryPartnerId: number) {
    try {
      const deliveriesData = await db
        .select({
          id: deliveries.id,
          orderId: deliveries.orderId,
          status: deliveries.status,
          pickupAddress: deliveries.pickupAddress,
          deliveryAddress: deliveries.deliveryAddress,
          deliveryFee: deliveries.deliveryFee,
          specialInstructions: deliveries.specialInstructions,
          assignedAt: deliveries.assignedAt,
          customerName: orders.customerName,
          customerPhone: orders.phone,
          totalAmount: orders.totalAmount
        })
        .from(deliveries)
        .innerJoin(orders, eq(orders.id, deliveries.orderId))
        .where(eq(deliveries.deliveryPartnerId, deliveryPartnerId))
        .orderBy(desc(deliveries.assignedAt));

      return deliveriesData;
    } catch (error) {
      console.error('Error getting delivery partner deliveries:', error);
      throw error;
    }
  }
}

export const trackingService = new TrackingService();

server/unsplashService.ts.content:
import fetch from 'node-fetch';

export interface UnsplashImage {
  id: string;
  urls: {
    raw: string;
    full: string;
    regular: string;
    small: string;
    thumb: string;
  };
  alt_description: string | null;
  description: string | null;
  user: {
    name: string;
    username: string;
  };
  links: {
    download: string;
    html: string;
  };
}

export interface UnsplashSearchResponse {
  total: number;
  total_pages: number;
  results: UnsplashImage[];
}

export class UnsplashService {
  private accessKey: string;
  private baseUrl = 'https://api.unsplash.com';

  constructor() {
    this.accessKey = process.env.UNSPLASH_ACCESS_KEY || '';
    if (!this.accessKey) {
      console.warn('Unsplash Access Key not configured. Image fetching will be limited.');
    }
  }

  isConfigured(): boolean {
    return !!this.accessKey;
  }

  /**
   * Search for images by query
   */
  async searchImages(
    query: string, 
    page: number = 1, 
    perPage: number = 12
  ): Promise<UnsplashSearchResponse | null> {
    if (!this.isConfigured()) {
      console.error('Unsplash service not configured');
      return null;
    }

    try {
      const response = await fetch(
        `${this.baseUrl}/search/photos?query=${encodeURIComponent(query)}&page=${page}&per_page=${perPage}`,
        {
          headers: {
            'Authorization': `Client-ID ${this.accessKey}`,
            'Accept-Version': 'v1'
          }
        }
      );

      if (!response.ok) {
        throw new Error(`Unsplash API error: ${response.status}`);
      }

      const data = await response.json() as UnsplashSearchResponse;
      return data;
    } catch (error) {
      console.error('Error fetching images from Unsplash:', error);
      return null;
    }
  }

  /**
   * Get curated product images for different categories
   */
  async getProductImages(category: string, count: number = 6): Promise<UnsplashImage[]> {
    const searchQueries = this.getCategorySearchQueries(category);

    for (const query of searchQueries) {
      const result = await this.searchImages(query, 1, count);
      if (result && result.results.length > 0) {
        return result.results;
      }
    }

    // Fallback to generic product search
    const fallbackResult = await this.searchImages('product', 1, count);
    return fallbackResult?.results || [];
  }

  /**
   * Get random images for a category
   */
  async getRandomImages(query: string, count: number = 6): Promise<UnsplashImage[]> {
    if (!this.isConfigured()) {
      return [];
    }

    try {
      const response = await fetch(
        `${this.baseUrl}/photos/random?query=${encodeURIComponent(query)}&count=${count}`,
        {
          headers: {
            'Authorization': `Client-ID ${this.accessKey}`,
            'Accept-Version': 'v1'
          }
        }
      );

      if (!response.ok) {
        throw new Error(`Unsplash API error: ${response.status}`);
      }

      const data = await response.json() as UnsplashImage[];
      return Array.isArray(data) ? data : [data];
    } catch (error) {
      console.error('Error fetching random images from Unsplash:', error);
      return [];
    }
  }

  /**
   * Generate optimized image URL with specific dimensions
   */
  getOptimizedImageUrl(image: UnsplashImage, width: number = 400, height: number = 300): string {
    return `${image.urls.raw}&w=${width}&h=${height}&fit=crop&crop=center&q=80`;
  }

  /**
   * Get category-specific search queries
   */
  private getCategorySearchQueries(category: string): string[] {
    const categoryQueries: Record<string, string[]> = {
      'electronics': ['electronics', 'gadgets', 'technology', 'devices'],
      'clothing': ['clothing', 'fashion', 'apparel', 'wear'],
      'food': ['food', 'meal', 'dish', 'cuisine'],
      'books': ['books', 'reading', 'literature', 'study'],
      'home': ['home decor', 'furniture', 'interior', 'house'],
      'sports': ['sports', 'fitness', 'exercise', 'athletic'],
      'beauty': ['beauty', 'cosmetics', 'skincare', 'makeup'],
      'automotive': ['cars', 'automotive', 'vehicle', 'transport'],
      'toys': ['toys', 'games', 'play', 'children'],
      'jewelry': ['jewelry', 'accessories', 'rings', 'necklace'],
      'health': ['health', 'medical', 'wellness', 'healthcare'],
      'office': ['office', 'workspace', 'business', 'desk'],
      'garden': ['garden', 'plants', 'outdoor', 'nature'],
      'music': ['music', 'instruments', 'audio', 'sound'],
      'art': ['art', 'creative', 'design', 'artistic']
    };

    const normalizedCategory = category.toLowerCase();
    return categoryQueries[normalizedCategory] || [category, 'product'];
  }

  /**
   * Get restaurant/food images
   */
  async getRestaurantImages(cuisineType: string = 'food', count: number = 6): Promise<UnsplashImage[]> {
    const foodQueries = [
      `${cuisineType} dish`,
      `${cuisineType} cuisine`,
      `restaurant ${cuisineType}`,
      'delicious food',
      'restaurant meal'
    ];

    for (const query of foodQueries) {
      const result = await this.searchImages(query, 1, count);
      if (result && result.results.length > 0) {
        return result.results;
      }
    }

    return [];
  }

  /**
   * Download attribution for proper crediting
   */
  async trackDownload(image: UnsplashImage): Promise<void> {
    if (!this.isConfigured()) return;

    try {
      await fetch(image.links.download, {
        headers: {
          'Authorization': `Client-ID ${this.accessKey}`
        }
      });
    } catch (error) {
      console.error('Error tracking download:', error);
    }
  }
}

export const unsplashService = new UnsplashService();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "public",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

server/websocketService.ts.content:
import { WebSocket, WebSocketServer } from 'ws';
import { Server } from 'http';
import { db } from './db';
import { webSocketSessions } from '@shared/schema';
import { eq } from 'drizzle-orm';

interface WebSocketClient extends WebSocket {
  userId?: number;
  userType?: string;
  sessionId?: string;
  isAlive?: boolean;
}

export interface LocationBroadcast {
  type: 'location_update';
  deliveryId: number;
  latitude: number;
  longitude: number;
  timestamp: string;
  speed?: number;
  heading?: number;
}

export interface StatusBroadcast {
  type: 'status_update';
  deliveryId: number;
  status: string;
  description?: string;
  location?: { lat: number; lng: number };
  timestamp: string;
}

export class WebSocketService {
  private wss!: WebSocketServer;
  private clients: Map<string, WebSocketClient> = new Map();

  initialize(server: Server) {
    this.wss = new WebSocketServer({ 
      server, 
      path: '/ws',
      verifyClient: (info) => {
        // Add any authentication logic here if needed
        return true;
      }
    });

    this.wss.on('connection', (ws: WebSocketClient, request) => {
      this.handleConnection(ws, request);
    });

    // Ping clients every 30 seconds to keep connections alive
    setInterval(() => {
      this.pingClients();
    }, 30000);

    console.log('WebSocket server initialized on /ws');
  }

  private async handleConnection(ws: WebSocketClient, request: any) {
    ws.isAlive = true;

    ws.on('pong', () => {
      ws.isAlive = true;
    });

    ws.on('message', async (data) => {
      try {
        const message = JSON.parse(data.toString());
        await this.handleMessage(ws, message);
      } catch (error) {
        console.error('Error parsing WebSocket message:', error);
        ws.send(JSON.stringify({ type: 'error', message: 'Invalid message format' }));
      }
    });

    ws.on('close', () => {
      this.handleDisconnection(ws);
    });

    ws.on('error', (error) => {
      console.error('WebSocket error:', error);
      this.handleDisconnection(ws);
    });
  }

  private async handleMessage(ws: WebSocketClient, message: any) {
    switch (message.type) {
      case 'auth':
        await this.handleAuthentication(ws, message);
        break;
      case 'subscribe_tracking':
        await this.handleTrackingSubscription(ws, message);
        break;
      case 'location_update':
        await this.handleLocationUpdate(ws, message);
        break;
      default:
        ws.send(JSON.stringify({ type: 'error', message: 'Unknown message type' }));
    }
  }

  private async handleAuthentication(ws: WebSocketClient, message: any) {
    try {
      const { userId, userType, sessionId } = message;

      if (!userId || !userType) {
        ws.send(JSON.stringify({ type: 'error', message: 'Missing authentication data' }));
        return;
      }

      ws.userId = userId;
      ws.userType = userType;
      ws.sessionId = sessionId || `${userId}_${Date.now()}`;

      // Store session in database
      await db.insert(webSocketSessions).values({
        userId,
        sessionId: ws.sessionId,
        userType,
        isActive: true
      });

      // Add to active clients
      this.clients.set(ws.sessionId, ws);

      ws.send(JSON.stringify({ 
        type: 'auth_success', 
        sessionId: ws.sessionId,
        message: 'Authentication successful' 
      }));

      console.log(`WebSocket authenticated: User ${userId} (${userType})`);
    } catch (error) {
      console.error('Authentication error:', error);
      ws.send(JSON.stringify({ type: 'error', message: 'Authentication failed' }));
    }
  }

  private async handleTrackingSubscription(ws: WebSocketClient, message: any) {
    const { deliveryId } = message;

    if (!deliveryId) {
      ws.send(JSON.stringify({ type: 'error', message: 'Missing delivery ID' }));
      return;
    }

    // Store subscription info (you could enhance this with a proper subscription management)
    ws.send(JSON.stringify({ 
      type: 'subscription_success', 
      deliveryId,
      message: 'Subscribed to delivery tracking' 
    }));
  }

  private async handleLocationUpdate(ws: WebSocketClient, message: any) {
    // Only delivery partners can send location updates
    if (ws.userType !== 'delivery_partner') {
      ws.send(JSON.stringify({ type: 'error', message: 'Unauthorized location update' }));
      return;
    }

    const { deliveryId, latitude, longitude, speed, heading } = message;

    if (!deliveryId || !latitude || !longitude) {
      ws.send(JSON.stringify({ type: 'error', message: 'Missing location data' }));
      return;
    }

    // Broadcast location update to relevant clients
    await this.broadcastLocationUpdate({
      type: 'location_update',
      deliveryId,
      latitude,
      longitude,
      speed,
      heading,
      timestamp: new Date().toISOString()
    });
  }

  private handleDisconnection(ws: WebSocketClient) {
    if (ws.sessionId) {
      this.clients.delete(ws.sessionId);

      // Update session status in database
      db.update(webSocketSessions)
        .set({ isActive: false })
        .where(eq(webSocketSessions.sessionId, ws.sessionId))
        .catch(console.error);

      console.log(`WebSocket disconnected: Session ${ws.sessionId}`);
    }
  }

  private pingClients() {
    this.clients.forEach((ws, sessionId) => {
      if (!ws.isAlive) {
        this.clients.delete(sessionId);
        return ws.terminate();
      }

      ws.isAlive = false;
      ws.ping();
    });
  }

  /**
   * Broadcast location update to customers and shopkeepers
   */
  async broadcastLocationUpdate(data: LocationBroadcast) {
    const message = JSON.stringify(data);

    this.clients.forEach((ws) => {
      if (ws.readyState === WebSocket.OPEN) {
        // Send to customers and shopkeepers (not delivery partners)
        if (ws.userType === 'customer' || ws.userType === 'shopkeeper') {
          ws.send(message);
        }
      }
    });
  }

  /**
   * Broadcast status update to all relevant clients
   */
  async broadcastStatusUpdate(data: StatusBroadcast) {
    const message = JSON.stringify(data);

    this.clients.forEach((ws) => {
      if (ws.readyState === WebSocket.OPEN) {
        ws.send(message);
      }
    });
  }

  /**
   * Send notification to specific user
   */
  async sendToUser(userId: number, data: any) {
    const message = JSON.stringify(data);

    this.clients.forEach((ws) => {
      if (ws.readyState === WebSocket.OPEN && ws.userId === userId) {
        ws.send(message);
      }
    });
  }

  /**
   * Send notification to specific user type
   */
  async sendToUserType(userType: string, data: any) {
    const message = JSON.stringify(data);

    this.clients.forEach((ws) => {
      if (ws.readyState === WebSocket.OPEN && ws.userType === userType) {
        ws.send(message);
      }
    });
  }

  /**
   * Get connected clients count
   */
  getConnectedClientsCount(): number {
    return this.clients.size;
  }

  /**
   * Get clients by user type
   */
  getClientsByUserType(userType: string): number {
    let count = 0;
    this.clients.forEach((ws) => {
      if (ws.userType === userType) {
        count++;
      }
    });
    return count;
  }
}

export const webSocketService = new WebSocketService();

