const CACHE_NAME = 'siraha-bazaar-v1.0.1';
const urlsToCache = [
  '/',
  '/manifest.json',
  '/icon-192x192.png',
  '/icon-512x512.png',
  '/assets/icon1.png',
  '/assets/icon2.png',
  '/screenshot-wide.png',
  '/screenshot-narrow.png',
  '/widget-screenshot.svg',
  '/adaptive-card.json'
];

// Install event - cache resources
self.addEventListener('install', (event) => {
  console.log('Service Worker installing...');
  event.waitUntil(
    caches.open(CACHE_NAME)
      .then((cache) => {
        console.log('Caching app shell...');
        return cache.addAll(urlsToCache);
      })
      .then(() => {
        console.log('Service Worker installed and cache populated');
        return self.skipWaiting();
      })
  );
});

// Activate event - clean up old caches
self.addEventListener('activate', (event) => {
  console.log('Service Worker activating...');
  event.waitUntil(
    caches.keys().then((cacheNames) => {
      return Promise.all(
        cacheNames.map((cacheName) => {
          if (cacheName !== CACHE_NAME) {
            console.log('Deleting old cache:', cacheName);
            return caches.delete(cacheName);
          }
        })
      );
    }).then(() => {
      console.log('Service Worker activated');
      return self.clients.claim();
    })
  );
});

// Fetch event - serve from cache, fallback to network
self.addEventListener('fetch', (event) => {
  // Don't cache API requests or non-GET requests
  if (event.request.url.includes('/api/') || event.request.method !== 'GET') {
    event.respondWith(fetch(event.request));
    return;
  }

  event.respondWith(
    caches.match(event.request)
      .then((response) => {
        // Return cached version or fetch from network
        if (response) {
          return response;
        }

        // Clone the request because it's a stream
        const fetchRequest = event.request.clone();

        return fetch(fetchRequest).then((response) => {
          // Check if we received a valid response
          if (!response || response.status !== 200 || response.type !== 'basic') {
            return response;
          }

          // Clone the response because it's a stream
          const responseToCache = response.clone();

          // Cache static assets only (CSS, JS, images, fonts)
          if (event.request.method === 'GET' && 
              (event.request.url.includes('/assets/') || 
               event.request.url.includes('/sounds/') ||
               event.request.url.match(/\.(js|css|png|jpg|jpeg|gif|ico|svg|woff|woff2)$/))) {
            caches.open(CACHE_NAME)
              .then((cache) => {
                cache.put(event.request, responseToCache);
              })
              .catch((error) => {
                console.log('Cache put error:', error);
              });
          }

          return response;
        }).catch((error) => {
          console.log('Fetch error:', error);
          return new Response('Network error', { status: 408 });
        });
      })
  );
});

// Push notification event
self.addEventListener('push', (event) => {
  console.log('Push notification received:', event);

  const options = {
    body: 'New notification from Siraha Bazaar',
    icon: '/icon-192x192.png',
    badge: '/icon-72x72.png',
    vibrate: [100, 50, 100],
    data: {
      dateOfArrival: Date.now(),
      primaryKey: 1
    },
    actions: [
      {
        action: 'explore',
        title: 'View Details',
        icon: '/icon-72x72.png'
      },
      {
        action: 'close',
        title: 'Close',
        icon: '/icon-72x72.png'
      }
    ]
  };

  if (event.data) {
    const data = event.data.json();
    options.body = data.body || options.body;
    options.title = data.title || 'Siraha Bazaar';
    options.data = { ...options.data, ...data.data };
  }

  event.waitUntil(
    self.registration.showNotification('Siraha Bazaar', options)
  );
});

// Notification click event
self.addEventListener('notificationclick', (event) => {
  console.log('Notification click received:', event);

  event.notification.close();

  if (event.action === 'explore') {
    // Handle the explore action
    event.waitUntil(
      clients.openWindow('/')
    );
  } else if (event.action === 'close') {
    // Notification closed
    event.notification.close();
  } else {
    // Default action - open the app
    event.waitUntil(
      clients.openWindow('/')
    );
  }
});

// Background sync event
self.addEventListener('sync', (event) => {
  if (event.tag === 'background-sync') {
    console.log('Background sync triggered');
    event.waitUntil(
      // Perform background sync operations
      Promise.resolve()
    );
  }
});

// Message event for communication with main thread
self.addEventListener('message', (event) => {
  if (event.data && event.data.type === 'SKIP_WAITING') {
    self.skipWaiting();
  }
});

// File handling event
self.addEventListener('message', (event) => {
  if (event.data && event.data.type === 'FILE_HANDLER') {
    const files = event.data.files;
    console.log('Files received via File Handler API:', files);

    // Notify main thread about received files
    event.ports[0].postMessage({
      type: 'FILES_RECEIVED',
      files: files.map(file => ({
        name: file.name,
        type: file.type,
        size: file.size
      }))
    });
  }
});

// Share target handling
self.addEventListener('message', (event) => {
  if (event.data && event.data.type === 'SHARE_TARGET') {
    const shareData = event.data.shareData;
    console.log('Share data received:', shareData);

    // Process share data and redirect to appropriate page
    clients.openWindow(`/share-target?title=${encodeURIComponent(shareData.title || '')}&text=${encodeURIComponent(shareData.text || '')}&url=${encodeURIComponent(shareData.url || '')}`);
  }
});

// Widget update event
self.addEventListener('message', (event) => {
  if (event.data && event.data.type === 'UPDATE_WIDGET') {
    const widgetData = event.data.widgetData;
    console.log('Widget update received:', widgetData);

    // Update widget data cache
    caches.open('widget-cache').then(cache => {
      cache.put('/api/widget-data/orders', new Response(JSON.stringify(widgetData)));
    });
  }
});

// Protocol handling
self.addEventListener('message', (event) => {
  if (event.data && event.data.type === 'PROTOCOL_HANDLER') {
    const protocol = event.data.protocol;
    const url = event.data.url;
    console.log('Protocol handled:', protocol, url);

    // Route to appropriate handler page
    if (protocol === 'web+siraha') {
      clients.openWindow(`/protocol-handler?url=${encodeURIComponent(url)}`);
    } else if (protocol === 'mailto') {
      clients.openWindow(`/contact?email=${encodeURIComponent(url.replace('mailto:', ''))}`);
    }
  }
});

