function App() {
  // Initialize Android bridge on app startup

  // Initialize Android Bridge and Deep Linking
  useEffect(() => {
    // Initialize Android Bridge
    AndroidBridge.initialize();

    // Initialize Deep Linking Service
    deepLinkingService.initialize();

    // Check for OAuth redirect result (important for Android WebView)
    const checkOAuthRedirect = async () => {
      try {
        const { OAuthRedirectHandler } = await import('@/lib/oauthRedirectHandler');
        await OAuthRedirectHandler.checkForRedirectResult();
      } catch (error) {
        console.log('No OAuth redirect to process:', error);
      }
    };

    // Check for redirect result after a short delay to ensure Firebase is initialized
    setTimeout(checkOAuthRedirect, 1000);

    console.log('📱 App services initialized with OAuth redirect support');
  }, []);

  // Initialize FCM when app loads (like in YouTube tutorials)
  useEffect(() => {
    const initializeFCMOnLoad = async () => {
      console.log('🎬 Starting FCM initialization (like YouTube tutorial)...');

      try {
        // First initialize Firebase
        await FirebaseNotificationService.initialize();

        // Check if notification permission is already granted
        if (Notification.permission === 'granted') {
          console.log('✅ Notification permission already granted - FCM ready!');
        } else if (Notification.permission === 'default') {
          console.log('🔔 Requesting notification permission for FCM token...');
          const granted = await FirebaseNotificationService.requestPermission();
          if (granted) {
            console.log('🎉 Notification permission granted! Getting FCM token...');
            // Try to get the token now that permission is granted
            try {
              const token = await FirebaseNotificationService.getDeviceToken();
              if (token) {
                console.log('🎯 SUCCESS: FCM Token generated after permission grant!');
              }
            } catch (tokenError) {
              console.error('❌ Failed to get FCM token after permission grant:', tokenError);
            }
          } else {
            console.log('❌ Notification permission denied - FCM token cannot be generated');
          }
        } else {
          console.log('🚫 Notification permission permanently denied');
        }
      } catch (error) {
        console.error('❌ FCM initialization failed:', error);
      }
    };

    // Delay initialization slightly to ensure DOM is ready
    setTimeout(initializeFCMOnLoad, 2000);
  }, []);

  return (
    <ErrorBoundary>
      <QueryClientProvider client={queryClient}>
        <AuthProvider>
          <CartProvider>
            <WishlistProvider>
              <AppModeProvider>
                <SoundProvider>
                <TooltipProvider>
                  <ErrorBoundary>
                    <div className="min-h-screen flex flex-col">
                      <ErrorBoundary>
                        <NotificationBanner />
                      </ErrorBoundary>
                      <ErrorBoundary>
                        <MobileNotificationBar className="md:hidden" />
                      </ErrorBoundary>
                      <ErrorBoundary>
                        <NavbarWrapper />
                      </ErrorBoundary>
                      <main className="flex-1 pb-16 md:pb-0">
                        <ErrorBoundary>
                          <AppRouter />
                        </ErrorBoundary>
                      </main>
                      <ErrorBoundary>
                        <Footer />
                      </ErrorBoundary>
                      <ErrorBoundary>
                        <BottomNavbar />
                      </ErrorBoundary>
                    </div>
                    <Toaster />
                    <PWAInstallPrompt />
                  </ErrorBoundary>
                </TooltipProvider>
                </SoundProvider>
              </AppModeProvider>
            </WishlistProvider>
          </CartProvider>
        </AuthProvider>
      </QueryClientProvider>
    </ErrorBoundary>
  );
}

export default App;

