import { Home, Package, Store, User, MapPin, ShoppingCart, Tag, UtensilsCrossed, ChefHat } from "lucide-react";
import { Link, useLocation } from "wouter";
import { useAuth } from "@/hooks/useAuth";
import { useAppMode } from "@/hooks/useAppMode";
import { cn } from "@/lib/utils";
import { useUser } from "@/hooks/use-user";

export default function BottomNavbar() {
  const [location] = useLocation();
  const { user } = useAuth();
  const { mode } = useAppMode();
  const { user: currentUser } = useUser();

  // Hide bottom navbar for delivery partners
  if (currentUser?.role === "delivery_partner") {
    return null;
  }

  // Role-based navigation items
  const sellerNavItems = [
    {
      href: "/seller/dashboard",
      icon: Home,
      label: "Dashboard",
      active: location.startsWith("/seller/dashboard")
    },
    {
      href: "/seller/store",
      icon: Store,
      label: "Store",
      active: location.startsWith("/seller/store")
    },
    {
      href: "/seller/orders",
      icon: ShoppingCart,
      label: "Orders",
      active: location.startsWith("/seller/orders")
    },
    {
      href: "/seller/inventory",
      icon: Package,
      label: "Inventory",
      active: location.startsWith("/seller/inventory")
    },
    {
      href: "/account",
      icon: User,
      label: "Account",
      active: location.startsWith("/account")
    }
  ];

  const customerNavItems = [
    {
      href: "/",
      icon: Home,
      label: "Home",
      active: location === "/"
    },
    ...(mode === 'shopping' ? [
      {
        href: "/products",
        icon: Package,
        label: "Products",
        active: location.startsWith("/products")
      },
      {
        href: "/stores",
        icon: Store,
        label: "Stores",
        active: location.startsWith("/stores")
      },
      {
        href: "/store-maps",
        icon: MapPin,
        label: "Map",
        active: location === "/store-maps"
      }
    ] : [
      {
        href: "/food-categories",
        icon: UtensilsCrossed,
        label: "Menu",
        active: location.startsWith("/food-categories") || location.startsWith("/categories")
      },
      {
        href: "/restaurants",
        icon: ChefHat,
        label: "Restaurants",
        active: location.startsWith("/restaurants")
      },
      {
        href: "/restaurant-maps",
        icon: MapPin,
        label: "Map",
        active: location === "/restaurant-maps"
      }
    ]),
    {
      href: user ? "/account" : "/login",
      icon: User,
      label: "Account",
      active: location.startsWith("/account") || location.startsWith("/customer-dashboard")
    }
  ];

  const navItems = user?.role === "shopkeeper" ? sellerNavItems : customerNavItems;

  return (
    <nav className="fixed bottom-0 left-0 right-0 bg-white dark:bg-gray-900 border-t border-gray-200 dark:border-gray-800 z-50 md:hidden safe-area-inset-bottom">
      <div className="flex items-center justify-around h-16 px-4 pb-safe">
        {navItems.map((item) => {
          const Icon = item.icon;
          return (
            <Link
              key={item.href}
              href={item.href}
              className={cn(
                "flex flex-col items-center justify-center space-y-1 py-2 px-1 sm:px-3 rounded-lg transition-colors min-w-0 flex-1",
                item.active
                  ? "text-primary bg-primary/10"
                  : "text-gray-600 dark:text-gray-400 hover:text-primary hover:bg-primary/5"
              )}
            >
              <Icon className="h-4 w-4 sm:h-5 sm:w-5" />
              <span className="text-xs font-medium truncate">{item.label}</span>
            </Link>
          );
        })}
      </div>
    </nav>
  );
}

client/src/components/DashboardRouter.ts.content:
import { useEffect } from "react";
import { useLocation } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import type { Store } from "@shared/schema";

export default function DashboardRouter() {
  const { user } = useAuth();
  const [, setLocation] = useLocation();

  // Fetch user's store to determine dashboard type
  const { data: stores = [] } = useQuery<Store[]>({
    queryKey: [`/api/stores/user/${user?.id}`],
    enabled: !!user?.id && user?.role === 'shopkeeper',
  });

  useEffect(() => {
    if (!user || user.role !== 'shopkeeper') return;

    // If user has stores, check the type
    if (stores.length > 0) {
      const userStore = stores[0]; // Get first store

      // Check if store is restaurant by storeType or name detection
      const isRestaurant = userStore.storeType === 'restaurant' || 
                          isRestaurantByName(userStore.name);

      if (isRestaurant) {
        // Redirect to restaurant dashboard
        setLocation('/restaurant/dashboard');
      } else {
        // Stay on seller dashboard for retail stores
        setLocation('/seller/dashboard');
      }
    }
  }, [user, stores, setLocation]);

  return null; // This component doesn't render anything
}

// Helper function to detect restaurant by name
function isRestaurantByName(storeName: string): boolean {
  const restaurantKeywords = [
    'restaurant', 'cafe', 'food', 'kitchen', 'dining', 'eatery', 'bistro',
    'pizzeria', 'burger', 'pizza', 'chicken', 'biryani', 'dosa', 'samosa',
    'chinese', 'indian', 'nepali', 'thai', 'continental', 'fast food',
    'dhaba', 'hotel', 'canteen', 'mess', 'tiffin', 'sweet', 'bakery',
    'tyres' // Your specific restaurant name
  ];

  const lowerName = storeName.toLowerCase();
  return restaurantKeywords.some(keyword => lowerName.includes(keyword));
}

client/src/components/DeliveryAssignment.content:
import React, { useEffect, useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { MapPin, Clock, DollarSign, Package, Navigation, Phone, X, Check } from 'lucide-react';
import { usePushNotifications } from '@/hooks/usePushNotifications';

interface DeliveryAssignment {
  id: number;
  orderId: number;
  customerName: string;
  customerPhone: string;
  pickupAddress: string;
  deliveryAddress: string;
  deliveryFee: string;
  estimatedDistance: number;
  estimatedTime: number;
  specialInstructions?: string;
  pickupLocation: { lat: number; lng: number };
  deliveryLocation: { lat: number; lng: number };
}

interface DeliveryAssignmentNotificationProps {
  assignment: DeliveryAssignment;
  onAccept: (assignmentId: number) => void;
  onDecline: (assignmentId: number) => void;
  onViewRoute: (assignment: DeliveryAssignment) => void;
}

export function DeliveryAssignmentNotification({ 
  assignment, 
  onAccept, 
  onDecline, 
  onViewRoute 
}: DeliveryAssignmentNotificationProps) {
  const [timeLeft, setTimeLeft] = useState(60); // 60 seconds to respond
  const { showNotification } = usePushNotifications();

  useEffect(() => {
    // Show push notification when assignment arrives
    showNotification('🚚 New Delivery Assignment', {
      body: `New order #${assignment.orderId} for ${assignment.customerName}`,
      icon: '/icons/delivery-icon.png',
      tag: `delivery-${assignment.id}`,
      actions: [
        { action: 'accept', title: 'Accept' },
        { action: 'view', title: 'View Details' }
      ]
    });

    const timer = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          // Auto-decline when time runs out
          onDecline(assignment.id);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [assignment]);

  const formatDistance = (meters: number): string => {
    if (meters < 1000) {
      return `${meters}m`;
    }
    return `${(meters / 1000).toFixed(1)}km`;
  };

  const formatTime = (minutes: number): string => {
    if (minutes < 60) {
      return `${minutes} min`;
    }
    const hours = Math.floor(minutes / 60);
    const remainingMinutes = minutes % 60;
    return `${hours}h ${remainingMinutes}m`;
  };

  const openGoogleMapsRoute = () => {
    const { pickupLocation, deliveryLocation } = assignment;
    const url = `https://www.google.com/maps/dir/${pickupLocation.lat},${pickupLocation.lng}/${deliveryLocation.lat},${deliveryLocation.lng}`;
    window.open(url, '_blank');
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <Card className="w-full max-w-md bg-white shadow-xl border-2 border-blue-500 animate-pulse">
        <CardHeader className="bg-blue-50">
          <CardTitle className="flex items-center justify-between text-blue-900">
            <span className="flex items-center gap-2">
              <Package className="h-5 w-5" />
              New Delivery Assignment
            </span>
            <Badge variant="destructive" className="animate-bounce">
              {timeLeft}s
            </Badge>
          </CardTitle>
        </CardHeader>
        <CardContent className="p-6 space-y-4">
          {/* Order Info */}
          <div className="bg-gray-50 p-3 rounded-lg">
            <h3 className="font-semibold text-lg">Order #{assignment.orderId}</h3>
            <p className="text-gray-600">Customer: {assignment.customerName}</p>
            <div className="flex items-center gap-2 mt-1">
              <Phone className="h-4 w-4 text-gray-500" />
              <a href={`tel:${assignment.customerPhone}`} className="text-blue-600 hover:underline">
                {assignment.customerPhone}
              </a>
            </div>
          </div>

          {/* Route Information */}
          <div className="space-y-3">
            <div className="flex items-start gap-3">
              <div className="w-3 h-3 bg-green-500 rounded-full mt-1.5"></div>
              <div className="flex-1">
                <p className="font-medium text-green-700">Pickup from:</p>
                <p className="text-sm text-gray-600">{assignment.pickupAddress}</p>
              </div>
            </div>

            <div className="flex items-start gap-3">
              <div className="w-3 h-3 bg-red-500 rounded-full mt-1.5"></div>
              <div className="flex-1">
                <p className="font-medium text-red-700">Deliver to:</p>
                <p className="text-sm text-gray-600">{assignment.deliveryAddress}</p>
              </div>
            </div>
          </div>

          {/* Delivery Stats */}
          <div className="grid grid-cols-3 gap-4 text-center">
            <div>
              <MapPin className="h-4 w-4 mx-auto text-gray-500 mb-1" />
              <p className="text-xs text-gray-500">Distance</p>
              <p className="font-semibold">{formatDistance(assignment.estimatedDistance)}</p>
            </div>
            <div>
              <Clock className="h-4 w-4 mx-auto text-gray-500 mb-1" />
              <p className="text-xs text-gray-500">Time</p>
              <p className="font-semibold">{formatTime(assignment.estimatedTime)}</p>
            </div>
            <div>
              <DollarSign className="h-4 w-4 mx-auto text-gray-500 mb-1" />
              <p className="text-xs text-gray-500">Fee</p>
              <p className="font-semibold">₹{assignment.deliveryFee}</p>
            </div>
          </div>

          {/* Special Instructions */}
          {assignment.specialInstructions && (
            <div className="bg-yellow-50 p-3 rounded-lg">
              <p className="text-sm font-medium text-yellow-800">Special Instructions:</p>
              <p className="text-sm text-yellow-700">{assignment.specialInstructions}</p>
            </div>
          )}

          {/* Action Buttons */}
          <div className="space-y-2">
            <Button 
              onClick={openGoogleMapsRoute}
              variant="outline" 
              className="w-full flex items-center gap-2"
            >
              <Navigation className="h-4 w-4" />
              View Route in Google Maps
            </Button>

            <div className="grid grid-cols-2 gap-2">
              <Button 
                onClick={() => onDecline(assignment.id)}
                variant="destructive"
                className="flex items-center gap-2"
              >
                <X className="h-4 w-4" />
                Decline
              </Button>
              <Button 
                onClick={() => onAccept(assignment.id)}
                variant="default"
                className="flex items-center gap-2 bg-green-600 hover:bg-green-700"
              >
                <Check className="h-4 w-4" />
                Accept
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/DeliveryCalculator.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { MapPin, Calculator, Clock } from "lucide-react";
import { calculateDistance, getCoordinatesFromAddress, estimateDeliveryTime } from "@/lib/distance";
import { useQuery } from "@tanstack/react-query";

interface DeliveryCalculatorProps {
  storeAddress: string;
  onFeeCalculated: (fee: number, distance: number, estimatedTime: number) => void;
}

export function DeliveryCalculator({ storeAddress, onFeeCalculated }: DeliveryCalculatorProps) {
  const [customerAddress, setCustomerAddress] = useState("");
  const [isCalculating, setIsCalculating] = useState(false);
  const [deliveryInfo, setDeliveryInfo] = useState<{
    distance: number;
    fee: number;
    estimatedTime: number;
    zone: any;
  } | null>(null);

  const { data: deliveryZones = [] } = useQuery({
    queryKey: ["/api/delivery-zones"],
  });

  const calculateDeliveryFee = async () => {
    if (!customerAddress.trim()) return;

    setIsCalculating(true);
    try {
      // Get coordinates for both addresses
      const storeCoords = await getCoordinatesFromAddress(storeAddress);
      const customerCoords = await getCoordinatesFromAddress(customerAddress);

      if (!storeCoords || !customerCoords) {
        throw new Error("Could not find location coordinates");
      }

      // Calculate distance
      const distance = calculateDistance(storeCoords, customerCoords);

      // Find applicable delivery zone and calculate fee
      const applicableZone = deliveryZones.find((zone: any) => {
        const minDist = parseFloat(zone.minDistance);
        const maxDist = parseFloat(zone.maxDistance);
        return distance >= minDist && distance <= maxDist;
      });

      let fee = 100; // Default fee
      if (applicableZone) {
        const baseFee = parseFloat(applicableZone.baseFee);
        const perKmRate = parseFloat(applicableZone.perKmRate);
        fee = baseFee + (distance * perKmRate);
      }

      const estimatedTime = estimateDeliveryTime(distance);

      const info = {
        distance: Math.round(distance * 100) / 100,
        fee: Math.round(fee * 100) / 100,
        estimatedTime,
        zone: applicableZone
      };

      setDeliveryInfo(info);
      onFeeCalculated(info.fee, info.distance, info.estimatedTime);

    } catch (error) {
      console.error("Error calculating delivery fee:", error);
    } finally {
      setIsCalculating(false);
    }
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <MapPin className="h-5 w-5" />
          Delivery Fee Calculator
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        <div>
          <Label htmlFor="store-address">Store Location</Label>
          <Input 
            id="store-address" 
            value={storeAddress} 
            disabled 
            className="bg-gray-50"
          />
        </div>

        <div>
          <Label htmlFor="customer-address">Your Delivery Address</Label>
          <Input 
            id="customer-address" 
            value={customerAddress}
            onChange={(e) => setCustomerAddress(e.target.value)}
            placeholder="Enter your address (e.g., Siraha, Janakpur, Kathmandu)"
          />
        </div>

        <Button 
          onClick={calculateDeliveryFee}
          disabled={isCalculating || !customerAddress.trim()}
          className="w-full"
        >
          <Calculator className="h-4 w-4 mr-2" />
          {isCalculating ? "Calculating..." : "Calculate Delivery Fee"}
        </Button>

        {deliveryInfo && (
          <div className="mt-4 p-4 bg-blue-50 rounded-lg space-y-2">
            <div className="flex justify-between">
              <span className="font-medium">Distance:</span>
              <span>{deliveryInfo.distance} km</span>
            </div>
            <div className="flex justify-between">
              <span className="font-medium">Delivery Zone:</span>
              <span>{deliveryInfo.zone?.name || "Standard"}</span>
            </div>
            <div className="flex justify-between">
              <span className="font-medium">Delivery Fee:</span>
              <span className="font-bold text-green-600">Rs. {deliveryInfo.fee}</span>
            </div>
            <div className="flex justify-between items-center">
              <span className="font-medium">Estimated Time:</span>
              <span className="flex items-center gap-1">
                <Clock className="h-4 w-4" />
                {deliveryInfo.estimatedTime} minutes
              </span>
            </div>
            {deliveryInfo.zone && (
              <div className="text-sm text-gray-600 mt-2">
                Zone: {deliveryInfo.zone.name} (Rs. {deliveryInfo.zone.baseFee} base + Rs. {deliveryInfo.zone.perKmRate}/km)
              </div>
            )}
          </div>
        )}

        <div className="text-sm text-gray-500 mt-2">
          <strong>Delivery Zones:</strong>
          <ul className="mt-1 space-y-1">
            {deliveryZones.map((zone: any) => (
              <li key={zone.id}>
                • {zone.name}: {zone.minDistance}-{zone.maxDistance}km (Rs. {zone.baseFee} + Rs. {zone.perKmRate}/km)
              </li>
            ))}
          </ul>
        </div>
      </CardContent>
    </Card>
  );
}

