import { useState } from "react";
import { Link, useLocation } from "wouter";
import { 
  Truck, 
  Bell, 
  User, 
  Menu, 
  X, 
  LogOut, 
  Navigation, 
  Package, 
  DollarSign,
  Clock,
  MapPin,
  Settings,
  BarChart3
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useAuth } from "@/hooks/useAuth";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

export default function DeliveryPartnerNavbar() {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [location, setLocation] = useLocation();
  const { user, logout } = useAuth();

  const handleLogout = () => {
    logout();
    setLocation("/");
  };

  const navItems = [
    {
      label: "Dashboard",
      href: "/delivery-partner/dashboard",
      icon: BarChart3,
    },
    {
      label: "Notifications",
      href: "/delivery-partner/notifications", 
      icon: Bell,
      badge: 3,
    },
    {
      label: "Active Deliveries",
      href: "/delivery-partner/test",
      icon: Package,
      badge: 2,
    },
    {
      label: "Live Tracking",
      href: "/delivery-partner/tracking",
      icon: Navigation,
    },
  ];

  return (
    <nav className="bg-blue-600 text-white shadow-lg sticky top-0 z-40">
      <div className="max-w-7xl mx-auto px-3 sm:px-4 lg:px-8">
        <div className="flex justify-between items-center h-14 sm:h-16">
