import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { MapPin, Loader2 } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface LocationPickerProps {
  address: string;
  latitude?: string;
  longitude?: string;
  onLocationChange: (data: {
    address: string;
    latitude: string;
    longitude: string;
    googleMapsLink: string;
  }) => void;
}

export function LocationPicker({
  address,
  latitude,
  longitude,
  onLocationChange,
}: LocationPickerProps) {
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();

  const getCurrentLocation = async () => {
    if (!navigator.geolocation) {
      toast({
        title: "Location not supported",
        description: "Your browser doesn't support location services",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);

    try {
      const position = await new Promise<GeolocationPosition>((resolve, reject) => {
        navigator.geolocation.getCurrentPosition(resolve, reject, {
          enableHighAccuracy: true,
          timeout: 10000,
          maximumAge: 0,
        });
      });

      const { latitude: lat, longitude: lng } = position.coords;

      // Get address from coordinates using reverse geocoding
      const address = await reverseGeocode(lat, lng);
      const googleMapsLink = `https://maps.google.com/?q=${lat},${lng}`;

      onLocationChange({
        address,
        latitude: lat.toString(),
        longitude: lng.toString(),
        googleMapsLink,
      });

      toast({
        title: "Location found",
        description: "Your location has been automatically filled",
      });
    } catch (error) {
      console.error('Error getting location:', error);
      toast({
        title: "Location error",
        description: "Could not get your location. Please enter manually.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const reverseGeocode = async (lat: number, lng: number): Promise<string> => {
    try {
      // Using OpenStreetMap Nominatim for reverse geocoding (free service)
      const response = await fetch(
        `https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lng}&addressdetails=1`
      );

      if (!response.ok) {
        throw new Error('Geocoding failed');
      }

      const data = await response.json();

      if (data.display_name) {
        return data.display_name;
      } else {
        throw new Error('No address found');
      }
    } catch (error) {
      console.error('Reverse geocoding error:', error);
      return `${lat}, ${lng}`; // Fallback to coordinates
    }
  };

  const handleAddressChange = (newAddress: string) => {
    onLocationChange({
      address: newAddress,
      latitude: latitude || "",
      longitude: longitude || "",
      googleMapsLink: latitude && longitude ? `https://maps.google.com/?q=${latitude},${longitude}` : "",
    });
  };

  const generateGoogleMapsLink = () => {
    if (latitude && longitude) {
      const link = `https://maps.google.com/?q=${latitude},${longitude}`;
      window.open(link, '_blank');
    } else {
      toast({
        title: "No coordinates",
        description: "Please get your location first to generate Google Maps link",
        variant: "destructive",
      });
    }
  };

  return (
    <div className="space-y-4">
      <div>
        <Label htmlFor="address">Store Address</Label>
        <div className="flex gap-2 mt-1">
          <Textarea
            id="address"
            placeholder="Complete store address"
            value={address}
            onChange={(e) => handleAddressChange(e.target.value)}
            className="flex-1"
          />
          <Button
            type="button"
            variant="outline"
            size="sm"
            onClick={getCurrentLocation}
            disabled={isLoading}
            className="whitespace-nowrap"
          >
            {isLoading ? (
              <Loader2 className="h-4 w-4 animate-spin" />
            ) : (
              <MapPin className="h-4 w-4" />
            )}
            {isLoading ? "Getting..." : "Get My Location"}
          </Button>
        </div>
      </div>

      {latitude && longitude && (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <Label htmlFor="latitude">Latitude</Label>
            <Input
              id="latitude"
              value={latitude}
              readOnly
              className="bg-muted"
            />
          </div>
          <div>
            <Label htmlFor="longitude">Longitude</Label>
            <Input
              id="longitude"
              value={longitude}
              readOnly
              className="bg-muted"
            />
          </div>
          <div className="flex items-end">
            <Button
              type="button"
              variant="outline"
              onClick={generateGoogleMapsLink}
              className="w-full"
            >
              View on Google Maps
            </Button>
          </div>
        </div>
      )}
    </div>
  );
}

client/src/components/LocationTracker.ts.content:
import React, { useEffect, useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { MapPin, Play, Pause, Navigation } from 'lucide-react';

interface LocationTrackerProps {
  deliveryId: number;
  deliveryPartnerId: number;
  isActive: boolean;
  onToggleTracking: (active: boolean) => void;
}

export function LocationTracker({ deliveryId, deliveryPartnerId, isActive, onToggleTracking }: LocationTrackerProps) {
  const [watchId, setWatchId] = useState<number | null>(null);
  const [lastUpdate, setLastUpdate] = useState<Date | null>(null);
  const [accuracy, setAccuracy] = useState<number | null>(null);
  const [error, setError] = useState<string | null>(null);

  const startTracking = () => {
    if (!navigator.geolocation) {
      setError('Geolocation is not supported by this browser');
      return;
    }

    const options = {
      enableHighAccuracy: true,
      timeout: 10000,
      maximumAge: 5000 // Cache position for 5 seconds
    };

    const id = navigator.geolocation.watchPosition(
      (position) => {
        const { latitude, longitude, heading, speed, accuracy } = position.coords;

        // Send location update to server
        fetch('/api/tracking/location', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          credentials: 'include',
          body: JSON.stringify({
            deliveryId,
            deliveryPartnerId,
            latitude,
            longitude,
            heading: heading || undefined,
            speed: speed || undefined,
            accuracy: accuracy || undefined
          })
        }).then(() => {
          setLastUpdate(new Date());
          setAccuracy(accuracy);
          setError(null);
        }).catch((err) => {
          console.error('Failed to update location:', err);
          setError('Failed to update location');
        });
      },
      (err) => {
        console.error('Geolocation error:', err);
        setError(`Location error: ${err.message}`);
      },
      options
    );

    setWatchId(id);
    onToggleTracking(true);
  };

  const stopTracking = () => {
    if (watchId !== null) {
      navigator.geolocation.clearWatch(watchId);
      setWatchId(null);
    }
    onToggleTracking(false);
  };

  const toggleTracking = () => {
    if (isActive) {
      stopTracking();
    } else {
      startTracking();
    }
  };

  // Auto-start tracking when component mounts if it should be active
  useEffect(() => {
    if (isActive && watchId === null) {
      startTracking();
    }

    return () => {
      if (watchId !== null) {
        navigator.geolocation.clearWatch(watchId);
      }
    };
  }, []);

  return (
    <Card className="mb-4">
      <CardHeader>
        <CardTitle className="flex items-center justify-between">
          <span className="flex items-center gap-2">
            <MapPin className="h-5 w-5" />
            Location Tracking
          </span>
          <Badge variant={isActive ? "default" : "secondary"}>
            {isActive ? "Active" : "Inactive"}
          </Badge>
        </CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <div className="text-sm text-gray-600">
              {lastUpdate ? (
                <span>Last update: {lastUpdate.toLocaleTimeString()}</span>
              ) : (
                <span>No location updates yet</span>
              )}
              {accuracy && (
                <span className="block">Accuracy: ±{accuracy.toFixed(0)}m</span>
              )}
            </div>
            <Button 
              onClick={toggleTracking}
              variant={isActive ? "destructive" : "default"}
              size="sm"
              className="flex items-center gap-2"
            >
              {isActive ? (
                <>
                  <Pause className="h-4 w-4" />
                  Stop Tracking
                </>
              ) : (
                <>
                  <Play className="h-4 w-4" />
                  Start Tracking
                </>
              )}
            </Button>
          </div>

          {error && (
            <div className="text-sm text-red-600 bg-red-50 p-2 rounded">
              {error}
            </div>
          )}

          {isActive && (
            <div className="text-xs text-gray-500 bg-blue-50 p-2 rounded">
              <Navigation className="h-3 w-3 inline mr-1" />
              Your location is being shared with customers and the store for real-time tracking.
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

