import React, { useState, useEffect } from 'react';
import { X, Bell } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { useAuth } from '@/hooks/useAuth';
import { useQuery } from '@tanstack/react-query';

interface Notification {
  id: number;
  userId: number;
  title: string;
  message: string;
  type: string;
  isRead: boolean;
  createdAt: string;
}

export default function NotificationBanner() {
  const { user } = useAuth();
  const [visibleNotifications, setVisibleNotifications] = useState<Notification[]>([]);
  const [lastNotificationId, setLastNotificationId] = useState<number | null>(null);

  // Fetch notifications
  const { data: notifications = [] } = useQuery({
    queryKey: [`/api/notifications/user/${user?.id}`],
    enabled: !!user?.id,
    refetchInterval: 5000, // Check for new notifications every 5 seconds
  }) as { data: Notification[] };

  // Check for new notifications
  useEffect(() => {
    if (!notifications.length) return;

    const latestNotification = notifications[0];

    // If there's a new notification (different from the last one we showed)
    if (latestNotification && latestNotification.id !== lastNotificationId && !latestNotification.isRead) {
      setVisibleNotifications(prev => {
        // Check if this notification is already visible
        const exists = prev.some(n => n.id === latestNotification.id);
        if (!exists) {
          return [latestNotification, ...prev].slice(0, 3); // Show max 3 notifications
        }
        return prev;
      });
      setLastNotificationId(latestNotification.id);

      // Play notification sound when banner appears
      try {
        const audio = new Audio('/notification.mp3');
        // Check if this is an approval notification for louder sound
        const isApprovalNotification = latestNotification.title.includes('Approved') || 
                                     latestNotification.message.includes('approved');

        audio.volume = isApprovalNotification ? 0.8 : 0.6;

        audio.play().then(() => {
          console.log('Notification banner sound played successfully');

          // For approval notifications, play a second celebratory beep
          if (isApprovalNotification) {
            setTimeout(() => {
              const celebraryAudio = new Audio('/notification.mp3');
              celebraryAudio.volume = 0.5;
              celebraryAudio.play().catch(() => {
                console.log('Could not play celebratory sound');
              });
            }, 500);
          }
        }).catch(() => {
          console.log('Could not play notification banner sound');
        });
      } catch (error) {
        console.log('Error playing notification banner sound:', error);
      }

      // Auto-hide notification after 3 seconds
      setTimeout(() => {
        setVisibleNotifications(prev => prev.filter(n => n.id !== latestNotification.id));
      }, 3000);
    }
  }, [notifications, lastNotificationId]);

  const dismissNotification = (notificationId: number) => {
    setVisibleNotifications(prev => prev.filter(n => n.id !== notificationId));
  };

  const markAsRead = async (notificationId: number) => {
    try {
      await fetch(`/api/notifications/${notificationId}/read`, {
        method: 'PUT'
      });
      dismissNotification(notificationId);
    } catch (error) {
      console.error('Error marking notification as read:', error);
    }
  };

  const getNotificationIcon = (type: string) => {
    switch (type) {
      case 'order':
        return '📦';
      case 'delivery':
        return '🚚';
      case 'payment':
        return '💳';
      case 'product':
        return '🛍️';
      case 'store':
        return '🏪';
      case 'system':
        return '⚙️';
      default:
        return '🔔';
    }
  };

  const getNotificationColor = (type: string) => {
    switch (type) {
      case 'order':
        return 'bg-blue-500';
      case 'delivery':
        return 'bg-green-500';
      case 'payment':
        return 'bg-yellow-500';
      case 'product':
        return 'bg-purple-500';
      case 'store':
        return 'bg-orange-500';
      case 'system':
        return 'bg-gray-500';
      default:
        return 'bg-blue-500';
    }
  };

  if (!user || visibleNotifications.length === 0) {
    return null;
  }

  return (
    <div className="fixed top-0 left-0 right-0 z-50 space-y-2 p-2">
      {visibleNotifications.map((notification, index) => (
        <div
          key={notification.id}
          className={`transform transition-all duration-300 ease-in-out ${
            index === 0 ? 'translate-y-0 opacity-100' : 'translate-y-2 opacity-90'
          }`}
          style={{ marginTop: index * 10 }}
        >
          <div className={`${getNotificationColor(notification.type)} text-white rounded-lg shadow-lg mx-auto max-w-4xl`}>
            <div className="flex items-center justify-between p-3 sm:p-4">
              <div className="flex items-center space-x-3 flex-1 min-w-0">
                <div className="flex-shrink-0">
                  <div className="bg-white bg-opacity-20 rounded-full p-2">
                    <Bell className="h-4 w-4 text-white" />
                  </div>
                </div>
                <div className="flex-1 min-w-0">
                  <div className="flex items-center space-x-2 mb-1">
                    <span className="text-lg">{getNotificationIcon(notification.type)}</span>
                    <p className="font-semibold text-sm sm:text-base truncate">
                      {notification.title}
                    </p>
                    <Badge 
                      variant="secondary" 
                      className="bg-white bg-opacity-20 text-white text-xs"
                    >
                      {notification.type}
                    </Badge>
                  </div>
                  <p className="text-xs sm:text-sm text-white text-opacity-90 line-clamp-2">
                    {notification.message}
                  </p>
                </div>
              </div>
              <div className="flex items-center space-x-2 ml-3">
                <Button
                  variant="ghost"
                  size="sm"
                  className="text-white hover:bg-white hover:bg-opacity-20 text-xs p-2"
                  onClick={() => markAsRead(notification.id)}
                >
                  Mark Read
                </Button>
                <Button
                  variant="ghost"
                  size="sm"
                  className="text-white hover:bg-white hover:bg-opacity-20 p-1"
                  onClick={() => dismissNotification(notification.id)}
                >
                  <X className="h-4 w-4" />
                </Button>
              </div>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

client/src/components/NotificationCenter.content:
import { useState } from 'react';
import { Bell, Check, CheckCheck, X } from 'lucide-react';
import { Button } from '@/components/ui/button';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';
import { Badge } from '@/components/ui/badge';
import { ScrollArea } from '@/components/ui/scroll-area';
import { useNotifications } from '@/hooks/useNotifications';
import { formatDistanceToNow } from 'date-fns';

export default function NotificationCenter() {
  const [isOpen, setIsOpen] = useState(false);
  const {
    notifications,
    unreadCount,
    permission,
    requestPermission,
    markAsRead,
    markAllAsRead
  } = useNotifications();

  const handleNotificationClick = (notificationId: number, isRead: boolean) => {
    if (!isRead) {
      markAsRead(notificationId);
    }
  };

  const getNotificationIcon = (type: string) => {
    switch (type) {
      case 'order':
        return '🛍️';
      case 'delivery':
        return '🚚';
      case 'payment':
        return '💳';
      case 'product':
        return '📦';
      case 'store':
        return '🏪';
      case 'system':
        return '⚙️';
      default:
        return '🔔';
    }
  };

  const getNotificationColor = (type: string) => {
    switch (type) {
      case 'order':
        return 'bg-blue-100 text-blue-800';
      case 'delivery':
        return 'bg-green-100 text-green-800';
      case 'payment':
        return 'bg-yellow-100 text-yellow-800';
      case 'product':
        return 'bg-purple-100 text-purple-800';
      case 'store':
        return 'bg-orange-100 text-orange-800';
      case 'system':
        return 'bg-gray-100 text-gray-800';
      default:
        return 'bg-blue-100 text-blue-800';
    }
  };

  return (
    <div className="relative">
      {permission === 'default' && (
        <Button
          variant="outline"
          size="sm"
          onClick={requestPermission}
          className="mr-2 text-xs"
        >
          Enable Notifications
        </Button>
      )}

      <DropdownMenu open={isOpen} onOpenChange={setIsOpen}>
        <DropdownMenuTrigger asChild>
          <Button variant="ghost" size="sm" className="relative">
            <Bell className="h-5 w-5" />
            {unreadCount > 0 && (
              <Badge
                variant="destructive"
                className="absolute -top-1 -right-1 h-5 w-5 rounded-full p-0 text-xs flex items-center justify-center"
              >
                {unreadCount > 99 ? '99+' : unreadCount}
              </Badge>
            )}
          </Button>
        </DropdownMenuTrigger>

        <DropdownMenuContent align="end" className="w-80">
          <div className="flex items-center justify-between p-3 border-b">
            <h3 className="font-semibold">Notifications</h3>
            {unreadCount > 0 && (
              <Button
                variant="ghost"
                size="sm"
                onClick={markAllAsRead}
                className="text-xs"
              >
                <CheckCheck className="h-4 w-4 mr-1" />
                Mark all read
              </Button>
            )}
          </div>

          <DropdownMenuSeparator />

          <ScrollArea className="h-96">
            {notifications.length === 0 ? (
              <div className="p-4 text-center text-muted-foreground">
                No notifications yet
              </div>
            ) : (
              notifications.map((notification) => (
                <DropdownMenuItem
                  key={notification.id}
                  className={`p-3 cursor-pointer border-l-4 ${
                    notification.isRead 
                      ? 'border-transparent bg-background' 
                      : 'border-primary bg-primary/5'
                  }`}
                  onClick={() => handleNotificationClick(notification.id, notification.isRead)}
                >
                  <div className="flex items-start space-x-3 w-full">
                    <div className={`p-1 rounded-full text-sm ${getNotificationColor(notification.type)}`}>
                      {getNotificationIcon(notification.type)}
                    </div>

                    <div className="flex-1 min-w-0">
                      <div className="flex items-center justify-between">
                        <p className={`text-sm font-medium truncate ${
                          notification.isRead ? 'text-muted-foreground' : 'text-foreground'
                        }`}>
                          {notification.title}
                        </p>
                        {!notification.isRead && (
                          <div className="h-2 w-2 bg-primary rounded-full ml-2 flex-shrink-0" />
                        )}
                      </div>

                      <p className="text-xs text-muted-foreground mt-1 line-clamp-2">
                        {notification.message}
                      </p>

                      <p className="text-xs text-muted-foreground mt-1">
                        {(() => {
                          try {
                            if (!notification.createdAt) return 'Just now';
                            const date = new Date(notification.createdAt);
                            if (isNaN(date.getTime())) return 'Recently';
                            return formatDistanceToNow(date, { addSuffix: true });
                          } catch {
                            return 'Recently';
                          }
                        })()}
                      </p>
                    </div>
                  </div>
                </DropdownMenuItem>
              ))
            )}
          </ScrollArea>

          {notifications.length > 0 && (
            <>
              <DropdownMenuSeparator />
              <div className="p-2">
                <Button variant="ghost" className="w-full text-xs">
                  View all notifications
                </Button>
              </div>
            </>
          )}
        </DropdownMenuContent>
      </DropdownMenu>
    </div>
  );
}

client/src/components/PixabayImageSearch.content:
import React, { useState } from 'react';
import { useQuery, useQueryClient } from '@tanstack/react-query';
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Plus, Search, Download, Loader2, AlertCircle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface PixabayImage {
  id: string;
  urls: {
    raw: string;
    full: string;
    regular: string;
    small: string;
    thumb: string;
  };
  alt_description: string;
  description: string;
  user: {
    name: string;
    username: string;
  };
  links: {
    download: string;
    html: string;
  };
}

interface PixabayImageSearchResponse {
  total: number;
  total_pages: number;
  results: PixabayImage[];
}

interface PixabayImageSearchProps {
  onImageSelect: (imageUrl: string) => void;
  category?: string;
  trigger?: React.ReactNode;
  selectedImages?: string[];
  maxImages?: number;
  buttonText?: string;
}

export const PixabayImageSearch: React.FC<PixabayImageSearchProps> = ({
  onImageSelect,
  category = '',
  trigger,
  selectedImages = [],
  maxImages = 1,
  buttonText = 'Add Image from Pixabay'
}) => {
  const [searchQuery, setSearchQuery] = useState(category);
  const [selectedImageIds, setSelectedImageIds] = useState<string[]>([]);
  const [open, setOpen] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Search images from Pixabay
  const { data: searchResults, isLoading, refetch, error } = useQuery({
    queryKey: ['pixabay-search', searchQuery],
    queryFn: async (): Promise<PixabayImageSearchResponse> => {
      if (!searchQuery.trim()) return { total: 0, total_pages: 0, results: [] };

      const response = await fetch(`/api/pixabay/search?query=${encodeURIComponent(searchQuery)}&per_page=20`);
      if (!response.ok) {
        throw new Error('Failed to search images');
      }
      return response.json();
    },
    enabled: !!searchQuery,
    retry: 1
  });

  const handleImageSelect = (imageUrl: string, imageId: string) => {
    if (selectedImages.includes(imageUrl)) {
      toast({
        title: "Image already selected",
        description: "This image is already in your collection.",
        variant: "destructive"
      });
      return;
    }

    if (selectedImages.length >= maxImages) {
      toast({
        title: "Maximum images reached",
        description: `You can only select up to ${maxImages} images.`,
        variant: "destructive"
      });
      return;
    }

    onImageSelect(imageUrl);
    setSelectedImageIds(prev => [...prev, imageId]);

    toast({
      title: "Image added",
      description: "Image has been added to your collection."
    });

    if (maxImages === 1) {
      setOpen(false);
    }
  };

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      refetch();
    }
  };

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        {trigger || (
          <Button variant="outline" type="button">
            <Plus className="h-4 w-4 mr-2" />
            {buttonText}
          </Button>
        )}
      </DialogTrigger>
      <DialogContent className="max-w-4xl max-h-[80vh] overflow-hidden">
        <DialogHeader>
          <DialogTitle>Search Images with Pixabay</DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          {/* Search Form */}
          <form onSubmit={handleSearch} className="flex gap-2">
            <Input
              placeholder="Search for images..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="flex-1"
            />
            <Button type="submit" disabled={isLoading}>
              {isLoading ? (
                <Loader2 className="h-4 w-4 animate-spin" />
              ) : (
                <Search className="h-4 w-4" />
              )}
            </Button>
          </form>

          {/* Error Display */}
          {error && (
            <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded">
              <p className="font-medium flex items-center">
                <AlertCircle className="h-4 w-4 mr-2" />
                Error searching images:
              </p>
              <p className="text-sm">{error.message}</p>
            </div>
          )}

          {/* Success Message */}
          {searchResults && searchResults.results.length > 0 && (
            <div className="bg-green-50 border border-green-200 text-green-700 px-4 py-3 rounded">
              <p className="font-medium">✅ Search-Specific Images Found</p>
              <p className="text-sm">Found {searchResults.results.length} relevant images for "{searchQuery}"</p>
            </div>
          )}

          {/* Loading Indicator */}
          {isLoading && (
            <div className="flex items-center justify-center py-8">
              <Loader2 className="h-8 w-8 animate-spin text-blue-600" />
              <span className="ml-2 text-gray-600">Searching for images...</span>
            </div>
          )}

          {/* Results Grid */}
          {searchResults && searchResults.results.length > 0 && (
            <div className="max-h-[400px] overflow-y-auto">
              <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 p-4">
                {searchResults.results.map((image) => (
                  <div
                    key={image.id}
                    className={`relative group cursor-pointer rounded-lg overflow-hidden border-2 transition-all ${
                      selectedImageIds.includes(image.id)
                        ? 'border-blue-500 ring-2 ring-blue-200'
                        : 'border-gray-200 hover:border-gray-300'
                    }`}
                    onClick={() => handleImageSelect(image.urls.regular, image.id)}
                  >
                    <img
                      src={image.urls.small}
                      alt={image.alt_description}
                      className="w-full h-32 object-cover"
                    />
                    <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-40 transition-all duration-200 flex items-center justify-center">
                      <Button
                        size="sm"
                        variant="secondary"
                        className="opacity-0 group-hover:opacity-100 transition-opacity"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleImageSelect(image.urls.regular, image.id);
                        }}
                      >
                        <Download className="h-4 w-4 mr-1" />
                        Select
                      </Button>
                    </div>
                    {selectedImageIds.includes(image.id) && (
                      <div className="absolute top-2 right-2 bg-blue-500 text-white rounded-full p-1">
                        <Plus className="h-3 w-3" />
                      </div>
                    )}
                    <div className="absolute bottom-0 left-0 right-0 bg-black bg-opacity-60 text-white p-2 text-xs">
                      <p className="truncate">{image.alt_description}</p>
                      <p className="text-gray-300">by {image.user.name}</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* No Results */}
          {searchResults && searchResults.results.length === 0 && searchQuery && !isLoading && (
            <div className="text-center py-8">
              <AlertCircle className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <p className="text-gray-600">No images found for "{searchQuery}"</p>
              <p className="text-sm text-gray-500">Try a different search term</p>
            </div>
          )}

          {/* Getting Started */}
          {!searchQuery && (
            <div className="text-center py-8">
              <Search className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <p className="text-gray-600">Enter a search term to find images</p>
              <p className="text-sm text-gray-500">Search for products, categories, or any relevant keywords</p>
            </div>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
};

export default PixabayImageSearch;

client/src/components/ProductCard.tsx.content:
import { Link } from "wouter";
import { Star, ShoppingCart, Heart, MapPin, Clock } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tooltip, TooltipContent, TooltipTrigger } from "@/components/ui/tooltip";
import { useCart } from "@/hooks/useCart";
import { useWishlist } from "@/hooks/useWishlist";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import type { Product } from "@shared/schema";
import { getProductImage, getProductFallbackImage } from "@/utils/imageUtils";

interface ProductCardProps {
  product: Product & {
    storeDistance?: number;
    storeName?: string;
    deliveryTime?: string;
  };
}

export default function ProductCard({ product }: ProductCardProps) {
  const { addToCart } = useCart();
  const { toggleWishlist, isInWishlist } = useWishlist();
  const { user } = useAuth();
  const { toast } = useToast();

  const handleAddToCart = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();

    // Allow adding to cart even without login
    addToCart(product.id, 1);
    toast({
      title: "Added to cart",
      description: `${product.name} has been added to your cart.`,
    });
  };

  const handleWishlistToggle = async (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();

    if (!user) {
      toast({
        title: "Login required",
        description: "Please login to add items to your wishlist.",
        variant: "destructive",
      });
      return;
    }

    const wasInWishlist = isInWishlist(product.id);
    await toggleWishlist(product.id);

    toast({
      title: wasInWishlist ? "Removed from wishlist" : "Added to wishlist",
      description: `${product.name} has been ${wasInWishlist ? "removed from" : "added to"} your wishlist.`,
    });
  };

  const discount = product.originalPrice 
    ? Math.round(((Number(product.originalPrice) - Number(product.price)) / Number(product.originalPrice)) * 100)
    : 0;

  return (
    <Link href={`/products/${product.id}`}>
      <div className="product-card overflow-hidden">
        <div className="relative">
          <img
            src={getProductImage(product)}
            alt={product.name}
            className="w-full h-24 sm:h-32 md:h-40 object-cover"
            onError={(e) => {
              const target = e.target as HTMLImageElement;
              if (!target.src.includes('unsplash')) {
                target.src = getProductFallbackImage(product);
              }
            }}
            loading="lazy"
          />
          {discount > 0 && (
            <Badge className="absolute top-2 left-2 bg-red-500 text-white">
              {discount}% OFF
            </Badge>
          )}
        </div>

        <div className="p-2">
          <h3 className="font-medium text-foreground text-[10px] mb-1 line-clamp-2">
            {product.name}
          </h3>

          {/* Restaurant name and distance info for food items */}
          {product.storeName && (
            <div className="text-[9px] text-muted-foreground mb-1 flex items-center justify-between">
              <span className="truncate">{product.storeName}</span>
              {product.storeDistance !== undefined && (
                <div className="flex items-center gap-1 text-[8px] text-blue-600">
                  <MapPin className="h-2 w-2" />
                  <span>{product.storeDistance.toFixed(1)}km</span>
                </div>
              )}
            </div>
          )}

          {/* Delivery time for food items */}
          {product.deliveryTime && (
            <div className="text-[8px] text-muted-foreground mb-1 flex items-center gap-1">
              <Clock className="h-2 w-2" />
              <span>{product.deliveryTime}</span>
            </div>
          )}

          {/* Rating display */}
          {product.rating && parseFloat(product.rating) > 0 && (
            <div className="flex items-center gap-1 mb-1">
              <Star className="h-3 w-3 fill-yellow-400 text-yellow-400" />
              <span className="text-[10px] text-gray-600">
                {parseFloat(product.rating).toFixed(1)}
              </span>
            </div>
          )}

          <div className="flex items-center space-x-1 mb-2">
            <span className="text-xs font-bold text-foreground">
              ₹{Number(product.price).toLocaleString()}
            </span>
            {product.originalPrice && (
              <span className="text-[10px] text-muted-foreground line-through">
                ₹{Number(product.originalPrice).toLocaleString()}
              </span>
            )}
          </div>

          <div className="flex gap-2">
            <Button
              onClick={handleAddToCart}
              className="flex-1 btn-secondary text-xs py-1 h-8"
              size="sm"
            >
              <ShoppingCart className="h-3 w-3" />
            </Button>
            <Button
              onClick={handleWishlistToggle}
              variant="outline"
              className="text-xs py-1 h-8 w-8 p-0"
              size="sm"
            >
              <Heart 
                className={`h-3 w-3 transition-colors ${
                  isInWishlist(product.id) 
                    ? "fill-red-500 text-red-500" 
                    : "text-gray-600 hover:text-red-400"
                }`}
              />
            </Button>
          </div>
        </div>
      </div>
    </Link>
  );
}

client/src/components/ProductReviews.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Star, ThumbsUp, User, Calendar, Edit2, Trash2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Textarea } from "@/components/ui/textarea";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { StarRating } from "@/components/ui/star-rating";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface Review {
  id: number;
  rating: number;
  title?: string;
  comment?: string;
  createdAt: string;
  isVerifiedPurchase: boolean;
  helpfulCount: number;
  customer?: {
    id: number;
    username: string;
    fullName: string;
  };
}

interface ProductReviewsProps {
  productId: number;
  productName: string;
}

export function ProductReviews({ productId, productName }: ProductReviewsProps) {
  const [showReviewForm, setShowReviewForm] = useState(false);
  const [reviewData, setReviewData] = useState({
    rating: 0,
    title: "",
    comment: ""
  });
  const [filterRating, setFilterRating] = useState<number | null>(null);

  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: reviews = [], isLoading } = useQuery({
    queryKey: ["/api/products", productId, "reviews", { minRating: filterRating }],
    queryFn: async () => {
      const response = await fetch(`/api/products/${productId}/reviews${filterRating ? `?minRating=${filterRating}` : ""}`);
      const data = await response.json();
      // Ensure we always return an array
      return Array.isArray(data) ? data : [];
    }
  });

  const createReviewMutation = useMutation({
    mutationFn: (reviewData: any) => 
      apiRequest("/api/reviews", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(reviewData)
      }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products", productId, "reviews"] });
      setShowReviewForm(false);
      setReviewData({ rating: 0, title: "", comment: "" });
      toast({
        title: "Review submitted",
        description: "Your review has been posted successfully!"
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to submit review",
        variant: "destructive"
      });
    }
  });

  const markHelpfulMutation = useMutation({
    mutationFn: (reviewId: number) => 
      apiRequest(`/api/reviews/${reviewId}/helpful`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({ userId: user?.id || 9 }) // Use logged in user or default for testing
      }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products", productId, "reviews"] });
      toast({
        title: "Thank you!",
        description: "Review marked as helpful"
      });
    },
    onError: (error: any) => {
      const errorMessage = error?.error || error?.message || "Failed to mark review as helpful";
      toast({
        title: "Error",
        description: errorMessage,
        variant: "destructive"
      });
    }
  });

  const handleSubmitReview = () => {
    if (!user) {
      toast({
        title: "Login required",
        description: "Please login to submit a review",
        variant: "destructive"
      });
      return;
    }

    if (reviewData.rating === 0) {
      toast({
        title: "Rating required",
        description: "Please provide a rating",
        variant: "destructive"
      });
      return;
    }

    const reviewPayload = {
      productId,
      customerId: user.id,
      rating: reviewData.rating,
      title: reviewData.title || null,
      comment: reviewData.comment || null
    };

    console.log("Submitting review with payload:", reviewPayload);
    createReviewMutation.mutate(reviewPayload);
  };

  const averageRating = reviews.length > 0 
    ? reviews.reduce((sum: number, review: Review) => sum + review.rating, 0) / reviews.length 
    : 0;

  const ratingCounts = [5, 4, 3, 2, 1].map(rating => ({
    rating,
    count: reviews.filter((review: Review) => review.rating === rating).length,
    percentage: reviews.length > 0 
      ? (reviews.filter((review: Review) => review.rating === rating).length / reviews.length) * 100 
      : 0
  }));

  // Check if current user already reviewed this product
  const userHasReviewed = user && reviews.some((review: Review) => review.customer?.id === user.id);

  if (isLoading) {
    return (
      <div className="space-y-4">
        <div className="animate-pulse">
          <div className="h-4 bg-gray-200 rounded w-1/4 mb-2"></div>
          <div className="h-20 bg-gray-200 rounded"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      {/* Rating Summary */}
      <Card>
        <CardHeader className="pb-3">
          <CardTitle className="text-lg">Customer Reviews</CardTitle>
        </CardHeader>
        <CardContent className="pt-0">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {/* Overall Rating */}
            <div className="text-center">
              <div className="text-2xl font-bold text-gray-900 dark:text-white mb-1">
                {averageRating.toFixed(1)}
              </div>
              <StarRating rating={averageRating} readonly size="md" className="justify-center mb-1" />
              <p className="text-xs text-gray-600 dark:text-gray-400">
                {reviews.length} review{reviews.length !== 1 ? 's' : ''}
              </p>
            </div>

            {/* Rating Breakdown */}
            <div className="space-y-1">
              {ratingCounts.map(({ rating, count, percentage }) => (
                <div key={rating} className="flex items-center gap-2">
                  <span className="text-xs w-6">{rating}</span>
                  <Star className="h-3 w-3 fill-yellow-400 text-yellow-400" />
                  <div className="flex-1 bg-gray-200 dark:bg-gray-700 rounded-full h-1.5">
                    <div 
                      className="bg-yellow-400 h-1.5 rounded-full" 
                      style={{ width: `${percentage}%` }}
                    />
                  </div>
                  <span className="text-xs text-gray-600 dark:text-gray-400 w-8">
                    {count}
                  </span>
                </div>
              ))}
            </div>
          </div>

          {/* Filter Controls */}
          <div className="flex flex-wrap gap-1 mt-3">
            <Button
              variant={filterRating === null ? "default" : "outline"}
              size="sm"
              className="h-7 px-3 text-xs"
              onClick={() => setFilterRating(null)}
            >
              All
            </Button>
            {[5, 4, 3, 2, 1].map(rating => (
              <Button
                key={rating}
                variant={filterRating === rating ? "default" : "outline"}
                size="sm"
                className="h-7 px-3 text-xs"
                onClick={() => setFilterRating(rating)}
              >
                {rating}★
              </Button>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* Write Review Section */}
      {user && !userHasReviewed && (
        <Card>
          <CardHeader className="pb-3">
            <CardTitle className="text-base">Write a Review</CardTitle>
          </CardHeader>
          <CardContent className="pt-0">
            {!showReviewForm ? (
              <Button onClick={() => setShowReviewForm(true)}>
                Write a Review
              </Button>
            ) : (
              <div className="space-y-3">
                <div>
                  <label className="block text-xs font-medium mb-1">
                    Rating *
                  </label>
                  <StarRating
                    rating={reviewData.rating}
                    onRatingChange={(rating) => setReviewData(prev => ({ ...prev, rating }))}
                    size="md"
                  />
                </div>

                <div>
                  <label className="block text-xs font-medium mb-1">
                    Title (Optional)
                  </label>
                  <Input
                    placeholder="Summarize your review..."
                    value={reviewData.title}
                    onChange={(e) => setReviewData(prev => ({ ...prev, title: e.target.value }))}
                    className="h-8"
                  />
                </div>

                <div>
                  <label className="block text-xs font-medium mb-1">
                    Review (Optional)
                  </label>
                  <Textarea
                    placeholder={`Share your thoughts about ${productName}...`}
                    value={reviewData.comment}
                    onChange={(e) => setReviewData(prev => ({ ...prev, comment: e.target.value }))}
                    rows={3}
                    className="text-sm"
                  />
                </div>

                <div className="flex gap-2">
                  <Button 
                    onClick={handleSubmitReview}
                    disabled={createReviewMutation.isPending || reviewData.rating === 0}
                  >
                    {createReviewMutation.isPending ? "Submitting..." : "Submit Review"}
                  </Button>
                  <Button 
                    variant="outline" 
                    onClick={() => {
                      setShowReviewForm(false);
                      setReviewData({ rating: 0, title: "", comment: "" });
                    }}
                  >
                    Cancel
                  </Button>
                </div>
              </div>
            )}
          </CardContent>
        </Card>
      )}

      {/* Existing Reviews */}
      <div className="space-y-3">
        {reviews.length === 0 ? (
          <Card>
            <CardContent className="text-center py-6">
              <p className="text-gray-500 dark:text-gray-400 text-sm">
                No reviews yet. Be the first to review this product!
              </p>
            </CardContent>
          </Card>
        ) : (
          reviews.map((review: Review) => (
            <Card key={review.id}>
              <CardContent className="pt-4 pb-4">
                <div className="flex items-start justify-between mb-3">
                  <div className="flex items-center gap-2">
                    <div className="h-8 w-8 bg-gray-200 dark:bg-gray-700 rounded-full flex items-center justify-center">
                      <User className="h-4 w-4 text-gray-600 dark:text-gray-400" />
                    </div>
                    <div>
                      <p className="font-medium text-sm">
                        {review.customer?.fullName || review.customer?.username || "Anonymous"}
                      </p>
                      <div className="flex items-center gap-2 text-xs text-gray-600 dark:text-gray-400">
                        <Calendar className="h-3 w-3" />
                        {new Date(review.createdAt).toLocaleDateString()}
                        {review.isVerifiedPurchase && (
                          <Badge variant="secondary" className="text-xs h-4">
                            Verified
                          </Badge>
                        )}
                      </div>
                    </div>
                  </div>
                  <StarRating rating={review.rating} readonly size="sm" />
                </div>

                {review.title && (
                  <h4 className="font-medium text-sm mb-2">{review.title}</h4>
                )}

                {review.comment && (
                  <p className="text-gray-700 dark:text-gray-300 mb-3 text-sm leading-relaxed">
                    {review.comment}
                  </p>
                )}

                <div className="flex items-center gap-4 text-xs">
                  <button 
                    onClick={() => markHelpfulMutation.mutate(review.id)}
                    disabled={markHelpfulMutation.isPending}
                    className="flex items-center gap-1 text-gray-600 dark:text-gray-400 hover:text-blue-600 dark:hover:text-blue-400 disabled:opacity-50 transition-colors"
                  >
                    <ThumbsUp className="h-3 w-3" />
                    {markHelpfulMutation.isPending ? "..." : `Helpful (${review.helpfulCount})`}
                  </button>
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>
    </div>
  );
}

client/src/components/PushNotificationSe.content:
import React, { useState } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Switch } from '@/components/ui/switch';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { Bell, BellOff, Smartphone, AlertCircle, CheckCircle } from 'lucide-react';
import { usePushNotificationSetup } from '@/hooks/useFirebaseMessaging';
import { useUser } from '@/hooks/use-user';

export default function PushNotificationSettings() {
  const { user } = useUser();
  const { 
    isSupported, 
    isLoading, 
    error, 
    token, 
    isSetup, 
    requestPermission, 
    setupPushNotifications 
  } = usePushNotificationSetup(user?.id);

  const [notifications, setNotifications] = useState({
    orderUpdates: true,
    deliveryAlerts: true,
    promotions: false,
    newProducts: false,
  });

  const handleNotificationToggle = (type: keyof typeof notifications) => {
    setNotifications(prev => ({
      ...prev,
      [type]: !prev[type]
    }));
  };

  const handleEnablePushNotifications = async () => {
    if (!user?.id) return;

    try {
      const hasPermission = await requestPermission();
      if (hasPermission) {
        await setupPushNotifications();
      }
    } catch (error) {
      console.error('Error enabling push notifications:', error);
    }
  };

  if (isLoading) {
    return (
      <Card>
        <CardContent className="p-6">
          <div className="flex items-center gap-2">
            <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-gray-900"></div>
            <span>Loading notification settings...</span>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center gap-2">
          <Bell className="h-5 w-5" />
          <CardTitle>Push Notifications</CardTitle>
        </div>
        <CardDescription>
          Manage your notification preferences for order updates, promotions, and more.
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Browser Support Status */}
        <div className="flex items-center gap-2 p-3 bg-gray-50 rounded-lg">
          <Smartphone className="h-4 w-4" />
          <span className="text-sm">Browser Support:</span>
          {isSupported ? (
            <Badge variant="secondary" className="bg-green-100 text-green-800">
              <CheckCircle className="h-3 w-3 mr-1" />
              Supported
            </Badge>
          ) : (
            <Badge variant="destructive">
              <BellOff className="h-3 w-3 mr-1" />
              Not Supported
            </Badge>
          )}
        </div>

        {/* Error Display */}
        {error && (
          <div className="flex items-center gap-2 p-3 bg-red-50 border border-red-200 rounded-lg">
            <AlertCircle className="h-4 w-4 text-red-600" />
            <span className="text-sm text-red-700">{error}</span>
          </div>
        )}

        {/* Setup Status */}
        {isSupported && !error && (
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <div>
                <h4 className="font-medium">Push Notifications</h4>
                <p className="text-sm text-gray-600">
                  {isSetup 
                    ? 'You will receive push notifications for selected events'
                    : 'Enable push notifications to stay updated'
                  }
                </p>
              </div>
              <div className="flex items-center gap-2">
                {isSetup ? (
                  <Badge variant="secondary" className="bg-green-100 text-green-800">
                    <CheckCircle className="h-3 w-3 mr-1" />
                    Enabled
                  </Badge>
                ) : (
                  <Button onClick={handleEnablePushNotifications} size="sm">
                    Enable Notifications
                  </Button>
                )}
              </div>
            </div>

            {/* Notification Types */}
            {isSetup && (
              <div className="space-y-4 border-t pt-4">
                <h4 className="font-medium">Notification Types</h4>

                <div className="space-y-3">
                  <div className="flex items-center justify-between">
                    <div>
                      <Label htmlFor="orderUpdates" className="font-medium">
                        Order Updates
                      </Label>
                      <p className="text-sm text-gray-600">
                        Get notified about order status changes
                      </p>
                    </div>
                    <Switch
                      id="orderUpdates"
                      checked={notifications.orderUpdates}
                      onCheckedChange={() => handleNotificationToggle('orderUpdates')}
                    />
                  </div>

                  <div className="flex items-center justify-between">
                    <div>
                      <Label htmlFor="deliveryAlerts" className="font-medium">
                        Delivery Alerts
                      </Label>
                      <p className="text-sm text-gray-600">
                        Real-time updates on delivery progress
                      </p>
                    </div>
                    <Switch
                      id="deliveryAlerts"
                      checked={notifications.deliveryAlerts}
                      onCheckedChange={() => handleNotificationToggle('deliveryAlerts')}
                    />
                  </div>

                  <div className="flex items-center justify-between">
                    <div>
                      <Label htmlFor="promotions" className="font-medium">
                        Promotions & Offers
                      </Label>
                      <p className="text-sm text-gray-600">
                        Special deals and discount notifications
                      </p>
                    </div>
                    <Switch
                      id="promotions"
                      checked={notifications.promotions}
                      onCheckedChange={() => handleNotificationToggle('promotions')}
                    />
                  </div>

                  <div className="flex items-center justify-between">
                    <div>
                      <Label htmlFor="newProducts" className="font-medium">
                        New Products
                      </Label>
                      <p className="text-sm text-gray-600">
                        Updates about new products from your favorite stores
                      </p>
                    </div>
                    <Switch
                      id="newProducts"
                      checked={notifications.newProducts}
                      onCheckedChange={() => handleNotificationToggle('newProducts')}
                    />
                  </div>
                </div>
              </div>
            )}

            {/* Debug Info (only in development) */}
            {process.env.NODE_ENV === 'development' && token && (
              <div className="border-t pt-4">
                <details className="text-xs">
                  <summary className="cursor-pointer font-medium">Debug Info</summary>
                  <div className="mt-2 p-2 bg-gray-100 rounded text-xs font-mono break-all">
                    Token: {token.substring(0, 20)}...
                  </div>
                </details>
              </div>
            )}
          </div>
        )}

        {/* Fallback for unsupported browsers */}
        {!isSupported && (
          <div className="text-center py-6">
            <BellOff className="h-12 w-12 text-gray-400 mx-auto mb-3" />
            <h4 className="font-medium mb-2">Push Notifications Not Available</h4>
            <p className="text-sm text-gray-600">
              Your browser doesn't support push notifications. 
              Try using a modern browser like Chrome, Firefox, or Safari.
            </p>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/QuickRating.tsx.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Star, MessageSquare } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Textarea } from "@/components/ui/textarea";
import { StarRating } from "@/components/ui/star-rating";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface QuickRatingProps {
  productId: number;
  productName: string;
  onReviewSubmitted?: () => void;
}

export function QuickRating({ productId, productName, onReviewSubmitted }: QuickRatingProps) {
  const [showForm, setShowForm] = useState(false);
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState("");

  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const submitRatingMutation = useMutation({
    mutationFn: (data: any) => 
      apiRequest("/api/reviews", {
        method: "POST",
        body: JSON.stringify(data)
      }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products", productId] });
      setShowForm(false);
      setRating(0);
      setComment("");
      onReviewSubmitted?.();
      toast({
        title: "Rating submitted",
        description: "Thank you for your feedback!"
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to submit rating",
        variant: "destructive"
      });
    }
  });

  const handleSubmit = () => {
    if (!user) {
      toast({
        title: "Login required",
        description: "Please login to rate this product",
        variant: "destructive"
      });
      return;
    }

    if (rating === 0) {
      toast({
        title: "Rating required",
        description: "Please select a rating",
        variant: "destructive"
      });
      return;
    }

    submitRatingMutation.mutate({
      productId,
      customerId: user.id,
      rating,
      comment: comment.trim() || null
    });
  };

  if (!user) {
    return null;
  }

  return (
    <Card className="border-dashed">
      <CardContent className="p-4">
        {!showForm ? (
          <div className="text-center">
            <Button
              variant="outline"
              onClick={() => setShowForm(true)}
              className="gap-2"
            >
              <Star className="h-4 w-4" />
              Rate this product
            </Button>
          </div>
        ) : (
          <div className="space-y-4">
            <div className="text-center">
              <p className="text-sm font-medium mb-2">Rate {productName}</p>
              <StarRating
                rating={rating}
                onRatingChange={setRating}
                size="lg"
                className="justify-center"
              />
            </div>

            <div>
              <Textarea
                placeholder="Share your thoughts (optional)..."
                value={comment}
                onChange={(e) => setComment(e.target.value)}
                rows={3}
                className="resize-none"
              />
            </div>

            <div className="flex gap-2">
              <Button 
                onClick={handleSubmit}
                disabled={submitRatingMutation.isPending || rating === 0}
                className="flex-1"
              >
                {submitRatingMutation.isPending ? "Submitting..." : "Submit Rating"}
              </Button>
              <Button 
                variant="outline" 
                onClick={() => {
                  setShowForm(false);
                  setRating(0);
                  setComment("");
                }}
              >
                Cancel
              </Button>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

