import { useQuery } from "@tanstack/react-query";
import { Link } from "wouter";
import { useAuth } from "@/hooks/useAuth";
import { Home, ChefHat } from "lucide-react";
import type { Store } from "@shared/schema";

export default function SmartDashboardLink() {
  const { user } = useAuth();

  // Fetch user's store to determine dashboard type
  const { data: stores = [] } = useQuery<Store[]>({
    queryKey: [`/api/stores/user/${user?.id}`],
    enabled: !!user?.id && user?.role === 'shopkeeper',
  });

  // Determine if user has a restaurant
  const hasRestaurant = stores.some(store => 
    store.storeType === 'restaurant' || isRestaurantByName(store.name)
  );

  if (hasRestaurant) {
    return (
      <Link href="/restaurant/dashboard" className="flex items-center space-x-1 hover:text-accent transition-colors">
        <ChefHat className="h-4 w-4" />
        <span>Restaurant Dashboard</span>
      </Link>
    );
  }

  return (
    <Link href="/seller/dashboard" className="flex items-center space-x-1 hover:text-accent transition-colors">
      <Home className="h-4 w-4" />
      <span>Dashboard</span>
    </Link>
  );
}

// Helper function to detect restaurant by name
function isRestaurantByName(storeName: string): boolean {
  const restaurantKeywords = [
    'restaurant', 'cafe', 'food', 'kitchen', 'dining', 'eatery', 'bistro',
    'pizzeria', 'burger', 'pizza', 'chicken', 'biryani', 'dosa', 'samosa',
    'chinese', 'indian', 'nepali', 'thai', 'continental', 'fast food',
    'dhaba', 'hotel', 'canteen', 'mess', 'tiffin', 'sweet', 'bakery',
    'tyres' // Your specific restaurant name
  ];

  const lowerName = storeName.toLowerCase();
  return restaurantKeywords.some(keyword => lowerName.includes(keyword));
}

client/src/components/SoundTestButton.ts.content:
import React, { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Slider } from '@/components/ui/slider';
import { Volume2, VolumeX, TestTube, Settings, Play } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { playSound, soundManager } from '@/lib/soundEffects';

export default function SoundTestButton() {
  const [isPlaying, setIsPlaying] = useState(false);
  const [soundEnabled, setSoundEnabled] = useState(soundManager.isEnabled());
  const [volume, setVolume] = useState(soundManager.getVolume() * 100);
  const { toast } = useToast();

  const testSounds = [
    { name: 'Cart Add', sound: 'cart-add', icon: '🛒' },
    { name: 'Notification', sound: 'notification', icon: '🔔' },
    { name: 'Success', sound: 'success', icon: '✅' },
    { name: 'Error', sound: 'error', icon: '❌' },
    { name: 'Button Click', sound: 'button-click', icon: '👆' },
    { name: 'Order Placed', sound: 'order-placed', icon: '🎉' },
    { name: 'Product Like', sound: 'product-like', icon: '❤️' },
    { name: 'Payment Success', sound: 'payment-success', icon: '💳' },
  ];

  const playTestSound = (soundName: string, displayName: string) => {
    setIsPlaying(true);
    playSound.buttonClick(); // Play button click first

    setTimeout(() => {
      soundManager.play(soundName, { force: true });
      toast({
        title: "Sound Test",
        description: `${displayName} sound played successfully!`,
      });
      setIsPlaying(false);
    }, 150);
  };

  const playAllSounds = () => {
    setIsPlaying(true);
    soundManager.test(); // This plays all sounds with delays
    toast({
      title: "Sound Test Suite",
      description: "Playing all sound effects...",
    });
    setTimeout(() => setIsPlaying(false), testSounds.length * 300 + 500);
  };

  const toggleSounds = () => {
    const newState = !soundEnabled;
    setSoundEnabled(newState);
    soundManager.setEnabled(newState);
    playSound.toggle();
    toast({
      title: newState ? "Sounds Enabled" : "Sounds Disabled",
      description: newState ? "Sound effects are now enabled" : "Sound effects are now disabled",
    });
  };

  const updateVolume = (newVolume: number[]) => {
    const volumeValue = newVolume[0] / 100;
    setVolume(newVolume[0]);
    soundManager.setVolume(volumeValue);
    playSound.buttonClick();
  };

  const testVibration = () => {
    if ('vibrate' in navigator) {
      // Test vibration pattern: short-long-short
      navigator.vibrate([200, 100, 200]);
      toast({
        title: "Vibration Test",
        description: "Vibration pattern sent to device",
      });
    } else {
      toast({
        title: "Vibration Not Supported",
        description: "This device doesn't support vibration",
        variant: "destructive"
      });
    }
  };

  // Only show in development
  if (process.env.NODE_ENV !== 'development') {
    return null;
  }

  return (
    <Card className="border-dashed border-2 border-green-200 bg-green-50">
      <CardHeader>
        <div className="flex items-center gap-2">
          <TestTube className="h-5 w-5 text-green-600" />
          <CardTitle className="text-green-800">Sound Effects & Settings</CardTitle>
        </div>
        <CardDescription className="text-green-700">
          Test and configure sound effects for the application
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Sound Control Section */}
        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              <Settings className="h-4 w-4 text-green-600" />
              <span className="font-medium text-green-800">Sound Settings</span>
            </div>
            <Button 
              onClick={toggleSounds}
              size="sm"
              variant={soundEnabled ? "default" : "outline"}
              className={soundEnabled ? "bg-green-600 hover:bg-green-700" : "border-green-300 text-green-700 hover:bg-green-100"}
            >
              {soundEnabled ? (
                <>
                  <Volume2 className="h-4 w-4 mr-2" />
                  Enabled
                </>
              ) : (
                <>
                  <VolumeX className="h-4 w-4 mr-2" />
                  Disabled
                </>
              )}
            </Button>
          </div>

          {/* Volume Slider */}
          <div className="space-y-2">
            <div className="flex justify-between items-center">
              <label className="text-sm font-medium text-green-700">Volume</label>
              <span className="text-sm text-green-600">{Math.round(volume)}%</span>
            </div>
            <Slider
              value={[volume]}
              onValueChange={updateVolume}
              max={100}
              min={0}
              step={5}
              className="w-full"
              disabled={!soundEnabled}
            />
          </div>
        </div>

        {/* Quick Test Buttons */}
        <div className="space-y-3">
          <div className="flex items-center gap-2">
            <Play className="h-4 w-4 text-green-600" />
            <span className="font-medium text-green-800">Quick Tests</span>
          </div>
          <div className="flex gap-2 flex-wrap">
            <Button 
              onClick={playAllSounds}
              disabled={isPlaying || !soundEnabled}
              size="sm"
              className="bg-green-600 hover:bg-green-700"
            >
              {isPlaying ? "Playing..." : "Test All Sounds"}
            </Button>

            <Button 
              onClick={testVibration}
              size="sm"
              variant="outline"
              className="border-green-300 text-green-700 hover:bg-green-100"
            >
              📳 Test Vibration
            </Button>
          </div>
        </div>

        {/* Individual Sound Tests */}
        <div className="space-y-3">
          <span className="font-medium text-green-800">Individual Sound Tests</span>
          <div className="grid grid-cols-2 gap-2">
            {testSounds.map(({ name, sound, icon }) => (
              <Button
                key={sound}
                onClick={() => playTestSound(sound, name)}
                disabled={isPlaying || !soundEnabled}
                size="sm"
                variant="outline"
                className="border-green-300 text-green-700 hover:bg-green-100 justify-start"
              >
                <span className="mr-2">{icon}</span>
                {name}
              </Button>
            ))}
          </div>
        </div>

        <div className="text-xs text-green-600 space-y-1 pt-2 border-t border-green-200">
          <p>• Sound effects enhance user experience with audio feedback</p>
          <p>• Vibration tests haptic feedback on mobile devices</p>
          <p>• Settings are automatically saved to local storage</p>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/StoreCard.tsx.content:
import { useState, useEffect } from "react";
import { Link } from "wouter";
import { MapPin, Star, Clock, Navigation, ExternalLink } from "lucide-react";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { calculateDistance, formatDistance, getCurrentUserLocation } from "@/lib/distance";

interface Store {
  id: number;
  name: string;
  description?: string;
  address: string;
  latitude?: string;
  longitude?: string;
  phone?: string;
  website?: string;
  logo?: string;
  coverImage?: string;
  rating: string;
  totalReviews: number;
  storeType: string;
  cuisineType?: string;
  deliveryTime?: string;
  isDeliveryAvailable?: boolean;
}

interface StoreCardProps {
  store: Store;
  showDistance?: boolean;
}

export default function StoreCard({ store, showDistance = true }: StoreCardProps) {
  const [distance, setDistance] = useState<number | null>(null);
  const [isCalculatingDistance, setIsCalculatingDistance] = useState(false);
  const [userLocation, setUserLocation] = useState<{ latitude: number; longitude: number } | null>(null);

  useEffect(() => {
    if (showDistance && store.latitude && store.longitude) {
      setIsCalculatingDistance(true);
      getCurrentUserLocation()
        .then((location) => {
          setUserLocation(location);
          const dist = calculateDistance(
            location,
            { latitude: parseFloat(store.latitude!), longitude: parseFloat(store.longitude!) }
          );
          setDistance(dist);
          setIsCalculatingDistance(false);
        })
        .catch(() => {
          // Silently fail if location access is denied
          setIsCalculatingDistance(false);
        });
    }
  }, [store.latitude, store.longitude, showDistance]);

  const openGoogleMaps = () => {
    if (store.latitude && store.longitude) {
      const url = `https://maps.google.com/?q=${store.latitude},${store.longitude}`;
      window.open(url, '_blank');
    }
  };

  const getDirections = () => {
    if (store.latitude && store.longitude && userLocation) {
      const url = `https://maps.google.com/maps?saddr=${userLocation.latitude},${userLocation.longitude}&daddr=${store.latitude},${store.longitude}`;
      window.open(url, '_blank');
    } else if (store.latitude && store.longitude) {
      const url = `https://maps.google.com/maps?daddr=${store.latitude},${store.longitude}`;
      window.open(url, '_blank');
    }
  };

  return (
    <Link href={`/store/${store.id}`}>
      <Card className="hover:shadow-lg transition-shadow duration-200 cursor-pointer group">
        <CardHeader className="pb-2 p-3">
          <div className="flex items-start justify-between">
            <div className="flex-1">
              <h3 className="font-semibold text-sm group-hover:text-primary transition-colors line-clamp-1">
                {store.name}
              </h3>
              <div className="flex items-center gap-1 mt-1">
                <Badge variant={store.storeType === 'restaurant' ? 'default' : 'secondary'} className="text-xs px-1 py-0">
                  {store.storeType === 'restaurant' ? 'Restaurant' : 'Retail'}
                </Badge>
                {store.cuisineType && (
                  <Badge variant="outline" className="text-xs px-1 py-0">{store.cuisineType}</Badge>
                )}
              </div>
            </div>
          <img
            src={store.logo || "https://images.unsplash.com/photo-1560472355-536de3962603?w=400&h=400&fit=crop&crop=center"}
            alt={`${store.name} logo`}
            className="w-8 h-8 rounded object-cover"
            onError={(e) => {
              e.currentTarget.src = "https://images.unsplash.com/photo-1560472355-536de3962603?w=400&h=400&fit=crop&crop=center";
            }}
          />
        </div>
      </CardHeader>

      <CardContent className="space-y-2 p-3 pt-0">
        <img
          src={store.coverImage || (store.storeType === 'restaurant' 
            ? "https://images.unsplash.com/photo-1517248135467-4c7edcad34c4?w=800&h=400&fit=crop&crop=center"
            : "https://images.unsplash.com/photo-1441986300917-64674bd600d8?w=800&h=400&fit=crop&crop=center")}
          alt={`${store.name} cover`}
          className="w-full h-20 rounded object-cover"
          onError={(e) => {
            e.currentTarget.src = store.storeType === 'restaurant' 
              ? "https://images.unsplash.com/photo-1517248135467-4c7edcad34c4?w=800&h=400&fit=crop&crop=center"
              : "https://images.unsplash.com/photo-1441986300917-64674bd600d8?w=800&h=400&fit=crop&crop=center";
          }}
        />

        <div className="flex items-center justify-between gap-2 text-xs text-muted-foreground">
          <div className="flex items-center gap-2 flex-1 min-w-0">
            <div className="flex items-center gap-1">
              <Star className="h-3 w-3 fill-yellow-400 text-yellow-400" />
              <span>{parseFloat(store.rating).toFixed(1)}</span>
              <span>({store.totalReviews})</span>
            </div>

            {store.deliveryTime && (
              <div className="flex items-center gap-1">
                <Clock className="h-3 w-3" />
                <span className="truncate">{store.deliveryTime}</span>
              </div>
            )}
          </div>

          {/* Distance Display - Fixed width to prevent overflow */}
          {showDistance && (
            <div className="flex-shrink-0">
              {isCalculatingDistance ? (
                <Badge variant="outline" className="text-xs animate-pulse">
                  <MapPin className="h-3 w-3 mr-1" />
                  ...
                </Badge>
              ) : distance !== null ? (
                <Badge 
                  variant={distance <= 1 ? "default" : distance <= 5 ? "secondary" : "outline"} 
                  className="text-xs font-medium bg-blue-50 text-blue-700 border-blue-200 hover:bg-blue-100"
                >
                  {formatDistance(distance)}
                </Badge>
              ) : null}
            </div>
          )}
        </div>

        <div className="space-y-1">
          <div className="flex items-center gap-1 text-xs">
            <MapPin className="h-3 w-3 text-muted-foreground flex-shrink-0" />
            <span className="text-muted-foreground flex-1 line-clamp-1">
              {store.address}
            </span>
          </div>

          {/* Show precise coordinates when available */}
          {store.latitude && store.longitude && (
            <div className="text-xs text-muted-foreground/70 font-mono">
              📍 {parseFloat(store.latitude).toFixed(4)}, {parseFloat(store.longitude).toFixed(4)}
            </div>
          )}
        </div>



        {store.latitude && store.longitude && (
          <div className="flex gap-1 pt-1">
            <Button
              variant="outline"
              size="sm"
              onClick={(e) => {
                e.preventDefault();
                e.stopPropagation();
                openGoogleMaps();
              }}
              className="flex-1 text-xs h-7 w-7 p-0"
            >
              <ExternalLink className="h-3 w-3" />
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={(e) => {
                e.preventDefault();
                e.stopPropagation();
                getDirections();
              }}
              className="flex-1 text-xs h-7 w-7 p-0"
            >
              <Navigation className="h-3 w-3" />
            </Button>
          </div>
        )}

        {store.isDeliveryAvailable && (
          <Badge className="w-full justify-center text-xs py-1">
            Delivery Available
          </Badge>
        )}
      </CardContent>
    </Card>
    </Link>
  );
}

client/src/components/StoreDistanceFilte.content:
import { useState, useEffect } from "react";
import { MapPin, Filter, Target } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { calculateDistance, getCurrentUserLocation, formatDistance } from "@/lib/distance";

interface Store {
  id: number;
  name: string;
  description?: string;
  address: string;
  latitude?: string;
  longitude?: string;
  phone?: string;
  website?: string;
  logo?: string;
  coverImage?: string;
  rating: string;
  totalReviews: number;
  storeType: string;
  cuisineType?: string;
  deliveryTime?: string;
  isDeliveryAvailable?: boolean;
}

interface StoreWithDistance extends Store {
  distance: number;
}

interface StoreDistanceFilterProps {
  stores: Store[];
  onFilteredStores: (stores: StoreWithDistance[]) => void;
  className?: string;
}

export default function StoreDistanceFilter({ stores, onFilteredStores, className }: StoreDistanceFilterProps) {
  const [userLocation, setUserLocation] = useState<{ latitude: number; longitude: number } | null>(null);
  const [isGettingLocation, setIsGettingLocation] = useState(false);
  const [sortBy, setSortBy] = useState<"distance" | "rating" | "name">("distance");
  const [maxDistance, setMaxDistance] = useState<number>(50); // km
  const [storesWithDistance, setStoresWithDistance] = useState<StoreWithDistance[]>([]);

  // Get user location
  const handleGetLocation = async () => {
    setIsGettingLocation(true);
    try {
      const location = await getCurrentUserLocation();
      setUserLocation(location);
    } catch (error) {
      console.error('Failed to get location:', error);
    } finally {
      setIsGettingLocation(false);
    }
  };

  // Calculate distances for all stores
  useEffect(() => {
    if (userLocation && stores.length > 0) {
      const storesWithDist: StoreWithDistance[] = stores
        .map(store => {
          if (!store.latitude || !store.longitude) {
            return { ...store, distance: Infinity };
          }

          const distance = calculateDistance(
            userLocation,
            { 
              latitude: parseFloat(store.latitude), 
              longitude: parseFloat(store.longitude) 
            }
          );

          return { ...store, distance };
        })
        .filter(store => store.distance <= maxDistance && store.distance !== Infinity);

      setStoresWithDistance(storesWithDist);
    } else {
      // If no location, show all stores without distance
      setStoresWithDistance(stores.map(store => ({ ...store, distance: 0 })));
    }
  }, [userLocation, stores, maxDistance]);

  // Sort and filter stores
  useEffect(() => {
    let sortedStores = [...storesWithDistance];

    switch (sortBy) {
      case "distance":
        sortedStores.sort((a, b) => a.distance - b.distance);
        break;
      case "rating":
        sortedStores.sort((a, b) => parseFloat(b.rating) - parseFloat(a.rating));
        break;
      case "name":
        sortedStores.sort((a, b) => a.name.localeCompare(b.name));
        break;
    }

    onFilteredStores(sortedStores);
  }, [storesWithDistance, sortBy, onFilteredStores]);

  return (
    <Card className={className}>
      <CardHeader className="pb-4">
        <CardTitle className="flex items-center gap-2 text-lg">
          <Filter className="h-5 w-5" />
          Store Distance Filter
        </CardTitle>
      </CardHeader>

      <CardContent className="space-y-4">
        {/* Location Status */}
        <div className="space-y-2">
          {!userLocation ? (
            <Button 
              onClick={handleGetLocation} 
              disabled={isGettingLocation}
              variant="outline"
              className="w-full"
            >
              <Target className="h-4 w-4 mr-2" />
              {isGettingLocation ? "Getting Location..." : "Get My Location"}
            </Button>
          ) : (
            <div className="flex items-center justify-between p-3 bg-green-50 border border-green-200 rounded-lg">
              <div className="flex items-center gap-2">
                <Target className="h-4 w-4 text-green-600" />
                <span className="text-sm text-green-700 font-medium">
                  Location Found
                </span>
              </div>
              <Badge variant="outline" className="text-xs">
                {userLocation.latitude.toFixed(4)}, {userLocation.longitude.toFixed(4)}
              </Badge>
            </div>
          )}
        </div>

        {/* Filters */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="space-y-2">
            <label className="text-sm font-medium">Sort By</label>
            <Select value={sortBy} onValueChange={(value: any) => setSortBy(value)}>
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="distance">Closest First</SelectItem>
                <SelectItem value="rating">Highest Rated</SelectItem>
                <SelectItem value="name">Alphabetical</SelectItem>
              </SelectContent>
            </Select>
          </div>

          <div className="space-y-2">
            <label className="text-sm font-medium">Max Distance</label>
            <Select value={maxDistance.toString()} onValueChange={(value) => setMaxDistance(parseInt(value))}>
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="1">Within 1 km</SelectItem>
                <SelectItem value="2">Within 2 km</SelectItem>
                <SelectItem value="5">Within 5 km</SelectItem>
                <SelectItem value="10">Within 10 km</SelectItem>
                <SelectItem value="25">Within 25 km</SelectItem>
                <SelectItem value="50">Within 50 km</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </div>

        {/* Results Summary */}
        {userLocation && (
          <div className="flex items-center justify-between text-sm text-muted-foreground">
            <span>
              {storesWithDistance.length} stores within {maxDistance}km
            </span>
            {storesWithDistance.length > 0 && sortBy === "distance" && (
              <span>
                Closest: {formatDistance(storesWithDistance[0]?.distance || 0)}
              </span>
            )}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

