import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { 
  Users, 
  ShoppingCart, 
  Package, 
  DollarSign, 
  TrendingUp, 
  UserCheck, 
  UserX, 
  Clock, 
  Shield, 
  Store, 
  Eye,
  CheckCircle,
  XCircle,
  AlertCircle,
  Settings,
  BarChart3,
  Gift,
  Ticket,
  MessageSquare,
  Image,
  Tags,
  CreditCard,
  FileText,
  Ban,
  RefreshCw,
  Download,
  Search,
  Filter,
  MapPin,
  Phone,
  Mail,
  Calendar,
  UserMinus,
  Edit
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { User, Product, Order, Coupon, Banner, PaymentTransaction, SupportTicket } from "@shared/schema";

export default function AdminDashboard() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [adminUser, setAdminUser] = useState<any>(null);
  const [selectedUser, setSelectedUser] = useState<User | null>(null);
  const [rejectReason, setRejectReason] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [filterRole, setFilterRole] = useState("all");
  const [filterStatus, setFilterStatus] = useState("all");
  const [showUserDetails, setShowUserDetails] = useState(false);

  useEffect(() => {
    const stored = localStorage.getItem("adminUser");
    if (stored) {
      setAdminUser(JSON.parse(stored));
    } else {
      setLocation("/admin/login");
    }
  }, [setLocation]);

  // Fetch dashboard data
  const { data: allUsers = [], refetch: refetchUsers } = useQuery({
    queryKey: ["/api/admin/users"],
    enabled: !!adminUser,
  });

  const { data: pendingUsers = [] } = useQuery({
    queryKey: ["/api/admin/users/pending"],
    enabled: !!adminUser,
  });

  const { data: allProducts = [] } = useQuery({
    queryKey: ["/api/products"],
    enabled: !!adminUser,
  });

  const { data: allOrders = [] } = useQuery({
    queryKey: ["/api/admin/orders"],
    enabled: !!adminUser,
  });

  const { data: allStores = [] } = useQuery({
    queryKey: ["/api/stores"],
    enabled: !!adminUser,
  });

  const { data: analytics } = useQuery({
    queryKey: ["/api/admin/analytics/stats"],
    enabled: !!adminUser,
  });

  const { data: coupons = [] } = useQuery({
    queryKey: ["/api/admin/coupons"],
    enabled: !!adminUser,
  });

  const { data: banners = [] } = useQuery({
    queryKey: ["/api/admin/banners"],
    enabled: !!adminUser,
  });

  const { data: transactions = [] } = useQuery({
    queryKey: ["/api/admin/transactions"],
    enabled: !!adminUser,
  });

  const { data: supportTickets = [] } = useQuery({
    queryKey: ["/api/admin/support-tickets"],
    enabled: !!adminUser,
  });

  // User approval mutations
  const approveMutation = useMutation({
    mutationFn: async (userId: number) => {
      const response = await apiRequest("POST", `/api/admin/users/${userId}/approve`, { adminId: adminUser.id });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/users"] });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/users/pending"] });
      toast({
        title: "User approved",
        description: "Shopkeeper account has been approved successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Approval failed",
        description: "Failed to approve user. Please try again.",
        variant: "destructive",
      });
    },
  });

  const rejectMutation = useMutation({
    mutationFn: async ({ userId, reason }: { userId: number; reason: string }) => {
      const response = await apiRequest("POST", `/api/admin/users/${userId}/reject`, { 
        adminId: adminUser.id, 
        reason 
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/users"] });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/users/pending"] });
      setRejectReason("");
      toast({
        title: "User rejected",
        description: "Shopkeeper account has been rejected.",
      });
    },
    onError: () => {
      toast({
        title: "Rejection failed",
        description: "Failed to reject user. Please try again.",
        variant: "destructive",
      });
    },
  });

  const banUserMutation = useMutation({
    mutationFn: async (userId: number) => {
      const response = await apiRequest("PUT", `/api/admin/users/${userId}/ban`, { 
        reason: "Banned by admin" 
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/users"] });
      toast({
        title: "User banned",
        description: "User has been banned successfully.",
      });
    },
  });

  const deleteUserCompletelyMutation = useMutation({
    mutationFn: async ({ userId, reason }: { userId: number; reason?: string }) => {
      const response = await apiRequest("DELETE", `/api/admin/users/${userId}/complete`, {
        adminId: adminUser.id,
        reason: reason || "Deleted by admin"
      });
      return response.json();
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/users"] });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/users/pending"] });
      toast({
        title: "User deleted completely",
        description: `All user data removed: ${data.deletedData?.storesDeleted || 0} stores, ${data.deletedData?.productsDeleted || 0} products, ${data.deletedData?.ordersDeleted || 0} orders`,
      });
    },
    onError: (error: any) => {
      toast({
        title: "Deletion failed",
        description: error?.message || "Failed to delete user completely",
        variant: "destructive",
      });
    },
  });

  const handleLogout = () => {
    localStorage.removeItem("adminUser");
    setLocation("/admin/login");
    toast({
      title: "Logged out",
      description: "You have been logged out successfully.",
    });
  };

  const handleRefresh = async () => {
    try {
      await refetchUsers();
      toast({
        title: "Data refreshed",
        description: "User data has been refreshed successfully.",
      });
    } catch (error) {
      toast({
        title: "Refresh failed",
        description: "Failed to refresh data. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleExport = () => {
    try {
      const filteredUsers = getFilteredUsers();
      const csvData = [
        // CSV Header
        ['ID', 'Full Name', 'Username', 'Email', 'Phone', 'Role', 'Status', 'City', 'State', 'Address', 'Created Date', 'Approval Date', 'Approved By', 'Rejection Reason'].join(','),
        // CSV Data
        ...filteredUsers.map(user => [
          user.id,
          `"${user.fullName}"`,
          `"${user.username || ''}"`,
          `"${user.email}"`,
          `"${user.phone || ''}"`,
          user.role,
          user.status,
          `"${user.city || ''}"`,
          `"${user.state || ''}"`,
          `"${user.address || ''}"`,
          new Date(user.createdAt).toLocaleDateString(),
          user.approvalDate ? new Date(user.approvalDate).toLocaleDateString() : '',
          user.approvedBy || '',
          `"${user.rejectionReason || ''}"`
        ].join(','))
      ].join('\n');

      const blob = new Blob([csvData], { type: 'text/csv' });
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `users_export_${new Date().toISOString().split('T')[0]}.csv`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);

      toast({
        title: "Export successful",
        description: `Exported ${filteredUsers.length} users to CSV file.`,
      });
    } catch (error) {
      toast({
        title: "Export failed",
        description: "Failed to export data. Please try again.",
        variant: "destructive",
      });
    }
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case "active":
        return <Badge variant="default" className="bg-green-100 text-green-800"><CheckCircle className="h-3 w-3 mr-1" />Active</Badge>;
      case "pending":
        return <Badge variant="secondary" className="bg-yellow-100 text-yellow-800"><Clock className="h-3 w-3 mr-1" />Pending</Badge>;
      case "rejected":
        return <Badge variant="destructive"><XCircle className="h-3 w-3 mr-1" />Rejected</Badge>;
      case "suspended":
        return <Badge variant="destructive" className="bg-red-100 text-red-800"><Ban className="h-3 w-3 mr-1" />Banned</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  const formatCurrency = (amount: string | number) => {
    return `Rs. ${parseFloat(amount.toString()).toLocaleString()}`;
  };

  const getFilteredUsers = () => {
    return allUsers.filter((user: User) => {
      const matchesSearch = !searchTerm || 
        user.fullName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.username?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.phone?.includes(searchTerm);

      const matchesRole = filterRole === "all" || user.role === filterRole;
      const matchesStatus = filterStatus === "all" || user.status === filterStatus;

      return matchesSearch && matchesRole && matchesStatus;
    });
  };

  if (!adminUser) {
    return null;
  }

  const activeShopkeepers = allUsers.filter(user => user.role === "shopkeeper" && user.status === "active").length;
  const totalRevenue = transactions.reduce((sum: number, transaction: PaymentTransaction) => {
    return transaction.status === "completed" ? sum + parseFloat(transaction.amount) : sum;
  }, 0);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center">
              <Shield className="h-8 w-8 text-blue-600 mr-3" />
              <div>
                <h1 className="text-2xl font-bold text-gray-900">Admin Dashboard</h1>
                <p className="text-sm text-gray-500">Siraha Bazaar Administration</p>
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <span className="text-sm text-gray-600">Welcome, {adminUser.fullName}</span>
              <Button variant="outline" onClick={handleLogout}>
                Logout
              </Button>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <Card>
            <CardContent className="p-6">
              <div className="flex items-center">
                <Users className="h-8 w-8 text-blue-600" />
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Total Users</p>
                  <p className="text-2xl font-bold text-gray-900">{allUsers.length}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center">
                <Store className="h-8 w-8 text-green-600" />
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Active Stores</p>
                  <p className="text-2xl font-bold text-gray-900">{allStores.length}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center">
                <Package className="h-8 w-8 text-purple-600" />
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Total Products</p>
                  <p className="text-2xl font-bold text-gray-900">{allProducts.length}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center">
                <DollarSign className="h-8 w-8 text-yellow-600" />
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Total Revenue</p>
                  <p className="text-2xl font-bold text-gray-900">{formatCurrency(totalRevenue)}</p>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Enhanced User Management Section */}
        <Card>
          <CardHeader>
            <div className="flex justify-between items-center">
              <div>
                <CardTitle className="flex items-center gap-2">
                  <Users className="h-5 w-5" />
                  User Management
                </CardTitle>
                <p className="text-sm text-gray-600 mt-1">Manage all users and their details</p>
              </div>
              <div className="flex gap-2">
                <Button variant="outline" size="sm" onClick={handleRefresh}>
                  <RefreshCw className="h-4 w-4 mr-2" />
                  Refresh
                </Button>
                <Button variant="outline" size="sm" onClick={handleExport}>
                  <Download className="h-4 w-4 mr-2" />
                  Export CSV
                </Button>
              </div>
            </div>
          </CardHeader>
          <CardContent>
            {/* Filters */}
            <div className="flex flex-col sm:flex-row gap-4 mb-6">
              <div className="flex-1">
                <Input
                  placeholder="Search by name, email, username, or phone..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="w-full"
                />
              </div>
              <Select value={filterRole} onValueChange={setFilterRole}>
                <SelectTrigger className="w-40">
                  <SelectValue placeholder="Filter by role" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Roles</SelectItem>
                  <SelectItem value="customer">Customers</SelectItem>
                  <SelectItem value="shopkeeper">Shopkeepers</SelectItem>
                  <SelectItem value="delivery_partner">Delivery Partners</SelectItem>
                </SelectContent>
              </Select>
              <Select value={filterStatus} onValueChange={setFilterStatus}>
                <SelectTrigger className="w-40">
                  <SelectValue placeholder="Filter by status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Status</SelectItem>
                  <SelectItem value="active">Active</SelectItem>
                  <SelectItem value="pending">Pending</SelectItem>
                  <SelectItem value="rejected">Rejected</SelectItem>
                  <SelectItem value="suspended">Banned</SelectItem>
                </SelectContent>
              </Select>
            </div>

            {/* Stats Row */}
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
              <div className="bg-blue-50 p-3 rounded-lg">
                <p className="text-sm text-blue-600">Total Users</p>
                <p className="text-xl font-bold text-blue-900">{getFilteredUsers().length}</p>
              </div>
              <div className="bg-green-50 p-3 rounded-lg">
                <p className="text-sm text-green-600">Active</p>
                <p className="text-xl font-bold text-green-900">
                  {getFilteredUsers().filter(u => u.status === 'active').length}
                </p>
              </div>
              <div className="bg-yellow-50 p-3 rounded-lg">
                <p className="text-sm text-yellow-600">Pending</p>
                <p className="text-xl font-bold text-yellow-900">
                  {getFilteredUsers().filter(u => u.status === 'pending').length}
                </p>
              </div>
              <div className="bg-red-50 p-3 rounded-lg">
                <p className="text-sm text-red-600">Banned</p>
                <p className="text-xl font-bold text-red-900">
                  {getFilteredUsers().filter(u => u.status === 'suspended').length}
                </p>
              </div>
            </div>

            {/* Users Table */}
            <div className="overflow-x-auto">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>User</TableHead>
                    <TableHead>Contact</TableHead>
                    <TableHead>Role</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead>Location</TableHead>
                    <TableHead>Joined</TableHead>
                    <TableHead>Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {getFilteredUsers().map((user: User) => (
                    <TableRow key={user.id}>
                      <TableCell>
                        <div className="flex items-center gap-3">
                          <div className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center">
                            <Users className="h-4 w-4 text-blue-600" />
                          </div>
                          <div>
                            <p className="font-medium">{user.fullName}</p>
                            <p className="text-sm text-gray-500">@{user.username}</p>
                          </div>
                        </div>
                      </TableCell>
                      <TableCell>
                        <div className="space-y-1">
                          <div className="flex items-center gap-2">
                            <Mail className="h-3 w-3 text-gray-400" />
                            <span className="text-sm">{user.email}</span>
                          </div>
                          {user.phone && (
                            <div className="flex items-center gap-2">
                              <Phone className="h-3 w-3 text-gray-400" />
                              <span className="text-sm">{user.phone}</span>
                            </div>
                          )}
                        </div>
                      </TableCell>
                      <TableCell>
                        <Badge 
                          variant={
                            user.role === 'customer' ? 'default' :
                            user.role === 'shopkeeper' ? 'secondary' :
                            user.role === 'delivery_partner' ? 'outline' : 'default'
                          }
                        >
                          {user.role.replace('_', ' ')}
                        </Badge>
                      </TableCell>
                      <TableCell>{getStatusBadge(user.status)}</TableCell>
                      <TableCell>
                        <div className="space-y-1">
                          {user.city && (
                            <div className="flex items-center gap-2">
                              <MapPin className="h-3 w-3 text-gray-400" />
                              <span className="text-sm">{user.city}{user.state && `, ${user.state}`}</span>
                            </div>
                          )}
                          {user.address && (
                            <p className="text-xs text-gray-500 truncate max-w-32" title={user.address}>
                              {user.address}
                            </p>
                          )}
                        </div>
                      </TableCell>
                      <TableCell>
                        <div className="flex items-center gap-2">
                          <Calendar className="h-3 w-3 text-gray-400" />
                          <span className="text-sm">{formatDate(user.createdAt)}</span>
                        </div>
                      </TableCell>
                      <TableCell>
                        <div className="flex space-x-1">
                          <Button 
                            size="sm" 
                            variant="outline"
                            onClick={() => {
                              setSelectedUser(user);
                              setShowUserDetails(true);
                            }}
                          >
                            <Eye className="h-4 w-4" />
                          </Button>
                          {user.status === 'pending' && user.role === 'shopkeeper' && (
                            <>
                              <Button
                                size="sm"
                                onClick={() => approveMutation.mutate(user.id)}
                                disabled={approveMutation.isPending}
                                className="bg-green-600 hover:bg-green-700"
                              >
                                <CheckCircle className="h-4 w-4" />
                              </Button>
                              <Dialog>
                                <DialogTrigger asChild>
                                  <Button
                                    size="sm"
                                    variant="destructive"
                                    onClick={() => setSelectedUser(user)}
                                  >
                                    <XCircle className="h-4 w-4" />
                                  </Button>
                                </DialogTrigger>
                                <DialogContent>
                                  <DialogHeader>
                                    <DialogTitle>Reject User Application</DialogTitle>
                                    <DialogDescription>
                                      Are you sure you want to reject {user.fullName}'s application?
                                    </DialogDescription>
                                  </DialogHeader>
                                  <div className="space-y-4">
                                    <Textarea
                                      placeholder="Reason for rejection..."
                                      value={rejectReason}
                                      onChange={(e) => setRejectReason(e.target.value)}
                                    />
                                  </div>
                                  <DialogFooter>
                                    <Button
                                      variant="destructive"
                                      onClick={() => rejectMutation.mutate({ userId: user.id, reason: rejectReason })}
                                      disabled={rejectMutation.isPending}
                                    >
                                      Reject Application
                                    </Button>
                                  </DialogFooter>
                                </DialogContent>
                              </Dialog>
                            </>
                          )}
                          {user.status === 'active' && user.role !== 'customer' && (
                            <Button 
                              size="sm" 
                              variant="destructive"
                              onClick={() => banUserMutation.mutate(user.id)}
                              disabled={banUserMutation.isPending}
                            >
                              <Ban className="h-4 w-4" />
                            </Button>
                          )}
                          <Dialog>
                            <DialogTrigger asChild>
                              <Button 
                                size="sm" 
                                variant="destructive" 
                                className="bg-red-600 hover:bg-red-700"
                                title="Delete user and all associated data permanently"
                              >
                                <UserMinus className="h-4 w-4" />
                              </Button>
                            </DialogTrigger>
                            <DialogContent>
                              <DialogHeader>
                                <DialogTitle className="text-red-600">
                                  Delete User Completely
                                </DialogTitle>
                                <DialogDescription>
                                  This will permanently delete {user.fullName} and ALL associated data. This action cannot be undone.
                                </DialogDescription>
                              </DialogHeader>
                              <div className="space-y-4">
                                <div className="bg-red-50 border border-red-200 rounded-md p-4">
                                  <div className="flex items-center space-x-2">
                                    <AlertCircle className="h-5 w-5 text-red-600" />
                                    <h3 className="font-semibold text-red-800">Warning: This action cannot be undone!</h3>
                                  </div>
                                  <p className="text-red-700 mt-2 text-sm">
                                    This will permanently delete <strong>{user.fullName}</strong> and ALL associated data:
                                  </p>
                                  <ul className="text-red-700 text-sm mt-2 list-disc list-inside space-y-1">
                                    <li>User account and profile</li>
                                    <li>All stores and products (if shopkeeper)</li>
                                    <li>All orders and transaction history</li>
                                    <li>Cart items and wishlist</li>
                                    <li>Reviews and ratings</li>
                                    <li>Notifications and logs</li>
                                    <li>Delivery partner data (if applicable)</li>
                                  </ul>
                                </div>
                                <Textarea
                                  placeholder="Reason for deletion (optional)..."
                                  value={rejectReason}
                                  onChange={(e) => setRejectReason(e.target.value)}
                                />
                              </div>
                              <DialogFooter>
                                <Button
                                  variant="destructive"
                                  onClick={() => deleteUserCompletelyMutation.mutate({ 
                                    userId: user.id, 
                                    reason: rejectReason || "Deleted by admin" 
                                  })}
                                  disabled={deleteUserCompletelyMutation.isPending}
                                  className="bg-red-600 hover:bg-red-700"
                                >
                                  {deleteUserCompletelyMutation.isPending ? (
                                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                                  ) : (
                                    <UserMinus className="h-4 w-4 mr-2" />
                                  )}
                                  {deleteUserCompletelyMutation.isPending ? "Deleting..." : "Delete User Completely"}
                                </Button>
                              </DialogFooter>
                            </DialogContent>
                          </Dialog>
                        </div>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </div>

            {getFilteredUsers().length === 0 && (
              <div className="text-center py-8 text-gray-500">
                <Users className="h-12 w-12 mx-auto mb-4 opacity-50" />
                <p>No users found matching your criteria</p>
              </div>
            )}
          </CardContent>
        </Card>

        {/* User Details Dialog */}
        <Dialog open={showUserDetails} onOpenChange={setShowUserDetails}>
          <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle className="flex items-center gap-2">
                <Users className="h-5 w-5" />
                User Details
              </DialogTitle>
            </DialogHeader>
            {selectedUser && (
              <div className="space-y-6">
                {/* Basic Info */}
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <h4 className="font-medium text-sm text-gray-500 mb-1">Full Name</h4>
                    <p className="text-sm font-medium">{selectedUser.fullName}</p>
                  </div>
                  <div>
                    <h4 className="font-medium text-sm text-gray-500 mb-1">Username</h4>
                    <p className="text-sm">{selectedUser.username || 'Not set'}</p>
                  </div>
                  <div>
                    <h4 className="font-medium text-sm text-gray-500 mb-1">Email</h4>
                    <p className="text-sm">{selectedUser.email}</p>
                  </div>
                  <div>
                    <h4 className="font-medium text-sm text-gray-500 mb-1">Phone</h4>
                    <p className="text-sm">{selectedUser.phone || 'Not provided'}</p>
                  </div>
                  <div>
                    <h4 className="font-medium text-sm text-gray-500 mb-1">Role</h4>
                    <Badge variant="secondary">{selectedUser.role.replace('_', ' ')}</Badge>
                  </div>
                  <div>
                    <h4 className="font-medium text-sm text-gray-500 mb-1">Status</h4>
                    {getStatusBadge(selectedUser.status)}
                  </div>
                </div>

                {/* Location Info */}
                {(selectedUser.address || selectedUser.city || selectedUser.state) && (
                  <div>
                    <h4 className="font-medium text-sm text-gray-500 mb-2">Location Information</h4>
                    <div className="bg-gray-50 p-3 rounded-lg space-y-2">
                      {selectedUser.address && (
                        <div>
                          <span className="text-xs font-medium text-gray-500">Address:</span>
                          <p className="text-sm">{selectedUser.address}</p>
                        </div>
                      )}
                      <div className="grid grid-cols-2 gap-4">
                        {selectedUser.city && (
                          <div>
                            <span className="text-xs font-medium text-gray-500">City:</span>
                            <p className="text-sm">{selectedUser.city}</p>
                          </div>
                        )}
                        {selectedUser.state && (
                          <div>
                            <span className="text-xs font-medium text-gray-500">State:</span>
                            <p className="text-sm">{selectedUser.state}</p>
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                )}

                {/* Account Info */}
                <div>
                  <h4 className="font-medium text-sm text-gray-500 mb-2">Account Information</h4>
                  <div className="bg-gray-50 p-3 rounded-lg space-y-2">
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <span className="text-xs font-medium text-gray-500">User ID:</span>
                        <p className="text-sm">#{selectedUser.id}</p>
                      </div>
                      <div>
                        <span className="text-xs font-medium text-gray-500">Registration Date:</span>
                        <p className="text-sm">{formatDate(selectedUser.createdAt)}</p>
                      </div>
                      {selectedUser.approvalDate && (
                        <div>
                          <span className="text-xs font-medium text-gray-500">Approval Date:</span>
                          <p className="text-sm">{formatDate(selectedUser.approvalDate)}</p>
                        </div>
                      )}
                      {selectedUser.approvedBy && (
                        <div>
                          <span className="text-xs font-medium text-gray-500">Approved By:</span>
                          <p className="text-sm">Admin #{selectedUser.approvedBy}</p>
                        </div>
                      )}
                    </div>
                    {selectedUser.rejectionReason && (
                      <div>
                        <span className="text-xs font-medium text-gray-500">Rejection Reason:</span>
                        <p className="text-sm text-red-600">{selectedUser.rejectionReason}</p>
                      </div>
                    )}
                  </div>
                </div>

                {/* Actions */}
                <div className="flex gap-2 pt-4 border-t">
                  {selectedUser.status === 'pending' && selectedUser.role === 'shopkeeper' && (
                    <>
                      <Button
                        onClick={() => {
                          approveMutation.mutate(selectedUser.id);
                          setShowUserDetails(false);
                        }}
                        disabled={approveMutation.isPending}
                        className="bg-green-600 hover:bg-green-700"
                      >
                        <CheckCircle className="h-4 w-4 mr-2" />
                        Approve User
                      </Button>
                      <Button
                        variant="destructive"
                        onClick={() => {
                          setShowUserDetails(false);
                          // Open reject dialog
                        }}
                      >
                        <XCircle className="h-4 w-4 mr-2" />
                        Reject User
                      </Button>
                    </>
                  )}
                  {selectedUser.status === 'active' && selectedUser.role !== 'customer' && (
                    <Button
                      variant="destructive"
                      onClick={() => {
                        banUserMutation.mutate(selectedUser.id);
                        setShowUserDetails(false);
                      }}
                      disabled={banUserMutation.isPending}
                    >
                      <Ban className="h-4 w-4 mr-2" />
                      Ban User
                    </Button>
                  )}
                </div>
              </div>
            )}
          </DialogContent>
        </Dialog>
      </div>
    </div>
  );
}

client/src/pages/AdminDeliveryPartners.t.content:
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Truck, Clock, CheckCircle, XCircle, Eye, Phone, CreditCard } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useState } from "react";

interface DeliveryPartner {
  id: number;
  userId: number;
  vehicleType: string;
  vehicleNumber: string;
  drivingLicense: string;
  idProofType: string;
  idProofNumber: string;
  deliveryAreas: string[];
  emergencyContact: string;
  bankAccountNumber: string;
  ifscCode: string;
  status: string;
  isAvailable: boolean;
  currentLocation: string | null;
  totalDeliveries: number;
  totalEarnings: string;
  rating: number | null;
  approvedBy: number | null;
  rejectionReason: string | null;
  createdAt: string;
  approvalDate: string | null;
}

export default function AdminDeliveryPartners() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedPartner, setSelectedPartner] = useState<DeliveryPartner | null>(null);
  const [rejectionReason, setRejectionReason] = useState("");
  const [showRejectDialog, setShowRejectDialog] = useState(false);

  const { data: allPartners = [], isLoading: allLoading } = useQuery({
    queryKey: ['/api/delivery-partners'],
  });

  const { data: pendingPartners = [], isLoading: pendingLoading } = useQuery({
    queryKey: ['/api/delivery-partners/pending'],
  });

  const approvePartner = useMutation({
    mutationFn: async (partnerId: number) => {
      // Get the admin user ID from the admin authentication system
      const adminResponse = await fetch('/api/admin/current');
      const adminData = await adminResponse.json();
      const adminId = adminData?.id || 1; // Fallback to ID 1 if admin not found

      return apiRequest(`/api/delivery-partners/${partnerId}/approve`, {
        method: 'POST',
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ adminId }),
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/delivery-partners'] });
      queryClient.invalidateQueries({ queryKey: ['/api/delivery-partners/pending'] });
      toast({
        title: "Partner Approved",
        description: "Delivery partner has been approved successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to approve delivery partner.",
        variant: "destructive",
      });
    },
  });

  const rejectPartner = useMutation({
    mutationFn: async ({ partnerId, reason }: { partnerId: number; reason: string }) => {
      // Get the admin user ID from the admin authentication system
      const adminResponse = await fetch('/api/admin/current');
      const adminData = await adminResponse.json();
      const adminId = adminData?.id || 1; // Fallback to ID 1 if admin not found

      return apiRequest(`/api/delivery-partners/${partnerId}/reject`, {
        method: 'POST',
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ adminId, reason }),
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/delivery-partners'] });
      queryClient.invalidateQueries({ queryKey: ['/api/delivery-partners/pending'] });
      setShowRejectDialog(false);
      setRejectionReason("");
      setSelectedPartner(null);
      toast({
        title: "Partner Rejected",
        description: "Delivery partner application has been rejected.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to reject delivery partner.",
        variant: "destructive",
      });
    },
  });

  const handleReject = (partner: DeliveryPartner) => {
    setSelectedPartner(partner);
    setShowRejectDialog(true);
  };

  const confirmReject = () => {
    if (selectedPartner && rejectionReason.trim()) {
      rejectPartner.mutate({ partnerId: selectedPartner.id, reason: rejectionReason });
    }
  };

  const approvedPartners = allPartners.filter((p: DeliveryPartner) => p.status === 'approved');
  const rejectedPartners = allPartners.filter((p: DeliveryPartner) => p.status === 'rejected');

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'pending':
        return <Badge variant="secondary"><Clock className="h-3 w-3 mr-1" />Pending</Badge>;
      case 'approved':
        return <Badge variant="default"><CheckCircle className="h-3 w-3 mr-1" />Approved</Badge>;
      case 'rejected':
        return <Badge variant="destructive"><XCircle className="h-3 w-3 mr-1" />Rejected</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  const PartnerCard = ({ partner }: { partner: DeliveryPartner }) => (
    <Card key={partner.id}>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="text-lg">Partner #{partner.id}</CardTitle>
          {getStatusBadge(partner.status)}
        </div>
        <CardDescription>
          Applied on {new Date(partner.createdAt).toLocaleDateString()}
        </CardDescription>
      </CardHeader>
      <CardContent>
        <div className="space-y-3">
          <div className="grid grid-cols-2 gap-4 text-sm">
            <div>
              <strong>Vehicle:</strong> {partner.vehicleType}
            </div>
            <div>
              <strong>Vehicle Number:</strong> {partner.vehicleNumber}
            </div>
            <div>
              <strong>License:</strong> {partner.drivingLicense}
            </div>
            <div>
              <strong>ID Proof:</strong> {partner.idProofType}
            </div>
            <div className="col-span-2">
              <strong>Delivery Areas:</strong> {partner.deliveryAreas.join(', ')}
            </div>
          </div>

          <div className="flex items-center gap-2 pt-3">
            <Dialog>
              <DialogTrigger asChild>
                <Button variant="outline" size="sm">
                  <Eye className="h-4 w-4 mr-1" />
                  View Details
                </Button>
              </DialogTrigger>
              <DialogContent className="max-w-2xl">
                <DialogHeader>
                  <DialogTitle>Delivery Partner Details</DialogTitle>
                  <DialogDescription>
                    Complete information for Partner #{partner.id}
                  </DialogDescription>
                </DialogHeader>
                <div className="grid grid-cols-2 gap-4 py-4">
                  <div>
                    <Label className="text-sm font-medium">Vehicle Type</Label>
                    <p className="text-sm text-muted-foreground">{partner.vehicleType}</p>
                  </div>
                  <div>
                    <Label className="text-sm font-medium">Vehicle Number</Label>
                    <p className="text-sm text-muted-foreground">{partner.vehicleNumber}</p>
                  </div>
                  <div>
                    <Label className="text-sm font-medium">Driving License</Label>
                    <p className="text-sm text-muted-foreground">{partner.drivingLicense}</p>
                  </div>
                  <div>
                    <Label className="text-sm font-medium">ID Proof Type</Label>
                    <p className="text-sm text-muted-foreground">{partner.idProofType}</p>
                  </div>
                  <div>
                    <Label className="text-sm font-medium">ID Proof Number</Label>
                    <p className="text-sm text-muted-foreground">{partner.idProofNumber}</p>
                  </div>
                  <div>
                    <Label className="text-sm font-medium">Emergency Contact</Label>
                    <p className="text-sm text-muted-foreground flex items-center">
                      <Phone className="h-3 w-3 mr-1" />
                      {partner.emergencyContact}
                    </p>
                  </div>
                  <div>
                    <Label className="text-sm font-medium">Bank Account</Label>
                    <p className="text-sm text-muted-foreground flex items-center">
                      <CreditCard className="h-3 w-3 mr-1" />
                      {partner.bankAccountNumber}
                    </p>
                  </div>
                  <div>
                    <Label className="text-sm font-medium">IFSC Code</Label>
                    <p className="text-sm text-muted-foreground">{partner.ifscCode}</p>
                  </div>
                  <div className="col-span-2">
                    <Label className="text-sm font-medium">Delivery Areas</Label>
                    <p className="text-sm text-muted-foreground">{partner.deliveryAreas.join(', ')}</p>
                  </div>
                  {partner.status === 'approved' && (
                    <>
                      <div>
                        <Label className="text-sm font-medium">Total Deliveries</Label>
                        <p className="text-sm text-muted-foreground">{partner.totalDeliveries}</p>
                      </div>
                      <div>
                        <Label className="text-sm font-medium">Total Earnings</Label>
                        <p className="text-sm text-muted-foreground">₹{partner.totalEarnings}</p>
                      </div>
                      <div>
                        <Label className="text-sm font-medium">Rating</Label>
                        <p className="text-sm text-muted-foreground">
                          {partner.rating ? `${partner.rating.toFixed(1)}★` : "No ratings yet"}
                        </p>
                      </div>
                      <div>
                        <Label className="text-sm font-medium">Availability</Label>
                        <Badge variant={partner.isAvailable ? "default" : "secondary"}>
                          {partner.isAvailable ? "Available" : "Offline"}
                        </Badge>
                      </div>
                    </>
                  )}
                  {partner.rejectionReason && (
                    <div className="col-span-2">
                      <Label className="text-sm font-medium">Rejection Reason</Label>
                      <p className="text-sm text-muted-foreground">{partner.rejectionReason}</p>
                    </div>
                  )}
                </div>
              </DialogContent>
            </Dialog>

            {partner.status === 'pending' && (
              <>
                <Button
                  size="sm"
                  onClick={() => approvePartner.mutate(partner.id)}
                  disabled={approvePartner.isPending}
                >
                  <CheckCircle className="h-4 w-4 mr-1" />
                  Approve
                </Button>
                <Button
                  size="sm"
                  variant="destructive"
                  onClick={() => handleReject(partner)}
                  disabled={rejectPartner.isPending}
                >
                  <XCircle className="h-4 w-4 mr-1" />
                  Reject
                </Button>
              </>
            )}
          </div>
        </div>
      </CardContent>
    </Card>
  );

  return (
    <div className="container mx-auto p-6">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h1 className="text-3xl font-bold">Delivery Partner Management</h1>
          <p className="text-muted-foreground">Manage delivery partner applications and accounts</p>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-6">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Partners</CardTitle>
            <Truck className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{allPartners.length}</div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Pending Applications</CardTitle>
            <Clock className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{pendingPartners.length}</div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Approved Partners</CardTitle>
            <CheckCircle className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{approvedPartners.length}</div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Active Partners</CardTitle>
            <CheckCircle className="h-4 w-4 text-green-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">
              {approvedPartners.filter((p: DeliveryPartner) => p.isAvailable).length}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Partner Management Tabs */}
      <Tabs defaultValue="pending" className="w-full">
        <TabsList>
          <TabsTrigger value="pending">
            Pending Applications ({pendingPartners.length})
          </TabsTrigger>
          <TabsTrigger value="approved">
            Approved Partners ({approvedPartners.length})
          </TabsTrigger>
          <TabsTrigger value="rejected">
            Rejected Applications ({rejectedPartners.length})
          </TabsTrigger>
          <TabsTrigger value="all">
            All Partners ({allPartners.length})
          </TabsTrigger>
        </TabsList>

        <TabsContent value="pending" className="space-y-4">
          {pendingLoading ? (
            <div className="text-center py-8">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
              <p>Loading pending applications...</p>
            </div>
          ) : pendingPartners.length === 0 ? (
            <Card>
              <CardContent className="p-6 text-center">
                <Clock className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                <p className="text-muted-foreground">No pending applications</p>
              </CardContent>
            </Card>
          ) : (
            pendingPartners.map((partner: DeliveryPartner) => (
              <PartnerCard key={partner.id} partner={partner} />
            ))
          )}
        </TabsContent>

        <TabsContent value="approved" className="space-y-4">
          {approvedPartners.length === 0 ? (
            <Card>
              <CardContent className="p-6 text-center">
                <CheckCircle className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                <p className="text-muted-foreground">No approved partners</p>
              </CardContent>
            </Card>
          ) : (
            approvedPartners.map((partner: DeliveryPartner) => (
              <PartnerCard key={partner.id} partner={partner} />
            ))
          )}
        </TabsContent>

        <TabsContent value="rejected" className="space-y-4">
          {rejectedPartners.length === 0 ? (
            <Card>
              <CardContent className="p-6 text-center">
                <XCircle className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                <p className="text-muted-foreground">No rejected applications</p>
              </CardContent>
            </Card>
          ) : (
            rejectedPartners.map((partner: DeliveryPartner) => (
              <PartnerCard key={partner.id} partner={partner} />
            ))
          )}
        </TabsContent>

        <TabsContent value="all" className="space-y-4">
          {allLoading ? (
            <div className="text-center py-8">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
              <p>Loading all partners...</p>
            </div>
          ) : (
            allPartners.map((partner: DeliveryPartner) => (
              <PartnerCard key={partner.id} partner={partner} />
            ))
          )}
        </TabsContent>
      </Tabs>

      {/* Rejection Dialog */}
      <Dialog open={showRejectDialog} onOpenChange={setShowRejectDialog}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Reject Application</DialogTitle>
            <DialogDescription>
              Please provide a reason for rejecting this delivery partner application.
            </DialogDescription>
          </DialogHeader>
          <div className="py-4">
            <Label htmlFor="rejection-reason">Rejection Reason</Label>
            <Textarea
              id="rejection-reason"
              placeholder="Enter the reason for rejection..."
              value={rejectionReason}
              onChange={(e) => setRejectionReason(e.target.value)}
              className="mt-2"
            />
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => {
                setShowRejectDialog(false);
                setRejectionReason("");
                setSelectedPartner(null);
              }}
            >
              Cancel
            </Button>
            <Button
              variant="destructive"
              onClick={confirmReject}
              disabled={!rejectionReason.trim() || rejectPartner.isPending}
            >
              {rejectPartner.isPending ? "Rejecting..." : "Reject Application"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

