import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Link } from "wouter";
import { 
  User, 
  Package, 
  MapPin, 
  Heart, 
  Settings, 
  ShoppingBag,
  Clock,
  Truck,
  CheckCircle,
  XCircle,
  Bell,
  RefreshCw
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { useAuth } from "@/hooks/useAuth";
import { useWishlist } from "@/hooks/useWishlist";
import { useToast } from "@/hooks/use-toast";
import { apiPut } from "@/lib/api";
import NotificationCenter from "@/components/NotificationCenter";
import ReturnPolicy from "@/components/ReturnPolicy";
import { DeliveryTrackingMap } from "@/components/tracking/DeliveryTrackingMap";
import type { Order, OrderItem } from "@shared/schema";

const profileSchema = z.object({
  fullName: z.string().min(2, "Full name must be at least 2 characters"),
  email: z.string().email("Please enter a valid email address"),
  phone: z.string().min(10, "Phone number must be at least 10 digits"),
});

type ProfileForm = z.infer<typeof profileSchema>;

const addressSchema = z.object({
  title: z.string().min(1, "Address title is required"),
  address: z.string().min(10, "Full address is required"),
  city: z.string().min(1, "City is required"),
  postalCode: z.string().min(1, "Postal code is required"),
});

type AddressForm = z.infer<typeof addressSchema>;

export default function CustomerDashboard() {
  const [activeTab, setActiveTab] = useState("orders");
  const { user, logout } = useAuth();
  const { wishlistItems } = useWishlist();
  const { toast } = useToast();

  // Queries
  const { data: orders = [] } = useQuery<(Order & { items: OrderItem[] })[]>({
    queryKey: [`/api/orders/customer/${user?.id}`],
    enabled: !!user,
  });

  // Profile form
  const profileForm = useForm<ProfileForm>({
    resolver: zodResolver(profileSchema),
    defaultValues: {
      fullName: user?.fullName || "",
      email: user?.email || "",
      phone: user?.phone || "",
    },
  });

  // Address form
  const addressForm = useForm<AddressForm>({
    resolver: zodResolver(addressSchema),
    defaultValues: {
      title: "",
      address: "",
      city: "Siraha",
      postalCode: "",
    },
  });

  const handleProfileUpdate = async (data: ProfileForm) => {
    if (!user) return;

    try {
      await apiPut(`/api/users/${user.id}`, data);
      toast({
        title: "Profile updated",
        description: "Your profile has been updated successfully.",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to update profile. Please try again.",
        variant: "destructive",
      });
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case "delivered":
        return <CheckCircle className="h-4 w-4 text-green-600" />;
      case "shipped":
        return <Truck className="h-4 w-4 text-blue-600" />;
      case "processing":
        return <Clock className="h-4 w-4 text-yellow-600" />;
      case "cancelled":
        return <XCircle className="h-4 w-4 text-red-600" />;
      default:
        return <Package className="h-4 w-4 text-gray-600" />;
    }
  };

  const getStatusBadgeVariant = (status: string) => {
    switch (status) {
      case "delivered":
        return "default";
      case "shipped":
        return "secondary";
      case "processing":
        return "outline";
      case "cancelled":
        return "destructive";
      default:
        return "outline";
    }
  };

  if (!user || user.role !== "customer") {
    return (
      <div className="min-h-screen bg-muted flex items-center justify-center">
        <Card className="w-full max-w-md">
          <CardContent className="pt-6 text-center">
            <h2 className="text-xl font-semibold mb-2">Access Denied</h2>
            <p className="text-muted-foreground">This page is only accessible to customers.</p>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-muted">
      <div className="max-w-6xl mx-auto px-4 py-8">
        <div className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-3xl font-bold text-foreground">My Account</h1>
            <p className="text-muted-foreground">Welcome back, {user.fullName}</p>
          </div>
          <Button variant="outline" onClick={logout}>
            Logout
          </Button>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
          {/* Sidebar */}
          <div className="lg:col-span-1">
            <Card>
              <CardContent className="p-6">
                <div className="text-center mb-6">
                  <div className="w-20 h-20 bg-primary/10 rounded-full flex items-center justify-center mx-auto mb-4">
                    <User className="h-10 w-10 text-primary" />
                  </div>
                  <h3 className="font-semibold text-foreground">{user.fullName}</h3>
                  <p className="text-sm text-muted-foreground">{user.email}</p>
                </div>

                <nav className="space-y-2">
                  <button
                    onClick={() => setActiveTab("orders")}
                    className={`w-full text-left px-3 py-2 rounded-lg transition-colors flex items-center space-x-3 ${
                      activeTab === "orders" 
                        ? "bg-primary text-primary-foreground" 
                        : "hover:bg-muted"
                    }`}
                  >
                    <ShoppingBag className="h-4 w-4" />
                    <span>Order History</span>
                  </button>

                  <button
                    onClick={() => setActiveTab("tracking")}
                    className={`w-full text-left px-3 py-2 rounded-lg transition-colors flex items-center space-x-3 ${
                      activeTab === "tracking" 
                        ? "bg-primary text-primary-foreground" 
                        : "hover:bg-muted"
                    }`}
                  >
                    <Truck className="h-4 w-4" />
                    <span>Live Tracking</span>
                  </button>

                  <button
                    onClick={() => setActiveTab("addresses")}
                    className={`w-full text-left px-3 py-2 rounded-lg transition-colors flex items-center space-x-3 ${
                      activeTab === "addresses" 
                        ? "bg-primary text-primary-foreground" 
                        : "hover:bg-muted"
                    }`}
                  >
                    <MapPin className="h-4 w-4" />
                    <span>Addresses</span>
                  </button>

                  <button
                    onClick={() => setActiveTab("wishlist")}
                    className={`w-full text-left px-3 py-2 rounded-lg transition-colors flex items-center space-x-3 ${
                      activeTab === "wishlist" 
                        ? "bg-primary text-primary-foreground" 
                        : "hover:bg-muted"
                    }`}
                  >
                    <Heart className="h-4 w-4" />
                    <span>Wishlist</span>
                  </button>

                  <button
                    onClick={() => setActiveTab("notifications")}
                    className={`w-full text-left px-3 py-2 rounded-lg transition-colors flex items-center space-x-3 ${
                      activeTab === "notifications" 
                        ? "bg-primary text-primary-foreground" 
                        : "hover:bg-muted"
                    }`}
                  >
                    <Bell className="h-4 w-4" />
                    <span>Notifications</span>
                  </button>

                  <button
                    onClick={() => setActiveTab("returns")}
                    className={`w-full text-left px-3 py-2 rounded-lg transition-colors flex items-center space-x-3 ${
                      activeTab === "returns" 
                        ? "bg-primary text-primary-foreground" 
                        : "hover:bg-muted"
                    }`}
                  >
                    <RefreshCw className="h-4 w-4" />
                    <span>Returns</span>
                  </button>

                  <button
                    onClick={() => setActiveTab("profile")}
                    className={`w-full text-left px-3 py-2 rounded-lg transition-colors flex items-center space-x-3 ${
                      activeTab === "profile" 
                        ? "bg-primary text-primary-foreground" 
                        : "hover:bg-muted"
                    }`}
                  >
                    <Settings className="h-4 w-4" />
                    <span>Profile Settings</span>
                  </button>
                </nav>
              </CardContent>
            </Card>
          </div>

          {/* Main Content */}
          <div className="lg:col-span-3">
            {/* Order History */}
            {activeTab === "orders" && (
              <Card>
                <CardHeader>
                  <CardTitle>Order History</CardTitle>
                </CardHeader>
                <CardContent>
                  {orders.length === 0 ? (
                    <div className="text-center py-12">
                      <ShoppingBag className="h-16 w-16 text-muted-foreground mx-auto mb-4" />
                      <h3 className="text-lg font-semibold mb-2">No orders yet</h3>
                      <p className="text-muted-foreground mb-4">
                        You haven't placed any orders yet. Start shopping to see your orders here.
                      </p>
                      <Button>Start Shopping</Button>
                    </div>
                  ) : (
                    <div className="space-y-6">
                      {orders.map((order) => (
                        <div key={order.id} className="border rounded-lg p-6">
                          <div className="flex items-start justify-between mb-4">
                            <div>
                              <h3 className="font-semibold text-lg">Order #{order.id}</h3>
                              <p className="text-sm text-muted-foreground">
                                Placed on {new Date(order.createdAt).toLocaleDateString('en-US', {
                                  year: 'numeric',
                                  month: 'long',
                                  day: 'numeric'
                                })}
                              </p>
                            </div>
                            <div className="text-right">
                              <div className="flex items-center space-x-2 mb-2">
                                {getStatusIcon(order.status)}
                                <Badge variant={getStatusBadgeVariant(order.status)}>
                                  {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                                </Badge>
                              </div>
                              <p className="font-semibold text-lg">
                                ₹{Number(order.totalAmount).toLocaleString()}
                              </p>
                            </div>
                          </div>

                          <Separator className="my-4" />

                          <div className="space-y-3">
                            {/* Order Items */}
                            <div>
                              <h4 className="font-medium mb-2">Order Items</h4>
                              <div className="space-y-2">
                                {order.items && order.items.length > 0 ? (
                                  order.items.map((item: any, index: number) => (
                                    <div key={index} className="flex justify-between items-center p-2 bg-muted rounded-md">
                                      <div className="flex-1">
                                        <p className="font-medium text-sm">
                                          {item.product?.name || `Product ID: ${item.productId}`}
                                        </p>
                                        <p className="text-xs text-muted-foreground">
                                          Quantity: {item.quantity} × ₹{parseFloat(item.price).toFixed(2)}
                                        </p>
                                      </div>
                                      <p className="font-medium text-sm">
                                        ₹{(parseFloat(item.price) * item.quantity).toFixed(2)}
                                      </p>
                                    </div>
                                  ))
                                ) : (
                                  <p className="text-sm text-muted-foreground">No items found</p>
                                )}
                              </div>
                            </div>

                            <div>
                              <h4 className="font-medium mb-2">Delivery Address</h4>
                              <p className="text-sm text-muted-foreground">{order.shippingAddress}</p>
                            </div>

                            <div>
                              <h4 className="font-medium mb-2">Payment Method</h4>
                              <p className="text-sm text-muted-foreground">
                                {order.paymentMethod === "cod" ? "Cash on Delivery" : order.paymentMethod.toUpperCase()}
                              </p>
                            </div>
                          </div>

                          <div className="flex justify-between items-center mt-6">
                            <Button variant="outline" size="sm">
                              View Details
                            </Button>
                            {order.status === "delivered" && (
                              <Button variant="outline" size="sm">
                                Reorder
                              </Button>
                            )}
                            {order.status !== "delivered" && order.status !== "cancelled" && (
                              <Link href={`/orders/${order.id}/tracking`}>
                                <Button variant="outline" size="sm">
                                  Track Order
                                </Button>
                              </Link>
                            )}
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </CardContent>
              </Card>
            )}

            {/* Addresses */}
            {activeTab === "addresses" && (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center justify-between">
                    <span>Saved Addresses</span>
                    <Button size="sm">Add New Address</Button>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    {/* Sample addresses */}
                    <div className="border rounded-lg p-4">
                      <div className="flex items-start justify-between">
                        <div>
                          <div className="flex items-center space-x-2 mb-2">
                            <h3 className="font-medium">Home</h3>
                            <Badge variant="secondary">Default</Badge>
                          </div>
                          <p className="text-sm text-muted-foreground">{user.fullName}</p>
                          <p className="text-sm text-muted-foreground">
                            Mahendra Chowk, Siraha-56600<br />
                            Nepal<br />
                            Phone: {user.phone || "+977-9841234567"}
                          </p>
                        </div>
                        <div className="flex space-x-2">
                          <Button variant="outline" size="sm">Edit</Button>
                          <Button variant="outline" size="sm">Delete</Button>
                        </div>
                      </div>
                    </div>

                    <div className="border rounded-lg p-4">
                      <div className="flex items-start justify-between">
                        <div>
                          <h3 className="font-medium mb-2">Office</h3>
                          <p className="text-sm text-muted-foreground">{user.fullName}</p>
                          <p className="text-sm text-muted-foreground">
                            District Office, Siraha-56600<br />
                            Nepal<br />
                            Phone: {user.phone || "+977-9841234567"}
                          </p>
                        </div>
                        <div className="flex space-x-2">
                          <Button variant="outline" size="sm">Edit</Button>
                          <Button variant="outline" size="sm">Delete</Button>
                        </div>
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            )}

            {/* Wishlist */}
            {activeTab === "wishlist" && (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center justify-between">
                    <span>My Wishlist</span>
                    <Badge variant="secondary">{wishlistItems.length} items</Badge>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  {wishlistItems.length === 0 ? (
                    <div className="text-center py-12">
                      <Heart className="h-16 w-16 text-muted-foreground mx-auto mb-4" />
                      <h3 className="text-lg font-semibold mb-2">Your wishlist is empty</h3>
                      <p className="text-muted-foreground mb-4">
                        Save items you love to your wishlist and shop them later.
                      </p>
                      <Button>Start Shopping</Button>
                    </div>
                  ) : (
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                      {wishlistItems.slice(0, 6).map((item) => (
                        <div key={item.id} className="border rounded-lg p-4 hover:shadow-md transition-shadow">
                          <img
                            src={item.product?.images?.[0] || "https://images.unsplash.com/photo-1560472354-b33ff0c44a43?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=300"}
                            alt={item.product?.name || "Product"}
                            className="w-full h-32 object-cover rounded mb-3"
                          />
                          <h4 className="font-medium text-sm mb-2 line-clamp-2">
                            {item.product?.name || "Product Name"}
                          </h4>
                          <p className="text-lg font-bold text-primary mb-3">
                            ₹{item.product?.price || "0"}
                          </p>
                          <div className="flex gap-2">
                            <Button size="sm" className="flex-1">
                              Add to Cart
                            </Button>
                            <Button variant="outline" size="sm">
                              <Heart className="h-4 w-4" />
                            </Button>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                  {wishlistItems.length > 6 && (
                    <div className="mt-6 text-center">
                      <Button variant="outline">View All Wishlist Items</Button>
                    </div>
                  )}
                </CardContent>
              </Card>
            )}

            {/* Live Delivery Tracking */}
            {activeTab === "tracking" && (
              <div className="space-y-6">
                {/* Order Tracking System */}
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center justify-between">
                      <div className="flex items-center space-x-2">
                        <Package className="h-5 w-5" />
                        <span>Order Tracking System</span>
                      </div>
                      <Badge variant="secondary">{orders.length} orders</Badge>
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    {orders.length === 0 ? (
                      <div className="text-center py-12">
                        <Package className="h-16 w-16 text-muted-foreground mx-auto mb-4" />
                        <h3 className="text-lg font-semibold mb-2">No orders found</h3>
                        <p className="text-muted-foreground mb-4">
                          You haven't placed any orders yet. Start shopping to see your order tracking here.
                        </p>
                        <Link href="/">
                          <Button>Start Shopping</Button>
                        </Link>
                      </div>
                    ) : (
                      <div className="space-y-4">
                        {orders.slice(0, 5).map((order) => (
                          <div key={order.id} className="border rounded-lg p-4 hover:shadow-md transition-shadow">
                            <div className="flex items-center justify-between mb-4">
                              <div>
                                <h4 className="font-semibold text-lg">Order #{order.id}</h4>
                                <p className="text-sm text-muted-foreground">
                                  Placed on {new Date(order.createdAt).toLocaleDateString()}
                                </p>
                              </div>
                              <div className="text-right">
                                <Badge className={`
                                  ${order.status === 'delivered' ? 'bg-green-500' : 
                                    order.status === 'pending' ? 'bg-yellow-500' : 
                                    'bg-blue-500'} text-white
                                `}>
                                  {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                                </Badge>
                                <p className="text-sm font-semibold mt-1">
                                  ₹{Number(order.totalAmount).toLocaleString()}
                                </p>
                              </div>
                            </div>

                            {/* Quick Status Display */}
                            <div className="flex items-center space-x-2 mb-4">
                              <CheckCircle className={`h-4 w-4 ${order.status !== 'pending' ? 'text-green-500' : 'text-gray-300'}`} />
                              <span className="text-sm">Order Placed</span>
                              <div className="h-px bg-gray-300 flex-1"></div>
                              <CheckCircle className={`h-4 w-4 ${order.status === 'delivered' ? 'text-green-500' : 'text-gray-300'}`} />
                              <span className="text-sm">Delivered</span>
                            </div>

                            <div className="flex gap-2">
                              <Link href={`/orders/${order.id}/tracking`}>
                                <Button variant="outline" size="sm" className="flex items-center gap-2">
                                  <MapPin className="h-4 w-4" />
                                  Track Order
                                </Button>
                              </Link>
                              {order.status !== 'delivered' && (
                                <Button variant="outline" size="sm" className="flex items-center gap-2">
                                  <Truck className="h-4 w-4" />
                                  Contact Support
                                </Button>
                              )}
                            </div>
                          </div>
                        ))}

                        {orders.length > 5 && (
                          <div className="text-center pt-4">
                            <Button variant="outline">View All Orders</Button>
                          </div>
                        )}
                      </div>
                    )}
                  </CardContent>
                </Card>

                {/* Live Delivery Tracking */}
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center space-x-2">
                      <Truck className="h-5 w-5" />
                      <span>Live Delivery Tracking</span>
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    {/* Show active deliveries */}
                    {(() => {
                      const activeOrders = orders.filter(order => 
                        order.status === 'shipped' || 
                        order.status === 'processing' || 
                        order.status === 'out for delivery' ||
                        order.status === 'picked_up' ||
                        order.status === 'en_route_delivery'
                      );

                      if (activeOrders.length === 0) {
                        return (
                          <div className="text-center py-12">
                            <Truck className="h-16 w-16 text-muted-foreground mx-auto mb-4" />
                            <h3 className="text-lg font-semibold mb-2">No active deliveries</h3>
                            <p className="text-muted-foreground mb-4">
                              You don't have any orders currently being delivered.
                            </p>
                            <Link href="/">
                              <Button>Start Shopping</Button>
                            </Link>
                          </div>
                        );
                      }

                      return (
                        <div className="space-y-6">
                          {activeOrders.map((order) => (
                            <div key={order.id} className="space-y-4">
                              <div className="flex items-center justify-between p-4 border rounded-lg bg-blue-50">
                                <div>
                                  <h4 className="font-medium">Order #{order.id}</h4>
                                  <p className="text-sm text-muted-foreground">
                                    Status: <Badge variant="secondary">{order.status}</Badge>
                                  </p>
                                  <p className="text-sm text-muted-foreground">
                                    Total: ₹{Number(order.totalAmount).toLocaleString()}
                                  </p>
                                </div>
                                <div className="text-right">
                                  <div className="flex items-center gap-2 mb-2">
                                    <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
                                    <span className="text-sm font-medium text-green-600">Live Tracking</span>
                                  </div>
                                  <p className="text-sm text-muted-foreground">
                                    Ordered on {new Date(order.createdAt).toLocaleDateString()}
                                  </p>
                                </div>
                              </div>

                              {/* Delivery Tracking Map for this order */}
                              <div className="h-96 border rounded-lg">
                                <DeliveryTrackingMap
                                  deliveryId={order.id}
                                  userType="customer"
                                  onStatusUpdate={(status) => {
                                    console.log(`Order ${order.id} status updated to: ${status}`);
                                  }}
                                />
                              </div>

                              <div className="flex gap-2">
                                <Link href={`/orders/${order.id}/tracking`}>
                                  <Button variant="outline" size="sm">
                                    View Full Timeline
                                  </Button>
                                </Link>
                                <Button variant="outline" size="sm">
                                  Contact Delivery Partner
                                </Button>
                                <Button variant="outline" size="sm">
                                  <RefreshCw className="h-4 w-4 mr-2" />
                                  Refresh
                                </Button>
                              </div>
                            </div>
                          ))}
                        </div>
                      );
                    })()}
                  </CardContent>
                </Card>
              </div>
            )}

            {/* Profile Settings */}
            {activeTab === "profile" && (
              <Card>
                <CardHeader>
                  <CardTitle>Profile Settings</CardTitle>
                </CardHeader>
                <CardContent>
                  <Form {...profileForm}>
                    <form onSubmit={profileForm.handleSubmit(handleProfileUpdate)} className="space-y-6">
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <FormField
                          control={profileForm.control}
                          name="fullName"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Full Name</FormLabel>
                              <FormControl>
                                <Input placeholder="Enter your full name" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={profileForm.control}
                          name="email"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Email Address</FormLabel>
                              <FormControl>
                                <Input type="email" placeholder="Enter your email" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={profileForm.control}
                          name="phone"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Phone Number</FormLabel>
                              <FormControl>
                                <Input type="tel" placeholder="Enter your phone number" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </div>

                      <Separator />

                      <div>
                        <h3 className="text-lg font-semibold mb-4">Account Information</h3>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
                          <div>
                            <p className="text-muted-foreground">Account Type:</p>
                            <p className="font-medium capitalize">{user.role}</p>
                          </div>
                          <div>
                            <p className="text-muted-foreground">Member Since:</p>
                            <p className="font-medium">
                              {new Date(user.createdAt).toLocaleDateString('en-US', {
                                year: 'numeric',
                                month: 'long',
                                day: 'numeric'
                              })}
                            </p>
                          </div>
                        </div>
                      </div>

                      <Button type="submit" className="btn-primary">
                        Update Profile
                      </Button>
                    </form>
                  </Form>
                </CardContent>
              </Card>
            )}

            {/* Notifications */}
            {activeTab === "notifications" && (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <Bell className="h-5 w-5" />
                    <span>Notifications</span>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <NotificationCenter />
                </CardContent>
              </Card>
            )}

            {/* Returns */}
            {activeTab === "returns" && (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <RefreshCw className="h-5 w-5" />
                    <span>Returns & Refunds</span>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <ReturnPolicy customerId={user.id} orderItems={orders.flatMap(order => order.items)} />
                </CardContent>
              </Card>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/DeleteAccount.tsx.content:
import { useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { apiDelete } from "@/lib/api";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Checkbox } from "@/components/ui/checkbox";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { useToast } from "@/hooks/use-toast";
import { AlertTriangle, Trash2, ArrowLeft } from "lucide-react";
import { Link } from "wouter";
import { useLocation } from "wouter";

export default function DeleteAccount() {
  const { user, logout } = useAuth();
  const [, setLocation] = useLocation();
  const { toast } = useToast();

  const [confirmText, setConfirmText] = useState("");
  const [confirmChecked, setConfirmChecked] = useState(false);
  const [reason, setReason] = useState("");

  const deleteAccountMutation = useMutation({
    mutationFn: async (data: { reason?: string }) => {
      return apiRequest(`/api/auth/delete-account?userId=${user?.id}`, {
        method: "DELETE",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });
    },
    onSuccess: () => {
      toast({
        title: "Account deleted successfully",
        description: "Your account and all associated data have been permanently deleted.",
      });
      logout();
      setLocation("/");
    },
    onError: (error: any) => {
      toast({
        title: "Failed to delete account",
        description: error.message || "Please try again later.",
        variant: "destructive",
      });
    },
  });

  if (!user) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center">
          <h1 className="text-2xl font-bold mb-4">Please log in to access this page</h1>
          <Link href="/login">
            <Button>Login</Button>
          </Link>
        </div>
      </div>
    );
  }

  const isConfirmationValid = confirmText === "DELETE" && confirmChecked;

  const handleDeleteAccount = () => {
    if (!isConfirmationValid) return;

    deleteAccountMutation.mutate({
      reason: reason.trim() || undefined,
    });
  };

  return (
    <div className="container mx-auto px-4 py-8 max-w-2xl">
      <div className="mb-6">
        <Link href="/account">
          <Button variant="ghost" className="mb-4">
            <ArrowLeft className="h-4 w-4 mr-2" />
            Back to Account
          </Button>
        </Link>
        <h1 className="text-3xl font-bold text-red-600">Delete Account</h1>
        <p className="text-muted-foreground mt-2">
          Permanently delete your account and all associated data
        </p>
      </div>

      <Card className="border-red-200">
        <CardHeader>
          <CardTitle className="text-red-600 flex items-center space-x-2">
            <AlertTriangle className="h-5 w-5" />
            <span>Warning: This action cannot be undone</span>
          </CardTitle>
          <CardDescription>
            Deleting your account will permanently remove all your data including:
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-6">
          <Alert className="border-red-200 bg-red-50">
            <AlertTriangle className="h-4 w-4" />
            <AlertDescription className="text-red-800">
              <ul className="list-disc list-inside space-y-1 mt-2">
                <li>Your profile information and account details</li>
                <li>Order history and purchase records</li>
                <li>Saved addresses and payment methods</li>
                <li>Wishlist items and shopping cart</li>
                {user.role === "shopkeeper" && (
                  <>
                    <li>Your store(s) and all product listings</li>
                    <li>Store analytics and sales data</li>
                    <li>Customer reviews and ratings</li>
                  </>
                )}
                {user.role === "delivery_partner" && (
                  <>
                    <li>Delivery history and earnings data</li>
                    <li>Performance ratings and statistics</li>
                  </>
                )}
                <li>All notifications and communications</li>
              </ul>
            </AlertDescription>
          </Alert>

          <div className="space-y-4">
            <div>
              <Label htmlFor="reason">Reason for deletion (optional)</Label>
              <Input
                id="reason"
                placeholder="Help us improve by sharing why you're leaving..."
                value={reason}
                onChange={(e) => setReason(e.target.value)}
                className="mt-1"
              />
            </div>

            <div className="space-y-4 p-4 border rounded-lg bg-gray-50">
              <div className="flex items-center space-x-2">
                <Checkbox
                  id="confirm-checkbox"
                  checked={confirmChecked}
                  onCheckedChange={(checked) => setConfirmChecked(checked as boolean)}
                />
                <Label htmlFor="confirm-checkbox" className="text-sm">
                  I understand that this action is permanent and cannot be reversed
                </Label>
              </div>

              <div>
                <Label htmlFor="confirm-text" className="text-sm">
                  Type "DELETE" to confirm account deletion
                </Label>
                <Input
                  id="confirm-text"
                  placeholder="Type DELETE here"
                  value={confirmText}
                  onChange={(e) => setConfirmText(e.target.value)}
                  className="mt-1"
                />
              </div>
            </div>

            <div className="flex space-x-3 pt-4">
              <Link href="/account">
                <Button variant="outline" className="flex-1">
                  Cancel
                </Button>
              </Link>
              <Button
                variant="destructive"
                onClick={handleDeleteAccount}
                disabled={!isConfirmationValid || deleteAccountMutation.isPending}
                className="flex-1"
              >
                {deleteAccountMutation.isPending ? (
                  "Deleting..."
                ) : (
                  <>
                    <Trash2 className="h-4 w-4 mr-2" />
                    Delete Account
                  </>
                )}
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      <div className="mt-6 text-center text-sm text-muted-foreground">
        <p>
          Need help? Contact our support team at{" "}
          <a href="mailto:sirahabazzar@gmail.com" className="text-blue-600 hover:underline">
            sirahabazzar@gmail.com
          </a>
        </p>
      </div>
    </div>
  );
}

client/src/pages/DeliveryMap.tsx.content:
import { useState, useEffect } from "react";
import { useParams } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { 
  MapPin, 
  Navigation, 
  Phone, 
  Clock,
  Package,
  Truck,
  CheckCircle,
  ArrowLeft
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useUser } from "@/hooks/use-user";

interface DeliveryDetails {
  id: number;
  orderId: number;
  status: string;
  customerName: string;
  customerPhone: string;
  pickupAddress: string;
  deliveryAddress: string;
  deliveryFee: string;
  estimatedDistance: number;
  estimatedTime: number;
  specialInstructions?: string;
  currentLocation?: string;
  assignedAt: string;
  pickedUpAt?: string;
  deliveredAt?: string;
}

export default function DeliveryMap() {
  const params = useParams();
  const deliveryId = parseInt(params.id || "0");
  const { user } = useUser();
  const { toast } = useToast();
  const [currentLocation, setCurrentLocation] = useState<string>("");

  const { data: delivery, isLoading } = useQuery({
    queryKey: ['/api/deliveries', deliveryId],
    queryFn: async () => {
      const response = await fetch(`/api/deliveries/${deliveryId}`);
      if (!response.ok) throw new Error('Failed to fetch delivery details');
      return response.json();
    },
    enabled: !!deliveryId,
    refetchInterval: 30000, // Refresh every 30 seconds
  });

  useEffect(() => {
    // Get current location
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setCurrentLocation(`${latitude}, ${longitude}`);
        },
        (error) => {
          console.error("Error getting location:", error);
          toast({
            title: "Location Error",
            description: "Could not get your current location",
            variant: "destructive"
          });
        }
      );
    }
  }, [toast]);

  const updateLocation = async () => {
    if (!currentLocation || !delivery) return;

    try {
      const response = await fetch(`/api/deliveries/${delivery.id}/location`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ location: currentLocation }),
      });

      if (response.ok) {
        toast({
          title: "Location Updated",
          description: "Your location has been shared with the customer.",
        });
      }
    } catch (error) {
      console.error("Error updating location:", error);
    }
  };

  const getStatusProgress = (status: string) => {
    switch (status) {
      case 'assigned': return 25;
      case 'picked_up': return 50;
      case 'in_transit': return 75;
      case 'delivered': return 100;
      default: return 0;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'assigned': return 'bg-yellow-500';
      case 'picked_up': return 'bg-blue-500';
      case 'in_transit': return 'bg-orange-500';
      case 'delivered': return 'bg-green-500';
      default: return 'bg-gray-500';
    }
  };

  const openNavigationApp = (address: string) => {
    const encodedAddress = encodeURIComponent(address);

    // Try to detect if user is on mobile
    const isMobile = /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);

    if (isMobile) {
      // Try Google Maps app first, fallback to web
      window.open(`geo:0,0?q=${encodedAddress}`, '_blank');
    } else {
      // Desktop - open in new tab with our internal map or fallback
      window.open(`https://www.google.com/maps/search/?api=1&query=${encodedAddress}`, '_blank');
    }
  };

  if (isLoading) {
    return (
      <div className="container mx-auto p-6">
        <div className="flex items-center justify-center min-h-screen">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
            <p>Loading delivery details...</p>
          </div>
        </div>
      </div>
    );
  }

  if (!delivery) {
    return (
      <div className="container mx-auto p-6">
        <Card>
          <CardContent className="p-6 text-center">
            <Package className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
            <p className="text-muted-foreground">Delivery not found</p>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 max-w-4xl">
      <div className="flex items-center gap-4 mb-6">
        <Button
          variant="outline"
          size="icon"
          onClick={() => window.history.back()}
        >
          <ArrowLeft className="h-4 w-4" />
        </Button>
        <div>
          <h1 className="text-3xl font-bold">Delivery Route</h1>
          <p className="text-muted-foreground">Order #{delivery.orderId}</p>
        </div>
      </div>

      {/* Status Progress */}
      <Card className="mb-6">
        <CardHeader>
          <CardTitle className="flex items-center justify-between">
            <span>Delivery Status</span>
            <Badge variant="outline" className={getStatusColor(delivery.status)}>
              {delivery.status.replace('_', ' ').toUpperCase()}
            </Badge>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <Progress value={getStatusProgress(delivery.status)} className="mb-4" />
          <div className="grid grid-cols-4 gap-2 text-xs">
            <div className="text-center">
              <div className="font-medium">Assigned</div>
            </div>
            <div className="text-center">
              <div className="font-medium">Picked Up</div>
            </div>
            <div className="text-center">
              <div className="font-medium">In Transit</div>
            </div>
            <div className="text-center">
              <div className="font-medium">Delivered</div>
            </div>
          </div>
        </CardContent>
      </Card>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Route Information */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <MapPin className="h-5 w-5" />
              Route Details
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-3">
              <div className="flex items-start gap-3 p-3 bg-green-50 dark:bg-green-900/20 rounded-lg">
                <div className="w-3 h-3 bg-green-500 rounded-full mt-2"></div>
                <div className="flex-1">
                  <p className="font-medium text-green-800 dark:text-green-400">Pickup Location</p>
                  <p className="text-sm text-green-600 dark:text-green-300">{delivery.pickupAddress}</p>
                  <Button
                    variant="outline"
                    size="sm"
                    className="mt-2"
                    onClick={() => openNavigationApp(delivery.pickupAddress)}
                  >
                    <Navigation className="h-4 w-4 mr-2" />
                    Navigate
                  </Button>
                </div>
              </div>

              <div className="flex items-center justify-center">
                <div className="w-0.5 h-8 bg-border"></div>
              </div>

              <div className="flex items-start gap-3 p-3 bg-red-50 dark:bg-red-900/20 rounded-lg">
                <div className="w-3 h-3 bg-red-500 rounded-full mt-2"></div>
                <div className="flex-1">
                  <p className="font-medium text-red-800 dark:text-red-400">Delivery Location</p>
                  <p className="text-sm text-red-600 dark:text-red-300">{delivery.deliveryAddress}</p>
                  <Button
                    variant="outline"
                    size="sm"
                    className="mt-2"
                    onClick={() => openNavigationApp(delivery.deliveryAddress)}
                  >
                    <Navigation className="h-4 w-4 mr-2" />
                    Navigate
                  </Button>
                </div>
              </div>
            </div>

            <div className="grid grid-cols-2 gap-4 pt-4 border-t">
              <div className="text-center">
                <div className="text-2xl font-bold text-primary">{delivery.estimatedDistance}km</div>
                <div className="text-sm text-muted-foreground">Distance</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-primary">₹{delivery.deliveryFee}</div>
                <div className="text-sm text-muted-foreground">Delivery Fee</div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Customer Information */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Phone className="h-5 w-5" />
              Customer Information
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <p className="font-medium">{delivery.customerName}</p>
              <p className="text-sm text-muted-foreground">{delivery.customerPhone}</p>
            </div>

            {delivery.specialInstructions && (
              <div>
                <p className="font-medium mb-1">Special Instructions:</p>
                <p className="text-sm text-muted-foreground bg-muted p-3 rounded-lg">
                  {delivery.specialInstructions}
                </p>
              </div>
            )}

            <div className="flex gap-2">
              <Button
                variant="outline"
                className="flex-1"
                onClick={() => window.open(`tel:${delivery.customerPhone}`, '_self')}
              >
                <Phone className="h-4 w-4 mr-2" />
                Call Customer
              </Button>
            </div>

            {currentLocation && (
              <Button
                onClick={updateLocation}
                className="w-full"
                variant="outline"
              >
                <MapPin className="h-4 w-4 mr-2" />
                Share My Location
              </Button>
            )}
          </CardContent>
        </Card>

        {/* Delivery Timeline */}
        <Card className="lg:col-span-2">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Clock className="h-5 w-5" />
              Delivery Timeline
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {delivery.assignedAt && (
                <div className="flex items-center gap-3">
                  <div className="w-3 h-3 bg-blue-500 rounded-full"></div>
                  <div>
                    <p className="font-medium">Delivery Assigned</p>
                    <p className="text-sm text-muted-foreground">
                      {new Date(delivery.assignedAt).toLocaleString()}
                    </p>
                  </div>
                </div>
              )}

              {delivery.pickedUpAt && (
                <div className="flex items-center gap-3">
                  <div className="w-3 h-3 bg-orange-500 rounded-full"></div>
                  <div>
                    <p className="font-medium">Order Picked Up</p>
                    <p className="text-sm text-muted-foreground">
                      {new Date(delivery.pickedUpAt).toLocaleString()}
                    </p>
                  </div>
                </div>
              )}

              {delivery.deliveredAt && (
                <div className="flex items-center gap-3">
                  <div className="w-3 h-3 bg-green-500 rounded-full"></div>
                  <div>
                    <p className="font-medium">Order Delivered</p>
                    <p className="text-sm text-muted-foreground">
                      {new Date(delivery.deliveredAt).toLocaleString()}
                    </p>
                  </div>
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

