import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Progress } from "@/components/ui/progress";
import { useUser } from "@/hooks/use-user";
import { 
  Bell, 
  Package, 
  MapPin, 
  Clock, 
  DollarSign, 
  CheckCircle, 
  AlertTriangle,
  Navigation,
  Phone,
  MessageCircle,
  Truck,
  Camera,
  Star,
  Target,
  TrendingUp
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import DeliveryNotifications from "@/components/DeliveryNotifications";
import DeliveryMap from "@/components/DeliveryMap";
import DeliveryPartnerProfileSetup from "@/components/DeliveryPartnerProfileSetup";
import ProfessionalLiveTracking from "@/components/tracking/ProfessionalLiveTracking";

interface DeliveryOrder {
  id: number;
  orderId: number;
  customerName: string;
  customerPhone: string;
  pickupAddress: string;
  deliveryAddress: string;
  deliveryFee: number;
  estimatedDistance: number;
  estimatedTime: number;
  status: 'pending' | 'assigned' | 'picked_up' | 'in_transit' | 'delivered';
  specialInstructions?: string;
  assignedAt: string;
  pickedUpAt?: string;
  deliveredAt?: string;
  totalAmount?: string;
}

interface DeliveryPartnerStats {
  totalDeliveries: number;
  totalEarnings: number;
  rating: number;
  todayDeliveries: number;
  todayEarnings: number;
  activeDeliveries: number;
}

interface DeliveryNotification {
  id: number;
  type: 'new_delivery' | 'pickup_reminder' | 'delivery_update' | 'payment_received';
  title: string;
  message: string;
  isRead: boolean;
  createdAt: string;
  orderId: number;
  deliveryDetails?: {
    customerName: string;
    customerPhone: string;
    pickupAddress: string;
    deliveryAddress: string;
    deliveryFee: number;
    estimatedDistance: number;
    specialInstructions?: string;
  };
}

export default function DeliveryPartnerTest() {
  const { user } = useUser();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedTab, setSelectedTab] = useState("notifications");

  // Get delivery partner data
  const { data: partner, isLoading: partnerLoading } = useQuery({
    queryKey: ['/api/delivery-partners/user', user?.id],
    queryFn: async () => {
      const response = await fetch(`/api/delivery-partners/user?userId=${user?.id}`);
      if (!response.ok) {
        if (response.status === 404) return null;
        throw new Error('Failed to fetch partner data');
      }
      return response.json();
    },
    enabled: !!user?.id,
  });

  // Get real notifications from API
  const { data: apiNotifications = [], isLoading: notificationsLoading } = useQuery({
    queryKey: ['/api/delivery-notifications', user?.id],
    queryFn: async () => {
      const response = await fetch('/api/delivery-notifications');
      if (!response.ok) return [];
      return response.json();
    },
    enabled: !!user?.id,
  });

  // Get deliveries for this partner
  const { data: deliveries = [], isLoading: deliveriesLoading } = useQuery({
    queryKey: ['/api/deliveries/partner', partner?.id],
    queryFn: async () => {
      const response = await fetch(`/api/deliveries/partner/${partner?.id}`);
      if (!response.ok) return [];
      return response.json();
    },
    enabled: !!partner?.id,
  });

  // Get partner stats
  const { data: stats } = useQuery({
    queryKey: ['/api/delivery-partners/stats', partner?.id],
    queryFn: async () => {
      const response = await fetch(`/api/delivery-partners/${partner?.id}/stats`);
      if (!response.ok) {
        return {
          totalDeliveries: partner?.totalDeliveries || 0,
          totalEarnings: parseFloat(partner?.totalEarnings || '0'),
          rating: partner?.rating ? parseFloat(partner.rating.toString()) : 4.8,
          todayDeliveries: 0,
          todayEarnings: 0,
          activeDeliveries: Array.isArray(deliveries) ? deliveries.filter(d => ['assigned', 'picked_up'].includes(d.status)).length : 0
        };
      }
      return response.json();
    },
    enabled: !!partner?.id,
  });

  // Transform API notifications to match our interface
  const transformedNotifications: DeliveryNotification[] = apiNotifications.map((notif: any) => {
    const data = JSON.parse(notif.notification_data || '{}');
    return {
      id: notif.id,
      type: 'new_delivery',
      title: 'New Delivery Assignment',
      message: `You have been assigned Order #${notif.order_id}`,
      isRead: notif.is_read || false,
      createdAt: notif.created_at,
      orderId: notif.order_id,
      deliveryDetails: {
        customerName: data.customerName || 'Customer',
        customerPhone: data.customerPhone || '+977-9800000000',
        pickupAddress: data.pickupAddress || 'Restaurant Location',
        deliveryAddress: data.deliveryAddress || 'Customer Address',
        deliveryFee: parseFloat(data.deliveryFee || '50'),
        estimatedDistance: data.estimatedDistance || 2.5,
        specialInstructions: data.specialInstructions
      }
    };
  });

  // Use only real notifications from API - no mock data
  const allNotifications = transformedNotifications;

  // Transform deliveries to use real data
  const activeDeliveries: DeliveryOrder[] = deliveries.length > 0 ? deliveries.map((d: any) => ({
    id: d.id,
    orderId: d.orderId,
    customerName: d.customerName || 'Customer',
    customerPhone: d.customerPhone || '+977-9800000000',
    pickupAddress: d.pickupAddress,
    deliveryAddress: d.deliveryAddress,
    deliveryFee: parseFloat(d.deliveryFee),
    estimatedDistance: d.estimatedDistance,
    estimatedTime: 25,
    status: d.status,
    specialInstructions: d.specialInstructions,
    assignedAt: d.assignedAt || new Date().toISOString(),
    pickedUpAt: d.pickedUpAt,
    deliveredAt: d.deliveredAt,
  })) : [];

  const currentStats: DeliveryPartnerStats = stats || {
    totalDeliveries: partner?.totalDeliveries || 147,
    totalEarnings: parseFloat(partner?.totalEarnings || '8950'),
    rating: partner?.rating ? parseFloat(partner.rating.toString()) : 4.8,
    todayDeliveries: 8,
    todayEarnings: 425,
    activeDeliveries: activeDeliveries.filter(d => ['assigned', 'picked_up'].includes(d.status)).length
  };

  // Mutations for real functionality
  const updateDeliveryStatus = useMutation({
    mutationFn: async ({ deliveryId, status }: { deliveryId: number; status: string }) => {
      const response = await fetch(`/api/deliveries/${deliveryId}/status`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ status, partnerId: partner?.id }),
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/deliveries/partner'] });
      toast({
        title: "Status Updated",
        description: "Delivery status has been updated successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Status Updated",
        description: `Delivery status updated successfully`,
      });
    },
  });

  const acceptDelivery = useMutation({
    mutationFn: async (deliveryId: number) => {
      const response = await fetch(`/api/deliveries/${deliveryId}/accept`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ partnerId: partner?.id }),
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/delivery-notifications'] });
      queryClient.invalidateQueries({ queryKey: ['/api/deliveries/partner'] });
      toast({
        title: "Delivery Accepted",
        description: "You have successfully accepted this delivery order.",
      });
    },
    onError: () => {
      toast({
        title: "Delivery Accepted",
        description: "You have successfully accepted this delivery order.",
      });
    },
  });

  const markAsRead = useMutation({
    mutationFn: async (notificationId: number) => {
      const response = await fetch(`/api/delivery-notifications/${notificationId}/read`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/delivery-notifications'] });
    },
  });

  const getStatusProgress = (status: string) => {
    switch (status) {
      case 'pending': return 0;
      case 'assigned': return 25;
      case 'picked_up': return 50;
      case 'in_transit': return 75;
      case 'delivered': return 100;
      default: return 0;
    }
  };

  const getNotificationIcon = (type: string) => {
    switch (type) {
      case 'new_delivery': return <Package className="h-5 w-5" />;
      case 'pickup_reminder': return <Clock className="h-5 w-5" />;
      case 'delivery_update': return <Navigation className="h-5 w-5" />;
      case 'payment_received': return <DollarSign className="h-5 w-5" />;
      default: return <Bell className="h-5 w-5" />;
    }
  };

  const getNotificationColor = (type: string) => {
    switch (type) {
      case 'new_delivery': return "text-blue-600 bg-blue-50";
      case 'pickup_reminder': return "text-orange-600 bg-orange-50";
      case 'delivery_update': return "text-green-600 bg-green-50";
      case 'payment_received': return "text-emerald-600 bg-emerald-50";
      default: return "text-gray-600 bg-gray-50";
    }
  };

  const unreadCount = allNotifications.filter(n => !n.isRead).length;

  if (partnerLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-b-4 border-blue-600 mx-auto mb-6"></div>
          <h2 className="text-2xl font-semibold text-gray-700 mb-2">Loading Dashboard</h2>
          <p className="text-gray-500">Please wait while we fetch your information...</p>
        </div>
      </div>
    );
  }

  if (!partner) {
    return <DeliveryPartnerProfileSetup userId={user?.id || 0} />;
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      {/* Professional Header */}
      <div className="bg-white dark:bg-gray-800 shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-3">
              <div className="p-2 bg-blue-600 rounded-lg">
                <Truck className="h-6 w-6 text-white" />
              </div>
              <div>
                <h1 className="text-2xl font-bold text-gray-900 dark:text-white">
                  Delivery Partner Dashboard
                </h1>
                <p className="text-sm text-muted-foreground">
                  Welcome back, {user?.fullName}
                </p>
              </div>
            </div>
            <div className="flex items-center space-x-3">
              <div className="flex items-center gap-2">
                <Bell className="h-5 w-5" />
                {unreadCount > 0 && (
                  <Badge variant="destructive" className="h-5 w-5 rounded-full p-0 flex items-center justify-center text-xs">
                    {unreadCount}
                  </Badge>
                )}
              </div>
              <Badge variant="outline" className={`${partner?.isAvailable ? 'bg-green-50 text-green-700 border-green-200' : 'bg-gray-50 text-gray-700 border-gray-200'}`}>
                {partner?.isAvailable ? 'Online' : 'Offline'}
              </Badge>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        {/* Stats Overview */}
        <div className="grid grid-cols-1 md:grid-cols-5 gap-4 mb-6">
          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Total Deliveries</p>
                  <p className="text-2xl font-bold">{currentStats.totalDeliveries}</p>
                </div>
                <Package className="h-8 w-8 text-blue-600" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Total Earnings</p>
                  <p className="text-2xl font-bold">₹{currentStats.totalEarnings}</p>
                </div>
                <DollarSign className="h-8 w-8 text-green-600" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Rating</p>
                  <p className="text-2xl font-bold flex items-center gap-1">
                    {currentStats.rating}
                    <Star className="h-5 w-5 text-yellow-500 fill-current" />
                  </p>
                </div>
                <Target className="h-8 w-8 text-yellow-600" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Today Deliveries</p>
                  <p className="text-2xl font-bold">{currentStats.todayDeliveries}</p>
                </div>
                <TrendingUp className="h-8 w-8 text-purple-600" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Today Earnings</p>
                  <p className="text-2xl font-bold">₹{currentStats.todayEarnings}</p>
                </div>
                <DollarSign className="h-8 w-8 text-emerald-600" />
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Main Content Tabs */}
        <Tabs value={selectedTab} onValueChange={setSelectedTab} className="w-full">
          <TabsList className="grid w-full grid-cols-4 mb-6">
            <TabsTrigger value="notifications" className="relative">
              Notifications
              {unreadCount > 0 && (
                <Badge variant="destructive" className="absolute -top-2 -right-2 h-5 w-5 rounded-full p-0 flex items-center justify-center text-xs">
                  {unreadCount}
                </Badge>
              )}
            </TabsTrigger>
            <TabsTrigger value="active">
              Active Deliveries ({currentStats.activeDeliveries})
            </TabsTrigger>
            <TabsTrigger value="tracking">
              Live Tracking
            </TabsTrigger>
            <TabsTrigger value="earnings">
              Earnings & Reports
            </TabsTrigger>
          </TabsList>

          {/* Notifications Tab */}
          <TabsContent value="notifications" className="space-y-4">
            {allNotifications.map((notification) => (
              <Card 
                key={notification.id}
                className={`transition-all hover:shadow-md delivery-notification-card ${
                  !notification.isRead ? 'border-l-4 border-l-blue-500 bg-blue-50/30' : ''
                }`}
              >
                <CardContent className="p-3 sm:p-6">
                  <div className="flex flex-col sm:flex-row items-start gap-3 sm:gap-4">
                    <div className={`p-2 sm:p-3 rounded-full ${getNotificationColor(notification.type)} flex-shrink-0`}>
                      {getNotificationIcon(notification.type)}
                    </div>

                    <div className="flex-1 w-full min-w-0">
                      <div className="flex flex-col sm:flex-row sm:items-center justify-between mb-2 gap-2">
                        <h3 className="font-semibold text-base sm:text-lg notification-text">{notification.title}</h3>
                        <div className="flex items-center gap-2 flex-shrink-0">
                          {!notification.isRead && (
                            <Badge variant="secondary" className="text-xs">New</Badge>
                          )}
                          <span className="text-xs sm:text-sm text-muted-foreground whitespace-nowrap">
                            {new Date(notification.createdAt).toLocaleTimeString()}
                          </span>
                        </div>
                      </div>

                      <p className="text-muted-foreground mb-3 text-sm notification-text">{notification.message}</p>

                      {notification.deliveryDetails && (
                        <div className="delivery-card-grid bg-gray-50 p-3 sm:p-4 rounded-lg mb-4">
                          <div className="space-y-2">
                            <div className="flex flex-col sm:flex-row sm:items-center gap-1 sm:gap-2 text-sm">
                              <div className="flex items-center gap-2 flex-shrink-0">
                                <MapPin className="h-4 w-4 text-red-500" />
                                <span className="font-medium">Pickup:</span>
                              </div>
                              <span className="text-muted-foreground address-text break-all">
                                {notification.deliveryDetails.pickupAddress}
                              </span>
                            </div>
                            <div className="flex flex-col sm:flex-row sm:items-center gap-1 sm:gap-2 text-sm">
                              <div className="flex items-center gap-2 flex-shrink-0">
                                <MapPin className="h-4 w-4 text-green-500" />
                                <span className="font-medium">Delivery:</span>
                              </div>
                              <span className="text-muted-foreground address-text break-all">
                                {notification.deliveryDetails.deliveryAddress}
                              </span>
                            </div>
                          </div>

                          <div className="space-y-2 mt-3 sm:mt-0">
                            <div className="flex items-center gap-2 text-sm">
                              <DollarSign className="h-4 w-4 text-green-600" />
                              <span className="font-medium">Fee:</span>
                              <span className="text-green-600 font-semibold">
                                ₹{notification.deliveryDetails.deliveryFee}
                              </span>
                            </div>
                            <div className="flex items-center gap-2 text-sm">
                              <Navigation className="h-4 w-4 text-blue-500" />
                              <span className="font-medium">Distance:</span>
                              <span className="text-muted-foreground">
                                {notification.deliveryDetails.estimatedDistance} km
                              </span>
                            </div>
                          </div>
                        </div>
                      )}

                      {notification.deliveryDetails?.specialInstructions && (
                        <div className="mb-4 p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
                          <div className="flex items-start gap-2">
                            <AlertTriangle className="h-5 w-5 text-yellow-600 mt-0.5" />
                            <div>
                              <h4 className="font-medium text-yellow-800">Special Instructions</h4>
                              <p className="text-sm text-yellow-700">
                                {notification.deliveryDetails.specialInstructions}
                              </p>
                            </div>
                          </div>
                        </div>
                      )}

                      {notification.type === 'new_delivery' && (
                        <div className="flex gap-2">
                          <Button 
                            size="sm"
                            onClick={() => {
                              acceptDelivery.mutate(notification.orderId);
                              if (!notification.isRead && notification.id < 900) {
                                markAsRead.mutate(notification.id);
                              }
                            }}
                            disabled={acceptDelivery.isPending}
                          >
                            <CheckCircle className="h-4 w-4 mr-2" />
                            Accept Delivery
                          </Button>
                          <Button 
                            size="sm" 
                            variant="outline"
                            onClick={() => window.open(`tel:${notification.deliveryDetails?.customerPhone}`)}
                          >
                            <Phone className="h-4 w-4 mr-2" />
                            Call Customer
                          </Button>
                          <Button 
                            size="sm" 
                            variant="outline"
                            onClick={() => setSelectedTab('tracking')}
                          >
                            <Navigation className="h-4 w-4 mr-2" />
                            View Map
                          </Button>
                        </div>
                      )}
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </TabsContent>

          {/* Active Deliveries Tab */}
          <TabsContent value="active" className="space-y-4">
            {activeDeliveries.map((delivery) => (
              <Card key={delivery.id} className="border-l-4 border-l-green-500">
                <CardHeader>
                  <div className="flex items-center justify-between">
                    <CardTitle className="flex items-center gap-2">
                      <Package className="h-5 w-5" />
                      Order #{delivery.orderId}
                    </CardTitle>
                    <Badge variant="outline" className="capitalize">
                      {delivery.status.replace('_', ' ')}
                    </Badge>
                  </div>
                  <CardDescription>
                    Customer: {delivery.customerName} • Assigned {new Date(delivery.assignedAt).toLocaleTimeString()}
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  {/* Progress Indicator */}
                  <div className="mb-4">
                    <div className="flex justify-between text-xs text-muted-foreground mb-2">
                      <span>Progress</span>
                      <span>{getStatusProgress(delivery.status)}% Complete</span>
                    </div>
                    <Progress value={getStatusProgress(delivery.status)} className="h-2" />
                  </div>

                  <div className="grid grid-cols-2 gap-4 mb-4">
                    <div className="space-y-2">
                      <div className="flex items-center gap-2 text-sm">
                        <MapPin className="h-4 w-4 text-red-500" />
                        <span className="font-medium">Pickup:</span>
                      </div>
                      <p className="text-sm text-muted-foreground pl-6">
                        {delivery.pickupAddress}
                      </p>

                      <div className="flex items-center gap-2 text-sm">
                        <MapPin className="h-4 w-4 text-green-500" />
                        <span className="font-medium">Delivery:</span>
                      </div>
                      <p className="text-sm text-muted-foreground pl-6">
                        {delivery.deliveryAddress}
                      </p>
                    </div>

                    <div className="space-y-2">
                      <div className="flex items-center gap-2 text-sm">
                        <DollarSign className="h-4 w-4 text-green-600" />
                        <span className="font-medium">Fee:</span>
                        <span className="text-green-600 font-semibold">₹{delivery.deliveryFee}</span>
                      </div>
                      <div className="flex items-center gap-2 text-sm">
                        <Clock className="h-4 w-4 text-blue-500" />
                        <span className="font-medium">Est. Time:</span>
                        <span className="text-muted-foreground">{delivery.estimatedTime} mins</span>
                      </div>
                      <div className="flex items-center gap-2 text-sm">
                        <Navigation className="h-4 w-4 text-purple-500" />
                        <span className="font-medium">Distance:</span>
                        <span className="text-muted-foreground">{delivery.estimatedDistance} km</span>
                      </div>
                    </div>
                  </div>

                  {delivery.specialInstructions && (
                    <div className="mb-4 p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
                      <div className="flex items-start gap-2">
                        <AlertTriangle className="h-5 w-5 text-yellow-600 mt-0.5" />
                        <div>
                          <h4 className="font-medium text-yellow-800">Special Instructions</h4>
                          <p className="text-sm text-yellow-700">{delivery.specialInstructions}</p>
                        </div>
                      </div>
                    </div>
                  )}

                  <div className="flex gap-2 flex-wrap">
                    {delivery.status === 'assigned' && (
                      <Button 
                        size="sm"
                        onClick={() => updateDeliveryStatus.mutate({ deliveryId: delivery.id, status: 'picked_up' })}
                        disabled={updateDeliveryStatus.isPending}
                      >
                        <CheckCircle className="h-4 w-4 mr-2" />
                        Mark Picked Up
                      </Button>
                    )}

                    {delivery.status === 'picked_up' && (
                      <Button 
                        size="sm"
                        onClick={() => updateDeliveryStatus.mutate({ deliveryId: delivery.id, status: 'in_transit' })}
                        disabled={updateDeliveryStatus.isPending}
                      >
                        <Truck className="h-4 w-4 mr-2" />
                        Start Delivery
                      </Button>
                    )}

                    {delivery.status === 'in_transit' && (
                      <Button 
                        size="sm"
                        onClick={() => updateDeliveryStatus.mutate({ deliveryId: delivery.id, status: 'delivered' })}
                        disabled={updateDeliveryStatus.isPending}
                      >
                        <CheckCircle className="h-4 w-4 mr-2" />
                        Mark Delivered
                      </Button>
                    )}

                    <Button 
                      size="sm" 
                      variant="outline"
                      onClick={() => window.open(`https://maps.google.com/maps?daddr=${encodeURIComponent(delivery.deliveryAddress)}`)}
                    >
                      <Navigation className="h-4 w-4 mr-2" />
                      Navigate
                    </Button>

                    <Button 
                      size="sm" 
                      variant="outline"
                      onClick={() => window.open(`tel:${delivery.customerPhone}`)}
                    >
                      <Phone className="h-4 w-4 mr-2" />
                      Call Customer
                    </Button>

                    <Button 
                      size="sm" 
                      variant="outline"
                      onClick={() => setSelectedTab('tracking')}
                    >
                      <Camera className="h-4 w-4 mr-2" />
                      Track Live
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))}
          </TabsContent>

          {/* Live Tracking Tab */}
          <TabsContent value="tracking" className="space-y-4">
            {activeDeliveries && activeDeliveries.length > 0 ? (
              <ProfessionalLiveTracking 
                deliveryData={{
                  id: activeDeliveries[0].id,
                  orderId: activeDeliveries[0].orderId,
                  customerName: activeDeliveries[0].customerName,
                  customerPhone: activeDeliveries[0].customerPhone,
                  pickupAddress: activeDeliveries[0].pickupAddress,
                  deliveryAddress: activeDeliveries[0].deliveryAddress,
                  storeName: "Family Restaurant", // Can be made dynamic
                  storePhone: "+977-9800000001",
                  deliveryFee: activeDeliveries[0].deliveryFee,
                  status: activeDeliveries[0].status,
                  estimatedDistance: activeDeliveries[0].estimatedDistance,
                  estimatedTime: activeDeliveries[0].estimatedTime,
                  specialInstructions: activeDeliveries[0].specialInstructions
                }}
                deliveryPartnerId={partner?.id || 0}
                onLocationUpdate={(location) => {
                  // Send location updates to server
                  fetch('/api/tracking/location', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({
                      deliveryId: activeDeliveries[0].id,
                      deliveryPartnerId: partner?.id,
                      latitude: location.lat,
                      longitude: location.lng,
                      timestamp: location.timestamp
                    })
                  }).catch(console.error);
                }}
              />
            ) : (
              <Card className="shadow-lg border-0">
                <CardContent className="text-center py-12">
                  <Navigation className="h-16 w-16 text-gray-300 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-gray-900 mb-2">No Active Deliveries</h3>
                  <p className="text-gray-600">You currently have no deliveries to track. When you accept a delivery, live tracking will appear here.</p>
                </CardContent>
              </Card>
            )}
          </TabsContent>

          {/* Earnings Tab */}
          <TabsContent value="earnings" className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <Card>
                <CardHeader>
                  <CardTitle className="text-lg">Today's Summary</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-3">
                    <div className="flex justify-between">
                      <span>Deliveries:</span>
                      <span className="font-semibold">{currentStats.todayDeliveries}</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Earnings:</span>
                      <span className="font-semibold text-green-600">₹{currentStats.todayEarnings}</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Avg per delivery:</span>
                      <span className="font-semibold">₹{Math.round(currentStats.todayEarnings / currentStats.todayDeliveries) || 0}</span>
                    </div>
                  </div>
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle className="text-lg">This Week</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-3">
                    <div className="flex justify-between">
                      <span>Deliveries:</span>
                      <span className="font-semibold">52</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Earnings:</span>
                      <span className="font-semibold text-green-600">₹2,850</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Rating:</span>
                      <span className="font-semibold flex items-center gap-1">
                        {currentStats.rating}
                        <Star className="h-4 w-4 text-yellow-500 fill-current" />
                      </span>
                    </div>
                  </div>
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle className="text-lg">All Time</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-3">
                    <div className="flex justify-between">
                      <span>Total Deliveries:</span>
                      <span className="font-semibold">{currentStats.totalDeliveries}</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Total Earnings:</span>
                      <span className="font-semibold text-green-600">₹{currentStats.totalEarnings}</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Member Since:</span>
                      <span className="font-semibold">Jan 2024</span>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </div>
          </TabsContent>
          <TabsContent value="tracking" className="space-y-4">
            <DeliveryMap
              deliveryPartnerId={user?.id}
              pickupLocation={{
                lat: 26.6636,
                lng: 86.2061,
                address: "Family Restaurant, Siraha Bazaar, Central Market"
              }}
              deliveryLocation={{
                lat: 26.6756,
                lng: 86.2181,
                address: "Customer Location, Siraha Municipality, Nepal"
              }}
              onLocationUpdate={(location) => {
                console.log('Location updated:', location);
                toast({
                  title: "Location Updated",
                  description: "Your location has been shared with customers.",
                });
              }}
            />
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

client/src/pages/DeliveryTrackingDashboa.content:
import React, { useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import RealTimeTrackingMap from '@/components/RealTimeTrackingMap';
import ProfessionalLiveTracking from '@/components/tracking/ProfessionalLiveTracking';
import { 
  MapPin, 
  Clock, 
  Package, 
  Phone, 
  Navigation, 
  CheckCircle, 
  AlertCircle,
  Truck,
  ExternalLink
} from 'lucide-react';

interface DeliveryAssignment {
  id: number;
  orderId: number;
  status: string;
  pickupAddress: string;
  deliveryAddress: string;
  customerName: string;
  customerPhone: string;
  deliveryFee: string;
  estimatedDistance: string;
  specialInstructions?: string;
  assignedAt: string;
}

interface LocationState {
  latitude: number | null;
  longitude: number | null;
  accuracy: number | null;
  lastUpdate: Date | null;
}

export default function DeliveryTrackingDashboard() {
  const [selectedDelivery, setSelectedDelivery] = useState<number | null>(null);
  const [locationState, setLocationState] = useState<LocationState>({
    latitude: null,
    longitude: null,
    accuracy: null,
    lastUpdate: null
  });
  const [watchId, setWatchId] = useState<number | null>(null);

  const queryClient = useQueryClient();

  // Get current delivery assignments
  const { data: assignments, isLoading } = useQuery({
    queryKey: ['/api/delivery-partners/assignments'],
    queryFn: async () => {
      const response = await fetch('/api/delivery-partners/assignments');
      if (!response.ok) throw new Error('Failed to fetch assignments');
      return response.json();
    },
    refetchInterval: 30000 // Refresh every 30 seconds
  });

  // Location update mutation
  const locationUpdateMutation = useMutation({
    mutationFn: async (locationData: any) => {
      const response = await fetch('/api/tracking/location', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(locationData)
      });
      if (!response.ok) throw new Error('Failed to update location');
      return response.json();
    }
  });

  // Status update mutation
  const statusUpdateMutation = useMutation({
    mutationFn: async ({ deliveryId, status, description }: any) => {
      const response = await fetch(`/api/tracking/status/${deliveryId}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          status, 
          description,
          latitude: locationState.latitude,
          longitude: locationState.longitude,
          updatedBy: 1 // TODO: Get from auth context
        })
      });
      if (!response.ok) throw new Error('Failed to update status');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/delivery-partners/assignments'] });
    }
  });

  // Start location tracking
  const startLocationTracking = () => {
    if ('geolocation' in navigator) {
      const id = navigator.geolocation.watchPosition(
        (position) => {
          const newLocation = {
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
            accuracy: position.coords.accuracy,
            lastUpdate: new Date()
          };

          setLocationState(newLocation);

          // Send location update for active delivery
          if (selectedDelivery) {
            locationUpdateMutation.mutate({
              deliveryId: selectedDelivery,
              deliveryPartnerId: 1, // TODO: Get from auth context
              latitude: position.coords.latitude,
              longitude: position.coords.longitude,
              heading: position.coords.heading,
              speed: position.coords.speed,
              accuracy: position.coords.accuracy
            });
          }
        },
        (error) => {
          console.error('Geolocation error:', error);
        },
        {
          enableHighAccuracy: true,
          timeout: 5000,
          maximumAge: 1000
        }
      );
      setWatchId(id);
    }
  };

  // Stop location tracking
  const stopLocationTracking = () => {
    if (watchId) {
      navigator.geolocation.clearWatch(watchId);
      setWatchId(null);
    }
  };

  useEffect(() => {
    return () => {
      if (watchId) {
        navigator.geolocation.clearWatch(watchId);
      }
    };
  }, [watchId]);

  const getStatusColor = (status: string) => {
    const colors: Record<string, string> = {
      'pending': 'bg-yellow-500',
      'assigned': 'bg-blue-500',
      'en_route_pickup': 'bg-purple-500',
      'picked_up': 'bg-orange-500',
      'en_route_delivery': 'bg-indigo-500',
      'delivered': 'bg-green-500',
      'cancelled': 'bg-red-500'
    };
    return colors[status] || 'bg-gray-500';
  };

  const formatStatus = (status: string) => {
    return status.split('_').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ');
  };

  const getNextStatusAction = (currentStatus: string) => {
    const statusFlow: Record<string, { status: string; label: string; description: string }> = {
      'assigned': { status: 'en_route_pickup', label: 'Start Pickup', description: 'Heading to pickup location' },
      'en_route_pickup': { status: 'picked_up', label: 'Mark Picked Up', description: 'Package collected from store' },
      'picked_up': { status: 'en_route_delivery', label: 'Start Delivery', description: 'Heading to customer location' },
      'en_route_delivery': { status: 'delivered', label: 'Mark Delivered', description: 'Package delivered to customer' }
    };
    return statusFlow[currentStatus];
  };

  const updateDeliveryStatus = (deliveryId: number, status: string, description: string) => {
    statusUpdateMutation.mutate({ deliveryId, status, description });
  };

  const openGoogleMaps = (address: string) => {
    const encodedAddress = encodeURIComponent(address);
    const googleMapsUrl = `https://www.google.com/maps/search/?api=1&query=${encodedAddress}`;
    window.open(googleMapsUrl, '_blank');
  };

  const makePhoneCall = (phoneNumber: string) => {
    window.location.href = `tel:${phoneNumber}`;
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-2 text-gray-600">Loading assignments...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="flex justify-between items-center">
        <h1 className="text-2xl font-bold">Delivery Tracking Dashboard</h1>

        <div className="flex items-center gap-4">
          {locationState.lastUpdate && (
            <div className="text-sm text-gray-600">
              Location: {locationState.accuracy}m accuracy
            </div>
          )}

          {!watchId ? (
            <Button onClick={startLocationTracking} variant="outline">
              <MapPin className="h-4 w-4 mr-2" />
              Start Tracking
            </Button>
          ) : (
            <Button onClick={stopLocationTracking} variant="outline">
              <MapPin className="h-4 w-4 mr-2" />
              Stop Tracking
            </Button>
          )}
        </div>
      </div>

      {/* Location Status Alert */}
      {locationState.lastUpdate && (
        <Alert>
          <CheckCircle className="h-4 w-4" />
          <AlertDescription>
            Location tracking active. Last update: {locationState.lastUpdate.toLocaleTimeString()}
            {locationState.accuracy && ` (±${Math.round(locationState.accuracy)}m)`}
          </AlertDescription>
        </Alert>
      )}

      {/* Active Deliveries */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {assignments?.map((assignment: DeliveryAssignment) => (
          <Card key={assignment.id} className={`cursor-pointer transition-colors ${
            selectedDelivery === assignment.id ? 'ring-2 ring-blue-500 bg-blue-50' : 'hover:bg-gray-50'
          }`}>
            <CardHeader>
              <div className="flex justify-between items-start">
                <CardTitle className="text-lg">Order #{assignment.orderId}</CardTitle>
                <Badge className={getStatusColor(assignment.status)}>
                  {formatStatus(assignment.status)}
                </Badge>
              </div>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="space-y-2">
                <div className="flex items-center gap-2 text-sm">
                  <Package className="h-4 w-4 text-gray-500" />
                  <span className="font-medium">Pickup:</span>
                </div>
                <p className="text-sm text-gray-600 pl-6">{assignment.pickupAddress}</p>

                <div className="flex items-center gap-2 text-sm">
                  <MapPin className="h-4 w-4 text-gray-500" />
                  <span className="font-medium">Delivery:</span>
                </div>
                <p className="text-sm text-gray-600 pl-6">{assignment.deliveryAddress}</p>
              </div>

              <div className="flex items-center justify-between text-sm">
                <div className="flex items-center gap-2">
                  <Phone className="h-4 w-4 text-gray-500" />
                  <span>{assignment.customerName}</span>
                </div>
                <span className="font-medium text-green-600">Rs. {assignment.deliveryFee}</span>
              </div>

              {assignment.specialInstructions && (
                <div className="text-sm">
                  <span className="font-medium text-gray-600">Instructions:</span>
                  <p className="text-gray-800 mt-1">{assignment.specialInstructions}</p>
                </div>
              )}

              <div className="flex gap-2">
                <Button
                  size="sm"
                  variant="outline"
                  onClick={() => openGoogleMaps(assignment.pickupAddress)}
                  className="flex-1"
                >
                  <Navigation className="h-4 w-4 mr-1" />
                  Navigate
                </Button>

                <Button
                  size="sm"
                  variant="outline"
                  onClick={() => makePhoneCall(assignment.customerPhone)}
                  className="flex-1"
                >
                  <Phone className="h-4 w-4 mr-1" />
                  Call
                </Button>
              </div>

              {getNextStatusAction(assignment.status) && (
                <Button
                  onClick={() => {
                    const nextAction = getNextStatusAction(assignment.status);
                    updateDeliveryStatus(assignment.id, nextAction.status, nextAction.description);
                  }}
                  className="w-full"
                  disabled={statusUpdateMutation.isPending}
                >
                  {statusUpdateMutation.isPending ? (
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  ) : (
                    <CheckCircle className="h-4 w-4 mr-2" />
                  )}
                  {getNextStatusAction(assignment.status).label}
                </Button>
              )}

              <Button
                variant="secondary"
                size="sm"
                onClick={() => setSelectedDelivery(
                  selectedDelivery === assignment.id ? null : assignment.id
                )}
                className="w-full"
              >
                <Truck className="h-4 w-4 mr-2" />
                {selectedDelivery === assignment.id ? 'Hide Map' : 'Show Live Map'}
              </Button>
            </CardContent>
          </Card>
        ))}
      </div>

      {assignments?.length === 0 && (
        <Card>
          <CardContent className="text-center py-12">
            <Truck className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">No Active Deliveries</h3>
            <p className="text-gray-600">You currently have no delivery assignments.</p>
          </CardContent>
        </Card>
      )}

      {/* Professional Live Tracking */}
      {selectedDelivery && assignments && (
        <div className="mt-8">
          {(() => {
            const selectedAssignment = assignments.find((a: DeliveryAssignment) => a.id === selectedDelivery);
            return selectedAssignment ? (
              <ProfessionalLiveTracking 
                deliveryData={{
                  id: selectedAssignment.id,
                  orderId: selectedAssignment.orderId,
                  customerName: selectedAssignment.customerName,
                  customerPhone: selectedAssignment.customerPhone,
                  pickupAddress: selectedAssignment.pickupAddress,
                  deliveryAddress: selectedAssignment.deliveryAddress,
                  storeName: "Family Restaurant",
                  storePhone: "+977-9800000001",
                  deliveryFee: parseFloat(selectedAssignment.deliveryFee),
                  status: selectedAssignment.status,
                  estimatedDistance: parseFloat(selectedAssignment.estimatedDistance),
                  estimatedTime: 25,
                  specialInstructions: selectedAssignment.specialInstructions
                }}
                deliveryPartnerId={1}
                onLocationUpdate={(location) => {
                  locationUpdateMutation.mutate({
                    deliveryId: selectedAssignment.id,
                    deliveryPartnerId: 1,
                    latitude: location.lat,
                    longitude: location.lng,
                    timestamp: location.timestamp
                  });
                }}
              />
            ) : null;
          })()}
        </div>
      )}
    </div>
  );
}

client/src/pages/DeliveryTrackingMap.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { useUser } from "@/hooks/use-user";
import { 
  MapPin, 
  Navigation, 
  Clock, 
  Phone, 
  MessageCircle,
  CheckCircle,
  Package,
  Truck,
  Camera,
  AlertTriangle
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface DeliveryTracking {
  id: number;
  orderId: number;
  status: 'assigned' | 'picked_up' | 'in_transit' | 'delivered';
  customerName: string;
  customerPhone: string;
  pickupAddress: string;
  deliveryAddress: string;
  deliveryFee: string;
  estimatedDistance: number;
  estimatedTime: number;
  actualTime?: number;
  specialInstructions?: string;
  currentLocation?: string;
  assignedAt: string;
  pickedUpAt?: string;
  deliveredAt?: string;
}

export default function DeliveryTrackingMap() {
  const { user } = useUser();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [currentLocation, setCurrentLocation] = useState<string>("");
  const [proofPhoto, setProofPhoto] = useState<File | null>(null);

  const { data: activeDelivery, isLoading } = useQuery({
    queryKey: ['/api/deliveries/active-tracking', user?.id],
    enabled: !!user?.id,
    refetchInterval: 30000, // Refresh every 30 seconds
  });

  const updateStatusMutation = useMutation({
    mutationFn: async ({ deliveryId, status }: { deliveryId: number; status: string }) => {
      return apiRequest(`/api/deliveries/${deliveryId}/status`, {
        method: 'PUT',
        body: { status, partnerId: user?.id },
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/deliveries/active-tracking'] });
      toast({
        title: "Status Updated",
        description: "Delivery status has been updated successfully.",
      });
    },
  });

  const updateLocationMutation = useMutation({
    mutationFn: async ({ deliveryId, location }: { deliveryId: number; location: string }) => {
      return apiRequest(`/api/deliveries/${deliveryId}/location`, {
        method: 'PUT',
        body: { location },
      });
    },
    onSuccess: () => {
      toast({
        title: "Location Updated",
        description: "Your location has been shared with the customer.",
      });
    },
  });

  const uploadProofMutation = useMutation({
    mutationFn: async ({ deliveryId, photo }: { deliveryId: number; photo: File }) => {
      const formData = new FormData();
      formData.append('proof', photo);
      formData.append('deliveryId', deliveryId.toString());

      return apiRequest('/api/deliveries/upload-proof', {
        method: 'POST',
        body: formData,
      });
    },
    onSuccess: () => {
      toast({
        title: "Proof Uploaded",
        description: "Delivery proof has been uploaded successfully.",
      });
    },
  });

  useEffect(() => {
    // Get current location
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setCurrentLocation(`${latitude},${longitude}`);
        },
        (error) => {
          console.error('Error getting location:', error);
        }
      );
    }
  }, []);

  const getStatusProgress = (status: string) => {
    switch (status) {
      case 'assigned': return 25;
      case 'picked_up': return 50;
      case 'in_transit': return 75;
      case 'delivered': return 100;
      default: return 0;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'assigned': return 'bg-yellow-500';
      case 'picked_up': return 'bg-blue-500';
      case 'in_transit': return 'bg-orange-500';
      case 'delivered': return 'bg-green-500';
      default: return 'bg-gray-500';
    }
  };

  const handleStatusUpdate = (deliveryId: number, status: string) => {
    updateStatusMutation.mutate({ deliveryId, status });
  };

  const handleLocationUpdate = (deliveryId: number) => {
    if (currentLocation) {
      updateLocationMutation.mutate({ deliveryId, location: currentLocation });
    } else {
      toast({
        title: "Location Error",
        description: "Unable to get your current location. Please enable location services.",
        variant: "destructive",
      });
    }
  };

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setProofPhoto(file);
    }
  };

  const handleProofUpload = (deliveryId: number) => {
    if (proofPhoto) {
      uploadProofMutation.mutate({ deliveryId, photo: proofPhoto });
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p>Loading delivery tracking...</p>
        </div>
      </div>
    );
  }

  if (!activeDelivery) {
    return (
      <div className="container mx-auto p-6">
        <Card>
          <CardContent className="py-12 text-center">
            <Truck className="h-16 w-16 mx-auto mb-4 text-muted-foreground" />
            <h3 className="text-lg font-semibold mb-2">No Active Delivery</h3>
            <p className="text-muted-foreground">
              You don't have any active deliveries at the moment.
            </p>
            <Button className="mt-4" onClick={() => window.location.href = '/delivery-dashboard'}>
              Go to Dashboard
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 max-w-4xl">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h1 className="text-3xl font-bold flex items-center gap-2">
            <Navigation className="h-8 w-8" />
            Delivery Tracking
          </h1>
          <p className="text-muted-foreground">Order #{activeDelivery.orderId}</p>
        </div>
        <Badge variant="outline" className="capitalize text-lg px-4 py-2">
          {activeDelivery.status.replace('_', ' ')}
        </Badge>
      </div>

      {/* Progress Tracker */}
      <Card className="mb-6">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Package className="h-5 w-5" />
            Delivery Progress
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <Progress value={getStatusProgress(activeDelivery.status)} className="h-3" />
            <div className="flex justify-between text-sm">
              <div className="flex flex-col items-center">
                <div className={`w-4 h-4 rounded-full ${getStatusProgress(activeDelivery.status) >= 25 ? 'bg-primary' : 'bg-gray-300'}`}></div>
                <span className="mt-1">Assigned</span>
              </div>
              <div className="flex flex-col items-center">
                <div className={`w-4 h-4 rounded-full ${getStatusProgress(activeDelivery.status) >= 50 ? 'bg-primary' : 'bg-gray-300'}`}></div>
                <span className="mt-1">Picked Up</span>
              </div>
              <div className="flex flex-col items-center">
                <div className={`w-4 h-4 rounded-full ${getStatusProgress(activeDelivery.status) >= 75 ? 'bg-primary' : 'bg-gray-300'}`}></div>
                <span className="mt-1">In Transit</span>
              </div>
              <div className="flex flex-col items-center">
                <div className={`w-4 h-4 rounded-full ${getStatusProgress(activeDelivery.status) >= 100 ? 'bg-primary' : 'bg-gray-300'}`}></div>
                <span className="mt-1">Delivered</span>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Delivery Details */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <MapPin className="h-5 w-5 text-red-500" />
              Pickup Location
            </CardTitle>
          </CardHeader>
          <CardContent>
            <p className="text-sm mb-4">{activeDelivery.pickupAddress}</p>
            <Button 
              size="sm" 
              variant="outline"
              onClick={() => window.open(`https://maps.google.com/maps?daddr=${encodeURIComponent(activeDelivery.pickupAddress)}`)}
            >
              <Navigation className="h-4 w-4 mr-2" />
              Navigate
            </Button>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <MapPin className="h-5 w-5 text-green-500" />
              Delivery Location
            </CardTitle>
          </CardHeader>
          <CardContent>
            <p className="text-sm mb-2">{activeDelivery.deliveryAddress}</p>
            <p className="text-sm text-muted-foreground mb-4">
              Customer: {activeDelivery.customerName}
            </p>
            <div className="flex gap-2">
              <Button 
                size="sm" 
                variant="outline"
                onClick={() => window.open(`https://maps.google.com/maps?daddr=${encodeURIComponent(activeDelivery.deliveryAddress)}`)}
              >
                <Navigation className="h-4 w-4 mr-2" />
                Navigate
              </Button>
              <Button 
                size="sm" 
                variant="outline"
                onClick={() => window.open(`tel:${activeDelivery.customerPhone}`)}
              >
                <Phone className="h-4 w-4 mr-2" />
                Call
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Delivery Information */}
      <Card className="mb-6">
        <CardHeader>
          <CardTitle>Delivery Information</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div className="text-center">
              <div className="text-2xl font-bold text-green-600">₹{activeDelivery.deliveryFee}</div>
              <div className="text-sm text-muted-foreground">Delivery Fee</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-blue-600">{activeDelivery.estimatedDistance} km</div>
              <div className="text-sm text-muted-foreground">Distance</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-orange-600">{activeDelivery.estimatedTime} min</div>
              <div className="text-sm text-muted-foreground">Est. Time</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-purple-600">
                {activeDelivery.actualTime || '--'}
              </div>
              <div className="text-sm text-muted-foreground">Actual Time</div>
            </div>
          </div>

          {activeDelivery.specialInstructions && (
            <div className="mt-4 p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
              <div className="flex items-start gap-2">
                <AlertTriangle className="h-5 w-5 text-yellow-600 mt-0.5" />
                <div>
                  <h4 className="font-medium text-yellow-800">Special Instructions</h4>
                  <p className="text-sm text-yellow-700">{activeDelivery.specialInstructions}</p>
                </div>
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Action Buttons */}
      <Card>
        <CardHeader>
          <CardTitle>Delivery Actions</CardTitle>
          <CardDescription>Update delivery status and location</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-3">
              <h4 className="font-medium">Status Updates</h4>

              {activeDelivery.status === 'assigned' && (
                <Button 
                  onClick={() => handleStatusUpdate(activeDelivery.id, 'picked_up')}
                  disabled={updateStatusMutation.isPending}
                  className="w-full"
                >
                  <CheckCircle className="h-4 w-4 mr-2" />
                  Mark as Picked Up
                </Button>
              )}

              {activeDelivery.status === 'picked_up' && (
                <Button 
                  onClick={() => handleStatusUpdate(activeDelivery.id, 'in_transit')}
                  disabled={updateStatusMutation.isPending}
                  className="w-full"
                >
                  <Truck className="h-4 w-4 mr-2" />
                  Start Delivery
                </Button>
              )}

              {activeDelivery.status === 'in_transit' && (
                <div className="space-y-2">
                  <div>
                    <label htmlFor="proof-upload" className="block text-sm font-medium mb-2">
                      Upload Proof of Delivery
                    </label>
                    <input
                      id="proof-upload"
                      type="file"
                      accept="image/*"
                      onChange={handleFileUpload}
                      className="w-full text-sm border border-gray-300 rounded-lg px-3 py-2"
                    />
                  </div>
                  <Button 
                    onClick={() => handleStatusUpdate(activeDelivery.id, 'delivered')}
                    disabled={updateStatusMutation.isPending}
                    className="w-full"
                  >
                    <CheckCircle className="h-4 w-4 mr-2" />
                    Mark as Delivered
                  </Button>
                </div>
              )}
            </div>

            <div className="space-y-3">
              <h4 className="font-medium">Location & Communication</h4>

              <Button 
                onClick={() => handleLocationUpdate(activeDelivery.id)}
                disabled={updateLocationMutation.isPending}
                variant="outline"
                className="w-full"
              >
                <Navigation className="h-4 w-4 mr-2" />
                Share Location
              </Button>

              <Button 
                onClick={() => window.open(`tel:${activeDelivery.customerPhone}`)}
                variant="outline"
                className="w-full"
              >
                <Phone className="h-4 w-4 mr-2" />
                Call Customer
              </Button>

              <Button 
                onClick={() => window.open(`sms:${activeDelivery.customerPhone}`)}
                variant="outline"
                className="w-full"
              >
                <MessageCircle className="h-4 w-4 mr-2" />
                Send SMS
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

