import { useState } from "react";
import { Link, useLocation } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Eye, EyeOff, Store, User } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { playSound } from "@/lib/soundEffects";

const loginSchema = z.object({
  email: z.string().email("Please enter a valid email address"),
  password: z.string().min(6, "Password must be at least 6 characters"),
});

type LoginForm = z.infer<typeof loginSchema>;

export default function Login() {
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const [, setLocation] = useLocation();
  const { login, loginWithGoogle } = useAuth();
  const { toast } = useToast();

  const form = useForm<LoginForm>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      email: "",
      password: "",
    },
  });

  const onSubmit = async (data: LoginForm) => {
    setIsLoading(true);
    try {
      await login(data.email, data.password);

      // Play success sound for successful login
      playSound.success();

      toast({
        title: "Welcome back!",
        description: "You have been successfully logged in.",
      });

      setLocation("/");
    } catch (error) {
      // Play error sound for failed login
      playSound.error();

      toast({
        title: "Login failed",
        description: error instanceof Error ? error.message : "Invalid credentials",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleGoogleLogin = async () => {
    try {
      setIsLoading(true);

      // Check if running in Android WebView
      const isAndroid = (window as any).AndroidBridge?.isAndroidApp?.() || false;

      if (isAndroid) {
        console.log('🔐 Starting Google login in Android WebView...');
        (window as any).AndroidBridge?.logMessage('Starting Google OAuth flow');
      }

      const userData = await loginWithGoogle();

      // Handle redirect case (Android WebView)
      if (!userData) {
        console.log('🔄 Redirect initiated, waiting for result...');
        return;
      }

      if (userData) {
        console.log('✅ Google authentication successful, registering with backend...');

        // Send user data to backend for registration/login
        const response = await fetch('/api/auth/social-login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            firebaseUid: userData.uid,
            email: userData.email,
            fullName: userData.displayName,
            photoUrl: userData.photoURL,
            emailVerified: userData.emailVerified,
            role: 'customer'
          }),
        });

        if (response.ok) {
          const { user } = await response.json();
          setUser(user);

          toast({
            title: "Login Successful",
            description: `Welcome back, ${user.fullName}!`,
          });

          // Show success in Android app
          if (isAndroid) {
            (window as any).AndroidBridge?.showToast(`Welcome ${user.fullName}!`);
            (window as any).AndroidBridge?.logMessage(`User ${user.email} logged in successfully`);
          }

          // Redirect to home or intended page
          setLocation('/');
        } else {
          throw new Error('Failed to authenticate with server');
        }
      }
    } catch (error) {
      console.error('🚫 Google login error:', error);

      const errorMessage = error instanceof Error ? error.message : 'Unknown error occurred';

      toast({
        title: "Login Failed",
        description: `Could not sign in with Google: ${errorMessage}`,
        variant: "destructive",
      });

      // Show error in Android app
      if ((window as any).AndroidBridge?.isAndroidApp?.()) {
        (window as any).AndroidBridge?.showToast('Google login failed. Please try again.');
        (window as any).AndroidBridge?.logMessage(`Google login error: ${errorMessage}`);
      }
    } finally {
      setIsLoading(false);
    }
  };



  return (
    <div className="min-h-screen bg-muted flex items-center justify-center py-12">
      <div className="w-full max-w-md">
        <Card>
          <CardHeader className="text-center">
            <div className="flex items-center justify-center mb-4">
              <Store className="h-8 w-8 text-primary mr-2" />
              <span className="text-2xl font-bold text-foreground">Siraha Bazaar</span>
            </div>
            <CardTitle className="text-2xl">Welcome Back</CardTitle>
            <p className="text-muted-foreground">
              Sign in to your account to continue shopping
            </p>
          </CardHeader>
          <CardContent>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="email"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Email Address</FormLabel>
                      <FormControl>
                        <Input
                          type="email"
                          placeholder="Enter your email"
                          {...field}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="password"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Password</FormLabel>
                      <FormControl>
                        <div className="relative">
                          <Input
                            type={showPassword ? "text" : "password"}
                            placeholder="Enter your password"
                            {...field}
                          />
                          <Button
                            type="button"
                            variant="ghost"
                            size="sm"
                            className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                            onClick={() => setShowPassword(!showPassword)}
                          >
                            {showPassword ? (
                              <EyeOff className="h-4 w-4" />
                            ) : (
                              <Eye className="h-4 w-4" />
                            )}
                          </Button>
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-2">
                    <input
                      type="checkbox"
                      id="remember"
                      className="rounded border-gray-300"
                    />
                    <label htmlFor="remember" className="text-sm text-muted-foreground">
                      Remember me
                    </label>
                  </div>
                  <Link href="/forgot-password">
                    <Button
                      type="button"
                      variant="link"
                      className="text-sm text-primary hover:underline p-0 h-auto"
                    >
                      Forgot password?
                    </Button>
                  </Link>
                </div>

                <Button type="submit" className="w-full btn-primary" disabled={isLoading}>
                  {isLoading ? "Signing in..." : "Sign In"}
                </Button>
              </form>
            </Form>



            <div className="mt-6">
              <div className="relative">
                <div className="absolute inset-0 flex items-center">
                  <span className="w-full border-t" />
                </div>
                <div className="relative flex justify-center text-xs uppercase">
                  <span className="bg-background px-2 text-muted-foreground">
                    Or continue with
                  </span>
                </div>
              </div>

              <Button
                type="button"
                variant="outline"
                className="w-full mt-4"
                onClick={handleGoogleLogin}
                disabled={isLoading}
              >
                <svg className="w-4 h-4 mr-2" viewBox="0 0 24 24">
                  <path fill="currentColor" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
                  <path fill="currentColor" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
                  <path fill="currentColor" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
                  <path fill="currentColor" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
                </svg>
                Continue with Google
              </Button>
            </div>

            <div className="mt-6">
              <div className="text-center">
                <span className="text-muted-foreground">Don't have an account? </span>
                <Link href="/register" className="text-primary hover:underline font-medium">
                  Create Account
                </Link>
              </div>
            </div>

            {/* Password Reset Info */}
            <div className="mt-6 border-t pt-6">
              <div className="text-center mb-4">
                <div className="p-3 bg-blue-50 dark:bg-blue-950 rounded-lg border border-blue-200 dark:border-blue-800 mb-4">
                  <p className="text-sm text-blue-800 dark:text-blue-200 mb-2">
                    <strong>Password Reset:</strong>
                  </p>
                  <p className="text-xs text-blue-700 dark:text-blue-300">
                    Enter your email address and click "Forgot password?" to receive reset instructions.
                    Note: Firebase domain authorization may be required for email delivery.
                  </p>
                </div>

                <p className="text-sm text-muted-foreground mb-3">Demo Accounts:</p>
                <div className="space-y-2 text-sm">
                  <div className="flex items-center justify-center space-x-2 p-2 bg-muted rounded">
                    <User className="h-4 w-4" />
                    <span>Customer: customer@example.com</span>
                  </div>
                  <div className="flex items-center justify-center space-x-2 p-2 bg-muted rounded">
                    <Store className="h-4 w-4" />
                    <span>Shopkeeper: shopkeeper@example.com</span>
                  </div>
                  <p className="text-xs text-muted-foreground">Password: password123</p>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

