              </Card>
            </div>

            {/* First Accept First Serve Notification System */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Truck className="h-5 w-5" />
                  First Accept First Serve - Delivery Notifications
                </CardTitle>
                <p className="text-muted-foreground">
                  Send notifications to all available delivery partners. The
                  first one to accept gets the delivery.
                </p>
              </CardHeader>
              <CardContent className="space-y-6">
                {/* Bulk Actions */}
                <div className="flex flex-wrap gap-3 p-4 bg-gray-50 rounded-lg">
                  <Button
                    onClick={() => {
                      const readyOrders = orders.filter(
                        (order) => order.status === "ready_for_pickup",
                      );
                      if (readyOrders.length > 0) {
                        handleBroadcastNotification(
                          `🚨 URGENT: ${readyOrders.length} orders ready for immediate pickup from ${currentStore?.name}`,
                          readyOrders.map((o) => o.id),
                        );
                      }
                    }}
                    disabled={
                      orders.filter(
                        (order) => order.status === "ready_for_pickup",
                      ).length === 0
                    }
                    className="bg-red-600 hover:bg-red-700"
                  >
                    <AlertCircle className="h-4 w-4 mr-2" />
                    Broadcast All Ready Orders (Urgent)
                  </Button>

                  <Button
                    variant="outline"
                    onClick={() => {
                      const processingOrders = orders.filter(
                        (order) => order.status === "processing",
                      );
                      if (processingOrders.length > 0) {
                        handleBroadcastNotification(
                          `📦 ${processingOrders.length} orders being prepared at ${currentStore?.name}`,
                          processingOrders.map((o) => o.id),
                        );
                      }
                    }}
                    disabled={
                      orders.filter((order) => order.status === "processing")
                        .length === 0
                    }
                  >
                    <Send className="h-4 w-4 mr-2" />
                    Notify About Processing Orders
                  </Button>
                </div>

                {/* Individual Order Notifications */}
                <div className="space-y-4">
                  <h3 className="font-semibold text-lg">
                    Individual Order Notifications
                  </h3>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {orders
                      .filter(
                        (order) =>
                          order.status === "processing" ||
                          order.status === "ready_for_pickup",
                      )
                      .map((order) => (
                        <div
                          key={order.id}
                          className="border rounded-lg p-4 space-y-3 hover:shadow-md transition-shadow"
                        >
                          <div className="flex justify-between items-start">
                            <div>
                              <h4 className="font-medium">Order #{order.id}</h4>
                              <p className="text-sm text-muted-foreground">
                                {order.customerName}
                              </p>
                              <p className="text-xs text-muted-foreground">
                                {order.phone}
                              </p>
                              <Badge
                                variant={
                                  order.status === "ready_for_pickup"
                                    ? "default"
                                    : "secondary"
                                }
                                className={
                                  order.status === "ready_for_pickup"
                                    ? "bg-green-100 text-green-800"
                                    : ""
                                }
                              >
                                {order.status.replace("_", " ").toUpperCase()}
                              </Badge>
                            </div>
                            <div className="text-right">
                              <p className="font-semibold">
                                ₹{Number(order.totalAmount).toLocaleString()}
                              </p>
                              <p className="text-xs text-muted-foreground">
                                {new Date(order.createdAt).toLocaleDateString()}
                              </p>
                            </div>
                          </div>

                          <div className="text-sm text-muted-foreground">
                            <p className="truncate">
                              📍 {order.shippingAddress}
                            </p>
                          </div>

                          <div className="space-y-2">
                            <Button
                              size="sm"
                              className="w-full bg-red-600 hover:bg-red-700"
                              onClick={() =>
                                handleNotifyDeliveryPartner(
                                  order.id,
                                  `🚨 URGENT PICKUP: Order #${order.id} ready NOW from ${currentStore?.name}! Customer: ${order.customerName}, Amount: ₹${order.totalAmount}. First to accept gets this delivery!`,
                                  true,
                                )
                              }
                            >
                              <AlertCircle className="h-4 w-4 mr-2" />
                              Send Urgent Alert (First Accept First Serve)
                            </Button>

                            <Button
                              size="sm"
                              variant="outline"
                              className="w-full"
                              onClick={() =>
                                handleNotifyDeliveryPartner(
                                  order.id,
                                  `📦 Pickup Available: Order #${order.id} at ${currentStore?.name}. Customer: ${order.customerName}, Amount: ₹${order.totalAmount}. Accept to claim this delivery!`,
                                  false,
                                )
                              }
                            >
                              <Send className="h-4 w-4 mr-2" />
                              Send Standard Notification
                            </Button>
                          </div>
                          {/* Display Delivery Tracking Map if a delivery partner is assigned */}
                          {order.deliveryPartner && (
                            <div className="mt-4 border-t pt-4">
                              <div className="flex items-center justify-between mb-3">
                                <h5 className="font-semibold flex items-center gap-2">
                                  <MapPin className="h-4 w-4" />
                                  Live Delivery Tracking
                                </h5>
                                <Badge
                                  variant="secondary"
                                  className="bg-green-100 text-green-800"
                                >
                                  Live
                                </Badge>
                              </div>
                              <div className="bg-gray-50 rounded-lg p-3 mb-3">
                                <p className="text-sm">
                                  <strong>Contact Delivery Partner:</strong>
                                  <a
                                    href={`tel:${order.deliveryPartner.phone}`}
                                    className="text-blue-600 hover:underline ml-2"
                                  >
                                    <Phone className="inline h-4 w-4 mr-1" />
                                    {order.deliveryPartner.phone}
                                  </a>
                                </p>
                                <p className="text-sm text-gray-600 mt-1">
                                  Partner:{" "}
                                  {order.deliveryPartner.name ||
                                    "Assigned Partner"}
                                </p>
                              </div>
                              <div className="h-64 rounded-lg overflow-hidden border">
                                <DeliveryTrackingMap
                                  deliveryId={order.id}
                                  userType="shopkeeper"
                                />
                              </div>
                            </div>
                          )}
                        </div>
                      ))}
                  </div>

                  {orders.filter(
                    (order) =>
                      order.status === "processing" ||
                      order.status === "ready_for_pickup",
                  ).length === 0 && (
                    <div className="text-center py-8">
                      <Truck className="h-16 w-16 text-muted-foreground mx-auto mb-4" />
                      <p className="text-muted-foreground">
                        No orders ready for delivery notifications
                      </p>
                      <p className="text-sm text-muted-foreground mt-2">
                        Orders with "Processing" or "Ready for Pickup" status
                        will appear here
                      </p>
                    </div>
                  )}
                </div>

                {/* Notification History */}
                {notificationHistory.length > 0 && (
                  <div className="space-y-4">
                    <h3 className="font-semibold text-lg">
                      Recent Notifications
                    </h3>
                    <div className="space-y-2 max-h-60 overflow-y-auto">
                      {notificationHistory.slice(0, 10).map((notification) => (
                        <div
                          key={notification.id}
                          className="flex items-center justify-between p-3 bg-gray-50 rounded-lg"
                        >
                          <div className="flex items-center gap-3">
                            {notification.urgent ? (
                              <AlertCircle className="h-4 w-4 text-red-500" />
                            ) : (
                              <Bell className="h-4 w-4 text-blue-500" />
                            )}
                            <div>
                              <p className="text-sm font-medium">
                                Order #{notification.orderId}
                              </p>
                              <p className="text-xs text-muted-foreground">
                                {new Date(notification.sentAt).toLocaleString()}
                              </p>
                            </div>
                          </div>
                          <Badge
                            variant={
                              notification.urgent ? "destructive" : "secondary"
                            }
                          >
                            {notification.urgent ? "Urgent" : "Standard"}
                          </Badge>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>

        {/* Floating Add Product Button */}
        {currentStore && (
          <Dialog open={showAddProductModal} onOpenChange={setShowAddProductModal}>
            <DialogTrigger asChild>
              <Button
                size="lg"
                className="fixed bottom-20 right-6 h-14 w-14 rounded-full shadow-lg bg-orange-500 hover:bg-orange-600 text-white z-50"
                onClick={() => {
                  setEditingProduct(null);
                  form.reset();
                }}
              >
                <Plus className="h-6 w-6" />
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
              <DialogHeader>
                <DialogTitle className="flex items-center gap-2">
                  <Plus className="h-5 w-5" />
                  {editingProduct ? "Edit Product" : "Add New Product"}
                </DialogTitle>
                <DialogDescription>
                  {editingProduct ? "Update your product details and images" : "Add a new product to your store with images and details"}
                </DialogDescription>
              </DialogHeader>
              <div className="mt-4">
                <Form {...form}>
                  <form
                    onSubmit={form.handleSubmit(handleAddProduct)}
                    className="space-y-6"
                  >
                    {/* Product Name */}
                    <FormField
                      control={form.control}
                      name="name"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel className="text-base font-medium">
                            {isRestaurant ? 'Menu Item Name *' : 'Product Name *'}
                          </FormLabel>
                          <FormControl>
                            <Input 
                              placeholder={isRestaurant ? "Enter menu item name" : "Enter product name"} 
                              className="h-11"
                              {...field} 
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />



                    {/* Category */}
                    <FormField
                      control={form.control}
                      name="categoryId"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel className="text-base font-medium">Category *</FormLabel>
                          <Select 
                            onValueChange={(value) => field.onChange(parseInt(value))}
                            defaultValue={field.value?.toString()}
                          >
                            <FormControl>
                              <SelectTrigger className="h-11">
                                <SelectValue placeholder="Select a category" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              {categories.length > 0 ? (
                                categories.map((category) => (
                                  <SelectItem key={category.id} value={category.id.toString()}>
                                    {category.name}
                                  </SelectItem>
                                ))
                              ) : (
                                <SelectItem value="1" disabled>
                                  Loading categories...
                                </SelectItem>
                              )}
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    {/* Description */}
                    <FormField
                      control={form.control}
                      name="description"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel className="text-base font-medium">Description</FormLabel>
                          <FormControl>
                            <Textarea 
                              placeholder="Enter product description" 
                              rows={4}
                              {...field} 
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    {/* Price and Original Price */}
                    <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                      <FormField
                        control={form.control}
                        name="price"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel className="text-base font-medium">Price *</FormLabel>
                            <FormControl>
                              <Input 
                                type="number" 
                                step="0.01" 
                                placeholder="0.00" 
                                className="h-11"
                                {...field} 
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={form.control}
                        name="originalPrice"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel className="text-base font-medium">Original Price</FormLabel>
                            <FormControl>
                              <Input 
                                type="number" 
                                step="0.01" 
                                placeholder="0.00" 
                                className="h-11"
                                {...field} 
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </div>

                    {/* Stock */}
                    <FormField
                      control={form.control}
                      name="stock"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel className="text-base font-medium">Stock Quantity *</FormLabel>
                          <FormControl>
                            <Input 
                              type="number" 
                              placeholder="Enter stock quantity" 
                              className="h-11"
                              {...field}
                              onChange={(e) => field.onChange(parseInt(e.target.value) || 0)}
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="images"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Product Images</FormLabel>
                          <div className="space-y-4">
                            <ImageUpload
                              label=""
                              maxImages={6}
                              minImages={1}
                              onImagesChange={field.onChange}
                              initialImages={field.value || []}
                              className="col-span-full"
                            />
                            <div className="flex items-center gap-2">
                              <div className="flex-1 border-t border-gray-200"></div>
                              <span className="text-sm text-gray-500 px-2">or</span>
                              <div className="flex-1 border-t border-gray-200"></div>
                            </div>
                            <UnifiedImageUpload
                              onImageSelect={(imageUrl) => {
                                const currentImages = field.value || [];
                                if (currentImages.length < 6) {
                                  field.onChange([...currentImages, imageUrl]);
                                }
                              }}
                              selectedImages={field.value || []}
                              maxImages={6 - (field.value?.length || 0)}
                              buttonText="Add Images"
                            />
                          </div>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    {/* Food-specific fields (shown only for restaurants) */}
                    {isRestaurant && (
                      <div className="space-y-6 border-t pt-6">
                        <h3 className="text-lg font-semibold text-orange-600">Food Item Details</h3>

                        {/* Preparation Time */}
                        <FormField
                          control={form.control}
                          name="preparationTime"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel className="text-base font-medium">Preparation Time</FormLabel>
                              <FormControl>
                                <Input 
                                  placeholder="e.g., 15-20 minutes" 
                                  className="h-11"
                                  {...field} 
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        {/* Ingredients */}
                        <div>
                          <FormLabel className="text-base font-medium">Ingredients</FormLabel>
                          <div className="mt-2 space-y-3">
                            <div className="flex gap-2">
                              <Input
                                placeholder="Enter ingredient"
                                value={newIngredient}
                                onChange={(e) => setNewIngredient(e.target.value)}
                                className="h-11"
                                onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), handleAddIngredient())}
                              />
                              <Button type="button" onClick={handleAddIngredient} className="h-11">
                                <Plus className="h-4 w-4" />
                              </Button>
                            </div>
                            {ingredients.length > 0 && (
                              <div className="flex flex-wrap gap-2">
                                {ingredients.map((ingredient, index) => (
                                  <span 
                                    key={index}
                                    className="inline-flex items-center gap-1 px-3 py-1 bg-orange-100 text-orange-800 rounded-full text-sm"
                                  >
                                    {ingredient}
                                    <Button
                                      type="button"
                                      variant="ghost"
                                      size="sm"
                                      className="h-4 w-4 p-0 hover:bg-orange-200"
                                      onClick={() => handleRemoveIngredient(ingredient)}
                                    >
                                      <X className="h-3 w-3" />
                                    </Button>
                                  </span>
                                ))}
                              </div>
                            )}
                          </div>
                        </div>

                        {/* Allergens */}
                        <div>
                          <FormLabel className="text-base font-medium">Allergens</FormLabel>
                          <div className="mt-2 space-y-3">
                            <div className="flex gap-2">
                              <Input
                                placeholder="Enter allergen"
                                value={newAllergen}
                                onChange={(e) => setNewAllergen(e.target.value)}
                                className="h-11"
                                onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), handleAddAllergen())}
                              />
                              <Button type="button" onClick={handleAddAllergen} className="h-11">
                                <Plus className="h-4 w-4" />
                              </Button>
                            </div>
                            {allergens.length > 0 && (
                              <div className="flex flex-wrap gap-2">
                                {allergens.map((allergen, index) => (
                                  <span 
                                    key={index}
                                    className="inline-flex items-center gap-1 px-3 py-1 bg-red-100 text-red-800 rounded-full text-sm"
                                  >
                                    {allergen}
                                    <Button
                                      type="button"
                                      variant="ghost"
                                      size="sm"
                                      className="h-4 w-4 p-0 hover:bg-red-200"
                                      onClick={() => handleRemoveAllergen(allergen)}
                                    >
                                      <X className="h-3 w-3" />
                                    </Button>
                                  </span>
                                ))}
                              </div>
                            )}
                          </div>
                        </div>

                        {/* Spice Level */}
                        <FormField
                          control={form.control}
                          name="spiceLevel"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel className="text-base font-medium">Spice Level</FormLabel>
                              <Select onValueChange={field.onChange} defaultValue={field.value}>
                                <FormControl>
                                  <SelectTrigger className="h-11">
                                    <SelectValue placeholder="Select spice level" />
                                  </SelectTrigger>
                                </FormControl>
                                <SelectContent>
                                  <SelectItem value="mild">Mild</SelectItem>
                                  <SelectItem value="medium">Medium</SelectItem>
                                  <SelectItem value="hot">Hot</SelectItem>
                                  <SelectItem value="extra-hot">Extra Hot</SelectItem>
                                </SelectContent>
                              </Select>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        {/* Dietary Options */}
                        <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                          <FormField
                            control={form.control}
                            name="isVegetarian"
                            render={({ field }) => (
                              <FormItem className="flex flex-row items-center space-x-3 space-y-0">
                                <FormControl>
                                  <input
                                    type="checkbox"
                                    checked={field.value}
                                    onChange={field.onChange}
                                    className="h-4 w-4"
                                  />
                                </FormControl>
                                <FormLabel className="text-base font-medium">Vegetarian</FormLabel>
                              </FormItem>
                            )}
                          />

                          <FormField
                            control={form.control}
                            name="isVegan"
                            render={({ field }) => (
                              <FormItem className="flex flex-row items-center space-x-3 space-y-0">
                                <FormControl>
                                  <input
                                    type="checkbox"
                                    checked={field.value}
                                    onChange={field.onChange}
                                    className="h-4 w-4"
                                  />
                                </FormControl>
                                <FormLabel className="text-base font-medium">Vegan</FormLabel>
                              </FormItem>
                            )}
                          />
                        </div>

                        {/* Nutrition Info */}
                        <FormField
                          control={form.control}
                          name="nutritionInfo"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel className="text-base font-medium">Nutrition Information</FormLabel>
                              <FormControl>
                                <Textarea 
                                  placeholder="Enter nutrition information (optional)" 
                                  rows={3}
                                  {...field} 
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </div>
                    )}

                    <div className="flex gap-3 pt-4">
                      <Button type="submit" className="flex-1">
                        <Plus className="h-4 w-4 mr-2" />
                        {editingProduct ? "Update Product" : "Add Product"}
                      </Button>
                      <Button
                        type="button"
                        variant="outline"
                        onClick={() => setShowAddProductModal(false)}
                      >
                        Cancel
                      </Button>
                    </div>
                  </form>
                </Form>
              </div>
            </DialogContent>
          </Dialog>
        )}
      </div>
    </div>
  );
}

client/src/pages/SellerInventory.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Link } from "wouter";
import { useAuth } from "@/hooks/useAuth";
import {
  Package,
  Plus,
  Edit,
  Trash2,
  Search,
  Filter,
  AlertTriangle,
  TrendingUp,
  TrendingDown,
  Eye,
  BarChart3,
  Download,
  Upload,
} from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useToast } from "@/hooks/use-toast";
import ImageUpload from "@/components/ImageUpload";

const productSchema = z.object({
  name: z.string().min(1, "Product name is required"),
  description: z.string().optional(),
  price: z.string().min(1, "Price is required"),
  originalPrice: z.string().optional(),
  categoryId: z.number().min(1, "Category is required"),
  stock: z.number().min(0, "Stock must be 0 or greater"),
  imageUrl: z.string().optional(),
  images: z
    .array(z.string())
    .min(1, "At least 1 image is required")
    .max(6, "Maximum 6 images allowed"),
  isFastSell: z.boolean().default(false),
  isOnOffer: z.boolean().default(false),
  offerPercentage: z.number().min(0).max(100).default(0),
  offerEndDate: z.string().optional(),
  // Food-specific fields
  preparationTime: z.string().optional(),
  ingredients: z.array(z.string()).default([]),
  allergens: z.array(z.string()).default([]),
  spiceLevel: z.string().optional(),
  isVegetarian: z.boolean().default(false),
  isVegan: z.boolean().default(false),
  nutritionInfo: z.string().optional(),
});

type ProductForm = z.infer<typeof productSchema>;

interface Product {
  id: number;
  name: string;
  description?: string;
  price: string;
  originalPrice?: string;
  stock: number;
  images?: string[];
  categoryId: number;
  storeId: number;
  isActive: boolean;
  isFastSell?: boolean;
  isOnOffer?: boolean;
  offerPercentage?: number;
  productType?: string;
  preparationTime?: string;
  spiceLevel?: string;
  isVegetarian?: boolean;
  isVegan?: boolean;
}

export default function InventoryManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [stockFilter, setStockFilter] = useState("all");
  const [editingProduct, setEditingProduct] = useState<Product | null>(null);
  const [showAddProduct, setShowAddProduct] = useState(false);

  const { user } = useAuth();

  // Form for adding/editing products
  const productForm = useForm<ProductForm>({
    resolver: zodResolver(productSchema),
    defaultValues: {
      name: "",
      description: "",
      price: "",
      originalPrice: "",
      categoryId: 1,
      stock: 0,
      imageUrl: "",
      images: [],
      isFastSell: false,
      isOnOffer: false,
      offerPercentage: 0,
      offerEndDate: "",
      preparationTime: "",
      ingredients: [],
      allergens: [],
      spiceLevel: "",
      isVegetarian: false,
      isVegan: false,
      nutritionInfo: "",
    },
  });

  // Categories query
  const { data: categories = [] } = useQuery({
    queryKey: ["/api/categories"],
  });

  // Store query to get current store info
  const { data: stores = [], isLoading: storesLoading, error: storesError } = useQuery({
    queryKey: [`/api/stores/owner`, user?.id],
    queryFn: async () => {
      if (!user?.id) return [];

      try {
        const response = await fetch(`/api/stores/owner/${user.id}`);
        if (!response.ok) {
          if (response.status === 404) {
            return []; // No stores found
          }
          throw new Error(`Failed to fetch stores: ${response.status}`);
        }
        const storesData = await response.json();
        console.log('Stores fetched for user:', user.id, 'Count:', storesData.length);
        return Array.isArray(storesData) ? storesData : [];
      } catch (error) {
        console.error('Store fetch error:', error);
        throw error;
      }
    },
    enabled: !!user && user.role === 'shopkeeper' && user.status === 'active',
  });

  const currentStore = stores[0]; // Assuming one store per shopkeeper

  // Products query
  const { data: products = [], isLoading: productsLoading, error: productsError } = useQuery<
    Product[]
  >({
    queryKey: [`/api/products/store/${currentStore?.id}`],
    queryFn: async () => {
      if (!currentStore?.id) return [];

      try {
        const response = await fetch(`/api/products/store/${currentStore.id}`);
        if (!response.ok) {
          if (response.status === 404) {
            return []; // No products found
          }
          throw new Error(`Failed to fetch store products: ${response.status}`);
        }
        const productsData = await response.json();
        console.log('Products fetched for store:', currentStore.id, 'Count:', productsData.length);
        return Array.isArray(productsData) ? productsData : [];
      } catch (error) {
        console.error('Products fetch error:', error);
        throw error;
      }
    },
    enabled: !!currentStore,
  });

  // Filter products
  const filteredProducts = products.filter((product) => {
    const matchesSearch = product.name
      .toLowerCase()
      .includes(searchTerm.toLowerCase());
    const matchesCategory =
      selectedCategory === "all" ||
      product.categoryId.toString() === selectedCategory;
    const matchesStock =
      stockFilter === "all" ||
      (stockFilter === "low" && product.stock <= 5) ||
      (stockFilter === "out" && product.stock === 0) ||
      (stockFilter === "available" && product.stock > 5);

    return matchesSearch && matchesCategory && matchesStock;
  });

  // Low stock products
  const lowStockProducts = products.filter(
    (product) => product.stock <= 5 && product.stock > 0,
  );
  const outOfStockProducts = products.filter((product) => product.stock === 0);

  // Product management functions
  const handleAddProduct = async (data: ProductForm) => {
    if (!currentStore) return;

    try {
      const productData = {
        ...data,
        storeId: currentStore.id,
        price: data.price,
        originalPrice: data.originalPrice || undefined,
        images: data.images || [],
        imageUrl: data.images?.[0] || undefined,
        isFastSell: data.isFastSell || false,
        isOnOffer: data.isOnOffer || false,
        offerPercentage: data.offerPercentage || 0,
        offerEndDate: data.offerEndDate || undefined,
        productType:
          currentStore.storeType === "restaurant" ? "food" : "retail",
        // Food-specific fields for restaurants
        preparationTime:
          currentStore.storeType === "restaurant"
            ? data.preparationTime || null
            : null,
        ingredients:
          currentStore.storeType === "restaurant" ? data.ingredients || [] : [],
        allergens:
          currentStore.storeType === "restaurant" ? data.allergens || [] : [],
        spiceLevel:
          currentStore.storeType === "restaurant"
            ? data.spiceLevel || null
            : null,
        isVegetarian:
          currentStore.storeType === "restaurant"
            ? data.isVegetarian || false
            : false,
        isVegan:
          currentStore.storeType === "restaurant"
            ? data.isVegan || false
            : false,
        nutritionInfo:
          currentStore.storeType === "restaurant"
            ? data.nutritionInfo || null
            : null,
      };

      if (editingProduct) {
        const response = await fetch(`/api/products/${editingProduct.id}`, {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(productData),
        });
        if (!response.ok) throw new Error("Failed to update product");
        toast({ title: "Product updated successfully" });
      } else {
        const response = await fetch("/api/products", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(productData),
        });
        if (!response.ok) throw new Error("Failed to create product");
        toast({ title: "Product added successfully" });
      }

      productForm.reset();
      setEditingProduct(null);
      setShowAddProduct(false);
      // Invalidate queries to refresh data
      queryClient.invalidateQueries({
        queryKey: [`/api/products/store/${currentStore.id}`],
      });
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
    } catch (error) {
      toast({
        title: "Error",
        description:
          error instanceof Error ? error.message : "Failed to save product",
        variant: "destructive",
      });
    }
  };

  const handleEditProduct = (product: Product) => {
    setEditingProduct(product);
    productForm.reset({
      name: product.name,
      description: product.description || "",
      price: product.price,
      originalPrice: product.originalPrice || "",
      categoryId: product.categoryId || 1,
      stock: product.stock || 0,
      imageUrl: product.images?.[0] || "",
      images: product.images || [],
      isFastSell: product.isFastSell || false,
      isOnOffer: product.isOnOffer || false,
      offerPercentage: product.offerPercentage || 0,
      offerEndDate: "",
      preparationTime: product.preparationTime || "",
      spiceLevel: product.spiceLevel || "",
      isVegetarian: product.isVegetarian || false,
      isVegan: product.isVegan || false,
    });
    setShowAddProduct(true);
  };

  const handleDeleteProduct = async (productId: number) => {
    if (!confirm("Are you sure you want to delete this product?")) return;

    try {
      const response = await fetch(`/api/products/${productId}`, {
        method: "DELETE",
      });
      if (!response.ok) throw new Error("Failed to delete product");
      toast({ title: "Product deleted successfully" });
      // Invalidate queries to refresh data
      if (currentStore) {
        queryClient.invalidateQueries({
          queryKey: [`/api/products/store/${currentStore.id}`],
        });
        queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to delete product",
        variant: "destructive",
      });
    }
  };

  if (!user || user.role !== "shopkeeper") {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50 dark:bg-gray-900">
        <Card className="w-full max-w-md">
          <CardHeader>
            <CardTitle className="text-center text-red-600">
              Access Denied
            </CardTitle>
          </CardHeader>
          <CardContent>
            <p className="text-center text-muted-foreground">
              You need to be a shopkeeper to access inventory management.
            </p>
            <div className="mt-4 text-center">
              <Link href="/" className="text-primary hover:underline">
                Go back to homepage
              </Link>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  // Check if shopkeeper is approved by admin
  if (user.role === 'shopkeeper' && user.status !== 'active') {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50 dark:bg-gray-900">
        <Card className="w-full max-w-lg">
          <CardHeader>
            <CardTitle className="text-center text-yellow-600">
              Pending Admin Approval
            </CardTitle>
          </CardHeader>
          <CardContent className="text-center space-y-4">
            <div className="mx-auto w-16 h-16 rounded-full bg-yellow-100 dark:bg-yellow-900/20 flex items-center justify-center">
              <Package className="h-8 w-8 text-yellow-600" />
            </div>
            <p className="text-muted-foreground">
              Your seller account is pending approval from our admin team. You cannot access inventory management until approved.
            </p>
            <div className="flex flex-col sm:flex-row gap-3 justify-center">
              <Link href="/">
                <Button variant="outline">Go to Homepage</Button>
              </Link>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  // Handle loading states
  if (storesLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50 dark:bg-gray-900">
        <Card className="w-full max-w-md">
          <CardContent className="pt-6 text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
            <p className="text-muted-foreground">Loading your store information...</p>
          </CardContent>
        </Card>
      </div>
    );
  }

  // Handle store errors
  if (storesError) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50 dark:bg-gray-900">
        <Card className="w-full max-w-md">
          <CardHeader>
            <CardTitle className="text-center text-red-600">Error Loading Store</CardTitle>
          </CardHeader>
          <CardContent className="text-center">
            <p className="text-muted-foreground mb-4">
              Failed to load your store information. Please try again.
            </p>
            <Button onClick={() => window.location.reload()}>Retry</Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (!currentStore) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50 dark:bg-gray-900">
        <Card className="w-full max-w-md">
          <CardHeader>
            <CardTitle className="text-center">No Store Found</CardTitle>
          </CardHeader>
          <CardContent className="text-center">
            <p className="text-muted-foreground mb-4">
              You need to create a store before managing inventory.
            </p>
            <Link href="/seller/store">
              <Button>Create Store</Button>
            </Link>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      {/* Header */}
      <div className="bg-white dark:bg-gray-800 shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-3 sm:px-4 lg:px-8">
          <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center py-3 sm:py-4 gap-3 sm:gap-0">
            <div className="flex items-center space-x-2 sm:space-x-3">
              <Package className="h-6 w-6 sm:h-8 sm:w-8 text-primary" />
              <div>
                <h1 className="text-lg sm:text-2xl font-bold text-gray-900 dark:text-white">
                  Inventory Management
                </h1>
                <p className="text-xs sm:text-sm text-muted-foreground">
                  {currentStore.name}
                </p>
              </div>
            </div>
            <div className="flex items-center space-x-2 sm:space-x-3 w-full sm:w-auto">
              <Button
                onClick={() => setShowAddProduct(true)}
                className="bg-primary flex-1 sm:flex-none text-sm"
              >
                <Plus className="h-4 w-4 mr-2" />
                Add Product
              </Button>
              <Button variant="outline" size="sm" className="hidden sm:flex">
                <Download className="h-4 w-4 mr-2" />
                Export
              </Button>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-3 sm:px-4 lg:px-8 py-4 sm:py-8">
        {/* Inventory Overview */}
        <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 sm:gap-6 mb-6 sm:mb-8">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2 p-3 sm:p-6">
              <CardTitle className="text-xs sm:text-sm font-medium">
                Total Products
              </CardTitle>
              <Package className="h-3 w-3 sm:h-4 sm:w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent className="p-3 pt-0 sm:p-6 sm:pt-0">
              <div className="text-lg sm:text-2xl font-bold">{products.length}</div>
              <p className="text-xs text-muted-foreground">
                <TrendingUp className="h-3 w-3 inline mr-1" />
                Active inventory items
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2 p-3 sm:p-6">
              <CardTitle className="text-xs sm:text-sm font-medium">
                Low Stock Alert
              </CardTitle>
              <AlertTriangle className="h-3 w-3 sm:h-4 sm:w-4 text-orange-500" />
            </CardHeader>
            <CardContent className="p-3 pt-0 sm:p-6 sm:pt-0">
              <div className="text-lg sm:text-2xl font-bold text-orange-600">
                {lowStockProducts.length}
              </div>
              <p className="text-xs text-muted-foreground">
                Items with ≤5 units remaining
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2 p-3 sm:p-6">
              <CardTitle className="text-xs sm:text-sm font-medium">
                Out of Stock
              </CardTitle>
              <TrendingDown className="h-3 w-3 sm:h-4 sm:w-4 text-red-500" />
            </CardHeader>
            <CardContent className="p-3 pt-0 sm:p-6 sm:pt-0">
              <div className="text-lg sm:text-2xl font-bold text-red-600">
                {outOfStockProducts.length}
              </div>
              <p className="text-xs text-muted-foreground">
                Items requiring restocking
              </p>
            </CardContent>
          </Card>
        </div>

        {/* Filters and Search */}
        <Card className="mb-4 sm:mb-6">
          <CardHeader className="p-3 sm:p-6">
            <CardTitle className="text-lg sm:text-xl">Product Management</CardTitle>
          </CardHeader>
          <CardContent className="p-3 sm:p-6">
            <div className="flex flex-col gap-3 sm:gap-4 mb-4 sm:mb-6">
              <div className="flex-1">
                <Input
                  placeholder="Search products..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="w-full text-sm"
                />
              </div>
              <div className="flex gap-2 sm:gap-4 flex-col sm:flex-row">
                <Select
                  value={selectedCategory}
                  onValueChange={setSelectedCategory}
                >
                  <SelectTrigger className="w-full sm:w-48">
                    <SelectValue placeholder="All Categories" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Categories</SelectItem>
                    {Array.isArray(categories) &&
                      categories.map((category: any) => (
                        <SelectItem
                          key={category.id}
                          value={category.id.toString()}
                        >
                          {category.name}
                        </SelectItem>
                      ))}
                  </SelectContent>
                </Select>
                <Select value={stockFilter} onValueChange={setStockFilter}>
                  <SelectTrigger className="w-full sm:w-48">
                    <SelectValue placeholder="Stock Status" />
                  </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Stock</SelectItem>
                  <SelectItem value="available">Available ({">"}5)</SelectItem>
                  <SelectItem value="low">Low Stock (≤5)</SelectItem>
                  <SelectItem value="out">Out of Stock</SelectItem>
                </SelectContent>
                </Select>
              </div>
            </div>

            {/* Products List */}
            <div className="space-y-3 sm:space-y-4">
              {filteredProducts.length > 0 ? (
                filteredProducts.map((product) => (
                  <div
                    key={product.id}
                    className="flex flex-col sm:flex-row sm:items-center justify-between p-3 sm:p-4 border rounded-lg hover:shadow-md transition-shadow gap-3 sm:gap-0"
                  >
                    <div className="flex items-center space-x-3 sm:space-x-4">
                      <div className="w-12 h-12 sm:w-16 sm:h-16 bg-gray-100 rounded-lg overflow-hidden flex-shrink-0">
                        {product.images?.[0] ? (
                          <img
                            src={product.images[0]}
                            alt={product.name}
                            className="w-full h-full object-cover"
                          />
                        ) : (
                          <div className="w-full h-full flex items-center justify-center">
                            <Package className="h-4 w-4 sm:h-6 sm:w-6 text-gray-400" />
                          </div>
                        )}
                      </div>
                      <div className="flex-1 min-w-0">
                        <h3 className="font-medium text-sm sm:text-base truncate">{product.name}</h3>
                        <p className="text-sm text-muted-foreground">
                          ₹{product.price}
                        </p>
                        <div className="flex items-center gap-2 mt-1 flex-wrap">
                          <Badge
                            variant={
                              product.stock === 0
                                ? "destructive"
                                : product.stock <= 5
                                  ? "secondary"
                                  : "default"
                            }
                            className="text-xs"
                          >
                            Stock: {product.stock}
                          </Badge>
                          {product.isOnOffer && (
                            <Badge variant="outline" className="text-green-600 text-xs">
                              {product.offerPercentage}% OFF
                            </Badge>
                          )}
                          {product.isFastSell && (
                            <Badge variant="outline" className="text-blue-600 text-xs">
                              Fast Sell
                            </Badge>
                          )}
                        </div>
                      </div>
                    </div>
                    <div className="flex items-center space-x-2 justify-end sm:justify-start">
                      <Button
                        onClick={() => handleEditProduct(product)}
                        variant="outline"
                        size="sm"
                        className="flex-1 sm:flex-none"
                      >
                        <Edit className="h-4 w-4" />
                        <span className="ml-1 sm:hidden">Edit</span>
                      </Button>
                      <Button
                        onClick={() => handleDeleteProduct(product.id)}
                        variant="destructive"
                        size="sm"
                        className="flex-1 sm:flex-none"
                      >
                        <Trash2 className="h-4 w-4" />
                        <span className="ml-1 sm:hidden">Delete</span>
                      </Button>
                    </div>
                  </div>
                ))
              ) : (
                <div className="text-center py-6 sm:py-8">
                  <Package className="h-10 w-10 sm:h-12 sm:w-12 text-gray-400 mx-auto mb-4" />
                  <p className="text-muted-foreground text-sm sm:text-base">
                    {searchTerm ||
                    selectedCategory !== "all" ||
                    stockFilter !== "all"
                      ? "No products match your filters"
                      : "No products in inventory"}
                  </p>
                  {!showAddProduct && (
                    <Button
                      onClick={() => setShowAddProduct(true)}
                      className="mt-4 text-sm"
                    >
                      <Plus className="h-4 w-4 mr-2" />
                      Add Your First Product
                    </Button>
                  )}
                </div>
              )}
            </div>
          </CardContent>
        </Card>

        {/* Add/Edit Product Form */}
        {showAddProduct && (
          <Card>
            <CardHeader className="p-3 sm:p-6">
              <CardTitle className="flex items-center gap-2 text-lg sm:text-xl">
                <Package className="h-4 w-4 sm:h-5 sm:w-5" />
                {editingProduct ? "Edit Product" : "Add New Product"}
              </CardTitle>
            </CardHeader>
            <CardContent className="p-3 sm:p-6">
              <Form {...productForm}>
                <form
                  onSubmit={productForm.handleSubmit(handleAddProduct)}
                  className="space-y-6"
                >
                  <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 sm:gap-6">
                    <FormField
                      control={productForm.control}
                      name="name"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Product Name *</FormLabel>
                          <FormControl>
                            <Input
                              placeholder="Enter product name"
                              {...field}
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={productForm.control}
                      name="categoryId"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Category *</FormLabel>
                          <Select
                            onValueChange={(value) =>
                              field.onChange(parseInt(value))
                            }
                            defaultValue={field.value?.toString()}
                          >
                            <FormControl>
                              <SelectTrigger>
                                <SelectValue placeholder="Select category" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              {Array.isArray(categories) &&
                                categories.map((category: any) => (
                                  <SelectItem
                                    key={category.id}
                                    value={category.id.toString()}
                                  >
                                    {category.name}
                                  </SelectItem>
                                ))}
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <FormField
                    control={productForm.control}
                    name="description"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Description</FormLabel>
                        <FormControl>
                          <Textarea
                            placeholder="Enter product description"
                            className="min-h-24"
                            {...field}
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 sm:gap-6">
                    <FormField
                      control={productForm.control}
                      name="price"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Price *</FormLabel>
                          <FormControl>
                            <Input
                              type="number"
                              placeholder="0.00"
                              {...field}
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={productForm.control}
                      name="originalPrice"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Original Price</FormLabel>
                          <FormControl>
                            <Input
                              type="number"
                              placeholder="0.00"
                              {...field}
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={productForm.control}
                      name="stock"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Stock Quantity *</FormLabel>
                          <FormControl>
                            <Input
                              type="number"
                              placeholder="Enter quantity"
                              {...field}
                              onChange={(e) =>
                                field.onChange(parseInt(e.target.value) || 0)
                              }
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <FormField
                    control={productForm.control}
                    name="images"
                    render={({ field }) => (
                      <FormItem>
                        <ImageUpload
                          label="Product Images"
                          maxImages={6}
                          minImages={1}
                          onImagesChange={field.onChange}
                          initialImages={field.value || []}
                          className="col-span-full"
                        />
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <div className="flex flex-col sm:flex-row justify-end gap-3 sm:gap-4">
                    <Button
                      type="button"
                      variant="outline"
                      onClick={() => {
                        setShowAddProduct(false);
                        setEditingProduct(null);
                        productForm.reset();
                      }}
                      className="order-2 sm:order-1"
                    >
                      Cancel
                    </Button>
                    <Button type="submit" className="order-1 sm:order-2">
                      <Plus className="h-4 w-4 mr-2" />
                      {editingProduct ? "Update Product" : "Add Product"}
                    </Button>
                  </div>
                </form>
              </Form>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

