import { useQuery } from "@tanstack/react-query";
import { Link, useLocation } from "wouter";
import {
  ArrowRight,
  Star,
  MapPin,
  RefreshCw,
  AlertCircle,
  Loader2,
  Sparkles,
  TrendingUp,
  Eye,
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Badge } from "@/components/ui/badge";
import ProductCard from "@/components/ProductCard";
import StoreCard from "@/components/StoreCard";
import { useAuth } from "@/hooks/useAuth";
import { useAppMode } from "@/hooks/useAppMode";
import type { Product, Store } from "@shared/schema";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination, Autoplay } from "swiper/modules";
import { useEffect, useRef, useState } from "react";
import { apiRequest } from "@/lib/queryClient";

// Import Swiper CSS
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";

// Smart Recommendations Response Type
interface SmartRecommendationsResponse {
  products: Product[];
  stores: Store[];
  totalProducts: number;
  totalStores: number;
  isPersonalized: boolean;
}

// Hero slides data
const slider1 = "/assets/slider1.jpg";
const slider2 = "/assets/slider2.jpg";
const slide3 = "/assets/slide3.jpg";

export default function SmartHomepage() {
  const [, setLocation] = useLocation();
  const { user, isLoading } = useAuth();
  const { mode } = useAppMode();

  // Redirect sellers to their dashboard
  useEffect(() => {
    if (!isLoading && user?.role === "shopkeeper") {
      setLocation("/seller/dashboard");
    }
  }, [user, isLoading, setLocation]);

  // Track homepage visit for recommendations
  useEffect(() => {
    const trackVisit = async () => {
      try {
        await apiRequest('/api/recommendations/track', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            userId: user?.id || null,
            page: '/',
            action: 'homepage_visit'
          })
        });
      } catch (error) {
        console.log('Visit tracking failed (non-critical):', error);
      }
    };

    trackVisit();
  }, [user]);

  // Get smart recommendations
  const {
    data: recommendations,
    isLoading: recommendationsLoading,
    error: recommendationsError,
    refetch: refetchRecommendations,
  } = useQuery<SmartRecommendationsResponse>({
    queryKey: [`/api/recommendations/homepage`],
    queryFn: async () => {
      const params = new URLSearchParams({
        mode: mode,
        ...(user?.id && { userId: user.id.toString() })
      });
      return apiRequest(`/api/recommendations/homepage?${params}`);
    },
    retry: 3,
    staleTime: 5 * 60 * 1000, // 5 minutes
  });

  const recommendedProducts = recommendations?.products || [];
  const recommendedStores = recommendations?.stores || [];
  const isPersonalized = recommendations?.isPersonalized || false;

  const categories = mode === "shopping" 
    ? [
        { name: "Electronics", icon: "📱", href: "/products?category=4" },
        { name: "Clothing", icon: "👕", href: "/products?category=5" },
        { name: "Home & Garden", icon: "🏠", href: "/products?category=3" },
        { name: "Books", icon: "📚", href: "/products?category=6" },
      ]
    : [
        { name: "Indian Cuisine", icon: "🍛", href: "/products?category=food&cuisine=indian" },
        { name: "Fast Food", icon: "🍔", href: "/products?category=food&cuisine=fast-food" },
        { name: "Pizza", icon: "🍕", href: "/products?category=food&type=pizza" },
        { name: "Desserts", icon: "🍰", href: "/products?category=food&cuisine=desserts" },
      ];

  // Error and Loading States
  const ErrorState = ({ onRetry }: { onRetry: () => void }) => (
    <Alert className="mb-6">
      <AlertCircle className="h-4 w-4" />
      <AlertDescription className="flex items-center justify-between">
        <span>Unable to load recommendations. Please try again.</span>
        <Button
          variant="outline"
          size="sm"
          onClick={onRetry}
          className="ml-4"
        >
          <RefreshCw className="h-3 w-3 mr-1" />
          Retry
        </Button>
      </AlertDescription>
    </Alert>
  );

  const LoadingState = () => (
    <div className="space-y-6">
      <div className="animate-pulse">
        <div className="h-8 bg-gray-200 rounded w-1/3 mb-4"></div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          {[...Array(8)].map((_, i) => (
            <div key={i} className="h-64 bg-gray-200 rounded"></div>
          ))}
        </div>
      </div>
    </div>
  );

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background">
        <div className="flex items-center justify-center py-20">
          <Loader2 className="h-8 w-8 animate-spin" />
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Hero Section */}
      <section className="relative overflow-hidden bg-gradient-to-r from-blue-600 to-purple-700">
        <div className="container mx-auto px-4 py-12 sm:py-16 lg:py-20">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 items-center">
            <div className="text-white space-y-6">
              <h1 className="text-3xl sm:text-4xl lg:text-5xl font-bold leading-tight">
                Welcome to Siraha Bazaar
                {isPersonalized && (
                  <Badge className="ml-3 bg-yellow-500 text-black">
                    <Sparkles className="h-3 w-3 mr-1" />
                    Personalized
                  </Badge>
                )}
              </h1>
              <p className="text-lg sm:text-xl text-blue-100 max-w-lg">
                {mode === "shopping" 
                  ? "Discover amazing products from local stores with smart recommendations tailored just for you."
                  : "Explore delicious food from the best restaurants with personalized dining suggestions."
                }
              </p>
              <div className="flex flex-col sm:flex-row gap-4">
                <Link href={mode === "shopping" ? "/products" : "/restaurants"}>
                  <Button size="lg" className="bg-white text-blue-700 hover:bg-blue-50">
                    Start {mode === "shopping" ? "Shopping" : "Ordering"}
                    <ArrowRight className="ml-2 h-4 w-4" />
                  </Button>
                </Link>
                {user && (
                  <Button variant="outline" size="lg" className="border-white text-white hover:bg-white hover:text-blue-700">
                    <Eye className="mr-2 h-4 w-4" />
                    View My Activity
                  </Button>
                )}
              </div>
            </div>

            {/* Hero Slider */}
            <div className="relative">
              <Swiper
                modules={[Navigation, Pagination, Autoplay]}
                spaceBetween={0}
                slidesPerView={1}
                navigation
                pagination={{ clickable: true }}
                autoplay={{ delay: 4000, disableOnInteraction: false }}
                className="rounded-xl overflow-hidden shadow-2xl"
              >
                <SwiperSlide>
                  <img 
                    src={slider1} 
                    alt="Featured Products" 
                    className="w-full h-64 sm:h-80 object-cover"
                  />
                </SwiperSlide>
                <SwiperSlide>
                  <img 
                    src={slider2} 
                    alt="Local Stores" 
                    className="w-full h-64 sm:h-80 object-cover"
                  />
                </SwiperSlide>
                <SwiperSlide>
                  <img 
                    src={slide3} 
                    alt="Fast Delivery" 
                    className="w-full h-64 sm:h-80 object-cover"
                  />
                </SwiperSlide>
              </Swiper>
            </div>
          </div>
        </div>
      </section>

      {/* Categories Section */}
      <section className="py-12 sm:py-16 bg-secondary/30">
        <div className="container mx-auto px-4">
          <h2 className="text-2xl sm:text-3xl font-bold text-center mb-8 sm:mb-12">
            Browse Categories
          </h2>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 sm:gap-6">
            {categories.map((category) => (
              <Link key={category.name} href={category.href}>
                <Card className="group hover:shadow-lg transition-all duration-200">
                  <CardContent className="p-6 text-center">
                    <div className="text-4xl mb-3">{category.icon}</div>
                    <h3 className="font-semibold text-sm sm:text-base group-hover:text-primary">
                      {category.name}
                    </h3>
                  </CardContent>
                </Card>
              </Link>
            ))}
          </div>
        </div>
      </section>

      {/* Smart Recommendations Section */}
      <section className="py-12 sm:py-16">
        <div className="container mx-auto px-4">
          {recommendationsError ? (
            <ErrorState onRetry={refetchRecommendations} />
          ) : recommendationsLoading ? (
            <LoadingState />
          ) : (
            <>
              {/* Recommended Products */}
              <div className="mb-12">
                <div className="flex flex-col sm:flex-row sm:justify-between sm:items-center mb-8 gap-4">
                  <div className="flex items-center gap-2">
                    <h2 className="text-2xl sm:text-3xl font-bold">
                      {isPersonalized ? "Recommended for You" : "Featured Products"}
                    </h2>
                    {isPersonalized && (
                      <Badge variant="secondary" className="bg-blue-100 text-blue-800">
                        <TrendingUp className="h-3 w-3 mr-1" />
                        Smart Pick
                      </Badge>
                    )}
                  </div>
                  <Link href="/products">
                    <Button variant="outline" className="border-primary text-primary hover:bg-primary hover:text-white">
                      View All ({recommendations?.totalProducts || 0})
                      <ArrowRight className="ml-2 h-4 w-4" />
                    </Button>
                  </Link>
                </div>

                {recommendedProducts.length === 0 ? (
                  <div className="text-center py-12">
                    <p className="text-muted-foreground text-lg mb-4">
                      No products available at the moment
                    </p>
                    <Button variant="outline" onClick={refetchRecommendations}>
                      <RefreshCw className="mr-2 h-4 w-4" />
                      Refresh
                    </Button>
                  </div>
                ) : (
                  <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 sm:gap-6">
                    {recommendedProducts.map((product) => (
                      <ProductCard key={product.id} product={product} />
                    ))}
                  </div>
                )}
              </div>

              {/* Recommended Stores */}
              <div>
                <div className="flex flex-col sm:flex-row sm:justify-between sm:items-center mb-8 gap-4">
                  <div className="flex items-center gap-2">
                    <h2 className="text-2xl sm:text-3xl font-bold">
                      {isPersonalized ? "Stores You Might Like" : `Popular ${mode === "shopping" ? "Stores" : "Restaurants"}`}
                    </h2>
                    {isPersonalized && (
                      <Badge variant="secondary" className="bg-green-100 text-green-800">
                        <Sparkles className="h-3 w-3 mr-1" />
                        Curated
                      </Badge>
                    )}
                  </div>
                  <Link href={mode === "shopping" ? "/stores" : "/restaurants"}>
                    <Button variant="outline" className="border-primary text-primary hover:bg-primary hover:text-white">
                      View All ({recommendations?.totalStores || 0})
                      <ArrowRight className="ml-2 h-4 w-4" />
                    </Button>
                  </Link>
                </div>

                {recommendedStores.length === 0 ? (
                  <div className="text-center py-12">
                    <p className="text-muted-foreground text-lg mb-4">
                      No {mode === "shopping" ? "stores" : "restaurants"} available at the moment
                    </p>
                    <Button variant="outline" onClick={refetchRecommendations}>
                      <RefreshCw className="mr-2 h-4 w-4" />
                      Refresh
                    </Button>
                  </div>
                ) : (
                  <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-4 sm:gap-6">
                    {recommendedStores.map((store) => (
                      <StoreCard key={store.id} store={store} />
                    ))}
                  </div>
                )}
              </div>
            </>
          )}
        </div>
      </section>

      {/* Call to Action Section */}
      <section className="py-12 sm:py-16 bg-gradient-to-r from-blue-600 to-purple-700">
        <div className="container mx-auto px-4 text-center text-white">
          <h2 className="text-2xl sm:text-3xl font-bold mb-4">
            Ready to Start {mode === "shopping" ? "Shopping" : "Ordering"}?
          </h2>
          <p className="text-lg text-blue-100 mb-8 max-w-2xl mx-auto">
            {mode === "shopping" 
              ? "Join thousands of happy customers shopping from local stores in Siraha with fast delivery."
              : "Join thousands of satisfied customers ordering delicious food from local restaurants with quick delivery."
            }
          </p>
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <Link href={mode === "shopping" ? "/products" : "/restaurants"}>
              <Button size="lg" className="bg-white text-blue-700 hover:bg-blue-50">
                Browse {mode === "shopping" ? "Products" : "Menu"}
                <ArrowRight className="ml-2 h-4 w-4" />
              </Button>
            </Link>
            {!user && (
              <Link href="/register">
                <Button variant="outline" size="lg" className="border-white text-white hover:bg-white hover:text-blue-700">
                  Create Account
                </Button>
              </Link>
            )}
          </div>
        </div>
      </section>
    </div>
  );
}

client/src/pages/SpecialOffers.tsx.content:
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardFooter, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Skeleton } from "@/components/ui/skeleton";
import { useLocation } from "wouter";
import { useAppMode } from "@/hooks/useAppMode";
import { useCart } from "@/hooks/useCart";
import { useToast } from "@/hooks/use-toast";
import { 
  TrendingUp, 
  Search, 
  Clock, 
  MapPin, 
  Star,
  Percent,
  Filter,
  SortAsc,
  Leaf,
  Utensils,
  Package
} from "lucide-react";
import type { Product, Store } from "@shared/schema";

interface ProductWithStore extends Product {
  store?: Store;
}

export default function SpecialOffers() {
  const [, setLocation] = useLocation();
  const { mode } = useAppMode();
  const { selectSingleItem } = useCart();
  const { toast } = useToast();
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState<"discount" | "price" | "name">("discount");

  // Fetch special offers
  const { data: specialOffers, isLoading, error } = useQuery<ProductWithStore[]>({
    queryKey: ['/api/products/special-offers'],
    queryFn: async () => {
      const response = await fetch('/api/products/special-offers');
      if (!response.ok) throw new Error('Failed to fetch special offers');
      const offers = await response.json();

      // Get store information for each product
      const offersWithStores = await Promise.all(
        offers.map(async (product: Product) => {
          try {
            const storeResponse = await fetch(`/api/stores/${product.storeId}`);
            if (storeResponse.ok) {
              const store = await storeResponse.json();
              return { ...product, store };
            }
          } catch (error) {
            console.error('Error fetching store:', error);
          }
          return product;
        })
      );

      return offersWithStores;
    },
  });

  // Filter offers based on app mode and search
  const filteredOffers = specialOffers?.filter(product => {
    const matchesMode = mode === 'food' ? 
      (product.productType === 'food' || product.store?.storeType === 'restaurant') :
      (product.productType === 'retail' || product.store?.storeType === 'retail');

    const matchesSearch = !searchQuery || 
      product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      product.description?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      product.store?.name.toLowerCase().includes(searchQuery.toLowerCase());

    return matchesMode && matchesSearch;
  }) || [];

  // Sort offers
  const sortedOffers = [...filteredOffers].sort((a, b) => {
    switch (sortBy) {
      case "discount":
        return (b.offerPercentage || 0) - (a.offerPercentage || 0);
      case "price":
        return parseFloat(a.price) - parseFloat(b.price);
      case "name":
        return a.name.localeCompare(b.name);
      default:
        return 0;
    }
  });

  // Calculate discount amount
  const calculateDiscountAmount = (product: Product) => {
    if (product.originalPrice) {
      const original = parseFloat(product.originalPrice);
      const current = parseFloat(product.price);
      return (original - current).toFixed(2);
    }
    return "0.00";
  };

  // Handle product click
  const handleProductClick = (product: Product) => {
    if (product.productType === 'food' || product.store?.storeType === 'restaurant') {
      setLocation(`/food/${product.id}`);
    } else {
      setLocation(`/products/${product.id}`);
    }
  };

  if (error) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-red-600 mb-4">Error Loading Special Offers</h1>
          <p className="text-gray-600">Failed to load special offers. Please try again later.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-gradient-to-r from-red-500 to-orange-500 text-white">
        <div className="container mx-auto px-4 py-8">
          <div className="flex items-center gap-3 mb-4">
            <TrendingUp className="h-8 w-8" />
            <h1 className="text-3xl font-bold">
              {mode === 'food' ? 'Food Special Offers' : 'Shopping Special Offers'}
            </h1>
          </div>
          <p className="text-red-100 text-lg">
            {mode === 'food' 
              ? 'Amazing discounts on your favorite restaurant items' 
              : 'Incredible deals on top products - limited time only!'
            }
          </p>

          {/* Search and Filters */}
          <div className="flex flex-col sm:flex-row gap-4 mt-6">
            <div className="relative flex-1">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
              <Input
                type="text"
                placeholder={`Search ${mode === 'food' ? 'food items' : 'products'}...`}
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10 bg-white/10 border-white/20 text-white placeholder:text-red-200"
              />
            </div>
            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value as "discount" | "price" | "name")}
              className="px-4 py-2 rounded-md bg-white/10 border border-white/20 text-white"
            >
              <option value="discount">Sort by Discount</option>
              <option value="price">Sort by Price</option>
              <option value="name">Sort by Name</option>
            </select>
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="container mx-auto px-4 py-8">
        {isLoading ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {Array.from({ length: 8 }).map((_, index) => (
              <Card key={index} className="overflow-hidden">
                <Skeleton className="h-48 w-full" />
                <CardHeader>
                  <Skeleton className="h-4 w-3/4" />
                  <Skeleton className="h-3 w-1/2" />
                </CardHeader>
                <CardContent>
                  <Skeleton className="h-6 w-1/3" />
                </CardContent>
              </Card>
            ))}
          </div>
        ) : sortedOffers.length === 0 ? (
          <div className="text-center py-12">
            <Percent className="h-16 w-16 text-gray-400 mx-auto mb-4" />
            <h2 className="text-2xl font-bold text-gray-600 mb-2">No Special Offers Available</h2>
            <p className="text-gray-500">
              {searchQuery 
                ? 'No offers match your search criteria.' 
                : `No special offers available in ${mode} mode right now.`
              }
            </p>
          </div>
        ) : (
          <>
            {/* Results count */}
            <div className="flex items-center justify-between mb-6">
              <p className="text-gray-600">
                Found {sortedOffers.length} special offer{sortedOffers.length !== 1 ? 's' : ''}
                {searchQuery && ` for "${searchQuery}"`}
              </p>
              <Badge variant="secondary" className="bg-red-100 text-red-800">
                {mode === 'food' ? 'Food Offers' : 'Shopping Offers'}
              </Badge>
            </div>

            {/* Products Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
              {sortedOffers.map((product) => (
                <Card 
                  key={product.id} 
                  className="overflow-hidden hover:shadow-lg transition-all duration-300 cursor-pointer group"
                  onClick={() => handleProductClick(product)}
                >
                  <div className="relative h-48">
                    <img
                      src={(product.images && product.images[0]) || product.imageUrl || "https://images.unsplash.com/photo-1565299624946-b28f40a0ca4b?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&h=300"}
                      alt={product.name}
                      className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
                      onError={(e) => {
                        e.currentTarget.src = "https://images.unsplash.com/photo-1565299624946-b28f40a0ca4b?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&h=300";
                      }}
                    />

                    {/* Discount Badge */}
                    <Badge className="absolute top-2 left-2 bg-red-500 hover:bg-red-600 text-white font-bold">
                      {product.offerPercentage}% OFF
                    </Badge>

                    {/* Product Type Badge */}
                    <Badge className="absolute top-2 right-2 bg-white/90 text-gray-800">
                      {product.productType === 'food' ? (
                        <><Utensils className="h-3 w-3 mr-1" /> Food</>
                      ) : (
                        <><Package className="h-3 w-3 mr-1" /> Product</>
                      )}
                    </Badge>

                    {/* Food specific badges */}
                    {product.productType === 'food' && (
                      <div className="absolute bottom-2 left-2 flex space-x-1">
                        {product.isVegetarian && (
                          <Badge className="bg-green-500 hover:bg-green-600 text-white">
                            <Leaf className="h-3 w-3" />
                          </Badge>
                        )}
                        {product.isVegan && (
                          <Badge className="bg-green-600 hover:bg-green-700 text-white">
                            Vegan
                          </Badge>
                        )}
                      </div>
                    )}
                  </div>

                  <CardHeader className="pb-2">
                    <h3 className="font-semibold text-lg text-gray-900 line-clamp-1">
                      {product.name}
                    </h3>
                    {product.store && (
                      <div className="flex items-center text-sm text-gray-600">
                        <MapPin className="h-3 w-3 mr-1" />
                        {product.store.name}
                      </div>
                    )}
                  </CardHeader>

                  <CardContent className="py-2">
                    <div className="flex items-center justify-between">
                      <div>
                        <div className="flex items-center gap-2">
                          <span className="text-xl font-bold text-green-600">
                            ₹{product.price}
                          </span>
                          {product.originalPrice && (
                            <span className="text-sm text-gray-500 line-through">
                              ₹{product.originalPrice}
                            </span>
                          )}
                        </div>
                        <div className="text-sm text-green-600 font-medium">
                          Save ₹{calculateDiscountAmount(product)}
                        </div>
                      </div>
                      {product.rating && parseFloat(product.rating) > 0 && (
                        <div className="flex items-center">
                          <Star className="h-4 w-4 fill-yellow-400 text-yellow-400" />
                          <span className="text-sm text-gray-600 ml-1">
                            {product.rating}
                          </span>
                        </div>
                      )}
                    </div>

                    {product.offerEndDate && (
                      <div className="flex items-center text-xs text-red-600 mt-2">
                        <Clock className="h-3 w-3 mr-1" />
                        Ends {new Date(product.offerEndDate).toLocaleDateString()}
                      </div>
                    )}
                  </CardContent>

                  <CardFooter className="pt-2">
                    <Button 
                      className="w-full bg-red-500 hover:bg-red-600 text-white"
                      onClick={async (e) => {
                        e.stopPropagation(); // Prevent card click
                        try {
                          if (mode === 'food') {
                            // Select only this item for checkout
                            await selectSingleItem(product.id);
                            toast({
                              title: "Ready for Checkout",
                              description: `${product.name} selected for checkout`,
                            });
                            setLocation("/checkout");
                          } else {
                            // For shopping items, go to product detail
                            setLocation(`/products/${product.id}`);
                          }
                        } catch (error) {
                          toast({
                            title: "Error",
                            description: "Failed to prepare item for checkout",
                            variant: "destructive",
                          });
                        }
                      }}
                    >
                      {mode === 'food' ? 'Order Now' : 'View Deal'}
                    </Button>
                  </CardFooter>
                </Card>
              ))}
            </div>
          </>
        )}
      </div>
    </div>
  );
}

