import { useParams, Link } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { MapPin, Star, Clock, Phone, ArrowRight } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import ProductCard from "@/components/ProductCard";
import StoreReviews from "@/components/StoreReviews";
import type { Store, Product } from "@shared/schema";

export default function StoreDetail() {
  const { id } = useParams();

  const { data: store, isLoading: storeLoading } = useQuery<Store>({
    queryKey: [`/api/stores/${id}`],
    enabled: !!id,
  });

  const { data: products = [], isLoading: productsLoading } = useQuery<Product[]>({
    queryKey: [`/api/products/store/${id}`],
    enabled: !!id,
  });

  if (storeLoading) {
    return (
      <div className="min-h-screen bg-muted flex items-center justify-center">
        <div>Loading store...</div>
      </div>
    );
  }

  if (!store) {
    return (
      <div className="min-h-screen bg-muted flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold mb-4">Store not found</h2>
          <Link href="/">
            <Button>Back to Home</Button>
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-muted">
      <div className="max-w-7xl mx-auto px-4 py-8">
        {/* Store Cover Image */}
        <div className="w-full h-48 md:h-64 bg-gray-200 rounded-lg mb-6 overflow-hidden">
          <img
            src={store.coverImage || (store.storeType === 'restaurant' 
              ? "https://images.unsplash.com/photo-1517248135467-4c7edcad34c4?w=800&h=400&fit=crop&crop=center"
              : "https://images.unsplash.com/photo-1441986300917-64674bd600d8?w=800&h=400&fit=crop&crop=center")}
            alt={`${store.name} cover`}
            className="w-full h-full object-cover"
            onError={(e) => {
              e.currentTarget.src = store.storeType === 'restaurant' 
                ? "https://images.unsplash.com/photo-1517248135467-4c7edcad34c4?w=800&h=400&fit=crop&crop=center"
                : "https://images.unsplash.com/photo-1441986300917-64674bd600d8?w=800&h=400&fit=crop&crop=center";
            }}
          />
        </div>

        {/* Store Header */}
        <Card className="mb-8">
          <CardContent className="p-6">
            <div className="flex flex-col md:flex-row items-start md:items-center space-y-4 md:space-y-0 md:space-x-6">
              {/* Store Logo */}
              <div className="w-32 h-32 bg-gray-200 rounded-lg flex items-center justify-center overflow-hidden flex-shrink-0">
                <img
                  src={store.logo || "https://images.unsplash.com/photo-1560472355-536de3962603?w=400&h=400&fit=crop&crop=center"}
                  alt={store.name}
                  className="w-full h-full object-cover"
                  onError={(e) => {
                    e.currentTarget.src = "https://images.unsplash.com/photo-1560472355-536de3962603?w=400&h=400&fit=crop&crop=center";
                  }}
                />
              </div>

              {/* Store Info */}
              <div className="flex-1">
                <h1 className="text-2xl font-bold text-foreground mb-2">{store.name}</h1>

                <div className="flex items-center mb-3">
                  <div className="flex text-yellow-400 mr-2">
                    {Array.from({ length: 5 }).map((_, i) => (
                      <Star
                        key={i}
                        className={`h-4 w-4 ${
                          i < Math.floor(Number(store.rating)) ? "fill-current" : ""
                        }`}
                      />
                    ))}
                  </div>
                  <span className="text-muted-foreground">
                    {store.rating} ({store.totalReviews} reviews)
                  </span>
                  <Badge variant="secondary" className="ml-3">
                    {store.isActive ? "Open" : "Closed"}
                  </Badge>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-3 text-sm text-muted-foreground mb-4">
                  <div className="flex items-center">
                    <MapPin className="h-4 w-4 mr-2" />
                    <span>{store.address}</span>
                  </div>
                  <div className="flex items-center">
                    <Clock className="h-4 w-4 mr-2" />
                    <span>Open: 7:00 AM - 10:00 PM</span>
                  </div>
                  {store.phone && (
                    <div className="flex items-center">
                      <Phone className="h-4 w-4 mr-2" />
                      <span>{store.phone}</span>
                    </div>
                  )}
                  {store.latitude && store.longitude && (
                    <div className="flex items-center">
                      <MapPin className="h-4 w-4 mr-2 text-blue-500" />
                      <span>Location available</span>
                    </div>
                  )}
                </div>

                {store.description && (
                  <p className="text-muted-foreground mb-4">{store.description}</p>
                )}
              </div>

              {/* Action Buttons */}
              <div className="flex flex-col space-y-3 w-full md:w-auto">
                {store.phone && (
                  <Button 
                    className="btn-primary"
                    onClick={() => window.open(`tel:${store.phone}`, '_self')}
                  >
                    <Phone className="h-4 w-4 mr-2" />
                    Call Store
                  </Button>
                )}

                <Button 
                  variant="outline"
                  onClick={() => {
                    const reviewsSection = document.getElementById('store-reviews');
                    if (reviewsSection) {
                      reviewsSection.scrollIntoView({ behavior: 'smooth' });
                      const rateButton = reviewsSection.querySelector('button[data-rate-store]');
                      if (rateButton) {
                        setTimeout(() => rateButton.click(), 500);
                      }
                    }
                  }}
                >
                  <Star className="h-4 w-4 mr-2" />
                  Rate Store
                </Button>

                {store.latitude && store.longitude && (
                  <Button 
                    variant="outline"
                    onClick={() => {
                      const mapsUrl = `https://www.google.com/maps/dir/?api=1&destination=${store.latitude},${store.longitude}`;
                      window.open(mapsUrl, '_blank');
                    }}
                  >
                    <MapPin className="h-4 w-4 mr-2" />
                    Get Directions
                  </Button>
                )}
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Store Products */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center justify-between">
              <span>Products from {store.name}</span>
              <Badge variant="outline">{products.length} products</Badge>
            </CardTitle>
          </CardHeader>
          <CardContent>
            {productsLoading ? (
              <div className="text-center py-8">Loading products...</div>
            ) : products.length === 0 ? (
              <div className="text-center py-12">
                <p className="text-muted-foreground text-lg">No products available at the moment</p>
                <p className="text-muted-foreground text-sm mt-2">Check back later for new arrivals</p>
              </div>
            ) : (
              <div className="grid grid-cols-3 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-4">
                {products.map((product) => (
                  <ProductCard key={product.id} product={product} />
                ))}
              </div>
            )}
          </CardContent>
        </Card>

        {/* Store Reviews */}
        <div id="store-reviews" className="mt-8">
          <StoreReviews storeId={parseInt(id!)} currentUserId={9} />
        </div>

        {/* Store Features */}
        <Card className="mt-8">
          <CardHeader>
            <CardTitle>Why Shop with {store.name}?</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="text-center p-4">
                <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-3">
                  <Clock className="h-6 w-6 text-green-600" />
                </div>
                <h3 className="font-semibold mb-2">Fast Delivery</h3>
                <p className="text-sm text-muted-foreground">Quick delivery within Siraha area</p>
              </div>

              <div className="text-center p-4">
                <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-3">
                  <Star className="h-6 w-6 text-blue-600" />
                </div>
                <h3 className="font-semibold mb-2">Quality Products</h3>
                <p className="text-sm text-muted-foreground">Fresh and authentic local products</p>
              </div>

              <div className="text-center p-4">
                <div className="w-12 h-12 bg-purple-100 rounded-full flex items-center justify-center mx-auto mb-3">
                  <Phone className="h-6 w-6 text-purple-600" />
                </div>
                <h3 className="font-semibold mb-2">Local Support</h3>
                <p className="text-sm text-muted-foreground">Direct communication with store owner</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/Stores.tsx.content:
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Search, MapPin, Filter, Utensils, Store, RefreshCw, AlertCircle, Loader2, X } from "lucide-react";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from "@/components/ui/collapsible";
import StoreCard from "@/components/StoreCard";
import StoreDistanceFilter from "@/components/StoreDistanceFilter";
import { getCurrentUserLocation } from "@/lib/distance";
import { useToast } from "@/hooks/use-toast";

interface Store {
  id: number;
  name: string;
  description?: string;
  address: string;
  latitude?: string;
  longitude?: string;
  phone?: string;
  website?: string;
  logo?: string;
  coverImage?: string;
  rating: string;
  totalReviews: number;
  storeType: string;
  cuisineType?: string;
  deliveryTime?: string;
  isDeliveryAvailable?: boolean;
}

interface StoreWithDistance extends Store {
  distance: number;
}

export default function Stores() {
  const [location] = useLocation();
  const isRestaurantPage = location.includes('/restaurants');

  const [searchQuery, setSearchQuery] = useState("");
  const [storeTypeFilter, setStoreTypeFilter] = useState(isRestaurantPage ? "restaurant" : "all");
  const [cuisineFilter, setCuisineFilter] = useState("all");
  const [deliveryFilter, setDeliveryFilter] = useState("all");
  const [ratingFilter, setRatingFilter] = useState("all");
  const [userLocation, setUserLocation] = useState<{ latitude: number; longitude: number } | null>(null);
  const [locationEnabled, setLocationEnabled] = useState(false);
  const [distanceFilteredStores, setDistanceFilteredStores] = useState<StoreWithDistance[]>([]);
  const [showDistanceFilter, setShowDistanceFilter] = useState(false);
  const [filtersOpen, setFiltersOpen] = useState(false);
  const { toast } = useToast();

  // Fetch all stores with comprehensive error handling
  const { data: stores = [], isLoading: storesLoading, error: storesError, refetch: refetchStores } = useQuery<Store[]>({
    queryKey: ["/api/stores"],
    retry: 3,
    staleTime: 0, // Always fetch fresh data to ensure ratings update immediately
    refetchOnWindowFocus: true, // Refetch when window regains focus
    refetchOnMount: true, // Always refetch on component mount
  });

  // Enhanced error logging
  if (storesError) {
    console.error("Stores loading failed:", {
      error: storesError,
      message: storesError instanceof Error ? storesError.message : 'Unknown error',
      timestamp: new Date().toISOString()
    });
  }

  // Get user location on component mount
  useEffect(() => {
    getCurrentUserLocation()
      .then((location) => {
        setUserLocation(location);
        setLocationEnabled(true);
      })
      .catch(() => {
        // User denied location access - this is fine, stores will just show without distance sorting
      });
  }, []);

  // Get unique cuisines for filter options
  const availableCuisines = Array.from(new Set(stores
    .filter(store => store.storeType === 'restaurant' && store.cuisineType)
    .map(store => store.cuisineType)
  )).filter(Boolean) as string[];

  // Filter stores based on all criteria and page context
  const baseFilteredStores = stores.filter((store) => {
    // Page-specific filtering: restaurants only on restaurant page, retail stores only on stores page
    if (isRestaurantPage) {
      // On restaurant page, only show restaurants
      if (store.storeType !== "restaurant") return false;
    } else {
      // On stores page, exclude restaurants (only show retail stores)
      if (store.storeType === "restaurant") return false;
    }

    const matchesSearch = store.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         store.description?.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         store.address.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         store.cuisineType?.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesType = storeTypeFilter === "all" || store.storeType === storeTypeFilter;

    const matchesCuisine = cuisineFilter === "all" || store.cuisineType === cuisineFilter;

    const matchesDelivery = deliveryFilter === "all" || 
                           (deliveryFilter === "delivery" && store.isDeliveryAvailable) ||
                           (deliveryFilter === "pickup" && !store.isDeliveryAvailable);

    const matchesRating = ratingFilter === "all" || 
                         (ratingFilter === "4+" && parseFloat(store.rating) >= 4.0) ||
                         (ratingFilter === "3+" && parseFloat(store.rating) >= 3.0);

    return matchesSearch && matchesType && matchesCuisine && matchesDelivery && matchesRating;
  });

  // Calculate distance for all stores if location is enabled
  const calculateDistance = (lat1: number, lon1: number, lat2: number, lon2: number) => {
    const R = 6371; // Earth's radius in km
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = 
      Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * 
      Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  };

  // Add distance to stores and sort by distance (closest first)
  const filteredStores = baseFilteredStores.map(store => {
    const distance = userLocation && store.latitude && store.longitude 
      ? calculateDistance(
          userLocation.latitude,
          userLocation.longitude,
          parseFloat(store.latitude),
          parseFloat(store.longitude)
        )
      : 0;
    return { ...store, distance };
  }).sort((a, b) => a.distance - b.distance);

  const handleGetLocation = async () => {
    try {
      const location = await getCurrentUserLocation();
      setUserLocation(location);
      setLocationEnabled(true);
      setShowDistanceFilter(true);
      toast({
        title: "Location enabled",
        description: "Stores will now show distance from your location",
      });
    } catch (error) {
      toast({
        title: "Location access denied",
        description: "Please allow location access to see distances",
        variant: "destructive",
      });
    }
  };

  // Handle distance filter updates
  const handleDistanceFilterUpdate = (filteredStores: StoreWithDistance[]) => {
    setDistanceFilteredStores(filteredStores);
  };

  if (storesLoading) {
    return (
      <div className="min-h-screen bg-muted flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">Loading stores...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-muted">
      <div className="max-w-7xl mx-auto px-4 py-8">

        {/* Search and Filters */}
        <Card className="mb-8">
          <CardContent className="pt-6">
            <div className="space-y-4">
              {/* Primary Search and Filter Button */}
              <div className="flex flex-col md:flex-row gap-4">
                {/* Search */}
                <div className="flex-1 relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
                  <Input
                    placeholder={isRestaurantPage ? "Search restaurants..." : "Search stores..."}
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="pl-10"
                  />
                </div>

                {/* Filter Button */}
                <Collapsible open={filtersOpen} onOpenChange={setFiltersOpen}>
                  <CollapsibleTrigger asChild>
                    <Button variant="outline" className="w-full md:w-auto">
                      <Filter className="h-4 w-4 mr-2" />
                      Filters
                      {filtersOpen ? <X className="h-4 w-4 ml-2" /> : null}
                    </Button>
                  </CollapsibleTrigger>
                  <CollapsibleContent className="mt-4">
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                      {/* Store Type Filter */}
                      {!isRestaurantPage && (
                        <Select value={storeTypeFilter} onValueChange={setStoreTypeFilter}>
                          <SelectTrigger>
                            <Store className="h-4 w-4 mr-2" />
                            <SelectValue placeholder="All Stores" />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="all">All Stores</SelectItem>
                            <SelectItem value="retail">Retail Stores</SelectItem>
                          </SelectContent>
                        </Select>
                      )}

                      {/* Cuisine Filter (for restaurants) */}
                      {(isRestaurantPage || storeTypeFilter === "restaurant") && availableCuisines.length > 0 && (
                        <Select value={cuisineFilter} onValueChange={setCuisineFilter}>
                          <SelectTrigger>
                            <Utensils className="h-4 w-4 mr-2" />
                            <SelectValue placeholder="All Cuisines" />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="all">All Cuisines</SelectItem>
                            {availableCuisines.map((cuisine) => (
                              <SelectItem key={cuisine} value={cuisine!}>
                                {cuisine}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      )}

                      {/* Delivery Filter */}
                      <Select value={deliveryFilter} onValueChange={setDeliveryFilter}>
                        <SelectTrigger>
                          <MapPin className="h-4 w-4 mr-2" />
                          <SelectValue placeholder="Delivery Options" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="all">All Options</SelectItem>
                          <SelectItem value="delivery">Delivery Available</SelectItem>
                          <SelectItem value="pickup">Pickup Only</SelectItem>
                        </SelectContent>
                      </Select>

                      {/* Rating Filter */}
                      <Select value={ratingFilter} onValueChange={setRatingFilter}>
                        <SelectTrigger>
                          <Filter className="h-4 w-4 mr-2" />
                          <SelectValue placeholder="Minimum Rating" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="all">All Ratings</SelectItem>
                          <SelectItem value="4+">4.0+ Stars</SelectItem>
                          <SelectItem value="3+">3.0+ Stars</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>
                  </CollapsibleContent>
                </Collapsible>

                {/* Location Button */}
                {!locationEnabled && (
                  <Button variant="outline" onClick={handleGetLocation}>
                    <MapPin className="h-4 w-4 mr-2" />
                    Enable Location
                  </Button>
                )}
              </div>

              {/* Location Status */}
              {locationEnabled && (
                <div className="flex items-center gap-2">
                  <MapPin className="h-4 w-4 text-green-500" />
                  <span className="text-sm text-muted-foreground">
                    Location enabled - stores sorted by distance
                  </span>
                </div>
              )}
            </div>
          </CardContent>
        </Card>

        {/* Simple header showing just stores */}
        <div className="mb-6">
          <h2 className="text-xl font-semibold">
            {isRestaurantPage ? "Restaurants" : "Stores"}
          </h2>
        </div>

        {/* Stores Grid */}
        {storesError ? (
          <Card>
            <CardContent className="pt-6 text-center">
              <Alert className="max-w-md mx-auto mb-6">
                <AlertCircle className="h-4 w-4" />
                <AlertDescription>
                  Failed to load stores. Please try again.
                </AlertDescription>
              </Alert>
              <Button 
                onClick={() => refetchStores()} 
                variant="outline"
                disabled={storesLoading}
              >
                <RefreshCw className={`mr-2 h-4 w-4 ${storesLoading ? 'animate-spin' : ''}`} />
                Retry Loading Stores
              </Button>
            </CardContent>
          </Card>
        ) : storesLoading ? (
          <div className={`grid gap-2 sm:gap-4 ${
            isRestaurantPage || storeTypeFilter === "restaurant"
              ? "grid-cols-2 sm:grid-cols-2 md:grid-cols-2 lg:grid-cols-2" 
              : "grid-cols-2 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4"
          }`}>
            {Array.from({ length: 6 }).map((_, index) => (
              <Card key={index} className="animate-pulse">
                <CardContent className="p-3">
                  <div className="bg-gray-200 h-20 rounded-md mb-2"></div>
                  <div className="bg-gray-200 h-3 rounded mb-1"></div>
                  <div className="bg-gray-200 h-2 rounded w-2/3 mb-1"></div>
                  <div className="bg-gray-200 h-2 rounded w-1/2"></div>
                </CardContent>
              </Card>
            ))}
          </div>
        ) : filteredStores.length > 0 ? (
          <div className={`grid gap-2 sm:gap-4 ${
            isRestaurantPage || storeTypeFilter === "restaurant"
              ? "grid-cols-2 sm:grid-cols-2 md:grid-cols-2 lg:grid-cols-2" 
              : "grid-cols-2 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4"
          }`}>
            {filteredStores.map((store) => (
              <StoreCard
                key={store.id}
                store={store}
                showDistance={locationEnabled}
              />
            ))}
          </div>
        ) : (
          <Card>
            <CardContent className="pt-6 text-center">
              <div className="text-muted-foreground mb-4">
                <Search className="h-12 w-12 mx-auto mb-4 opacity-50" />
                <h3 className="text-lg font-semibold mb-2">
                  {isRestaurantPage ? "No restaurants found" : "No stores found"}
                </h3>
                <p>Try adjusting your search criteria or filters to find what you're looking for</p>
              </div>
              <Button variant="outline" onClick={() => {
                setSearchQuery("");
                setStoreTypeFilter(isRestaurantPage ? "restaurant" : "all");
                setCuisineFilter("all");
                setDeliveryFilter("all");
                setRatingFilter("all");
              }}>
                Clear All Filters
              </Button>
            </CardContent>
          </Card>
        )}


      </div>
    </div>
  );
}

client/src/pages/TrackingDemo.tsx.content:
import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { MapPin, Package, Truck, Users, Clock, Navigation } from 'lucide-react';
import { DeliveryTrackingMap } from '../components/tracking/DeliveryTrackingMap';
import { DeliveryTrackingDashboard } from '../components/tracking/DeliveryTrackingDashboard';

interface DeliveryData {
  id: number;
  orderId: number;
  status: string;
  deliveryPartnerId?: number;
  pickupAddress: string;
  deliveryAddress: string;
  createdAt: string;
}

interface PartnerData {
  id: number;
  name: string;
  phone: string;
  vehicleType: string;
  status: string;
  rating: string;
}

interface TrackingData {
  deliveries: DeliveryData[];
  deliveryPartners: PartnerData[];
  orders: any[];
}

export default function TrackingDemo() {
  const [demoData, setDemoData] = useState<TrackingData>({
    deliveries: [],
    deliveryPartners: [],
    orders: []
  });
  const [selectedDeliveryId, setSelectedDeliveryId] = useState<number | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');
  const [demoMode, setDemoMode] = useState<'customer' | 'shopkeeper' | 'delivery_partner'>('customer');

  useEffect(() => {
    loadDemoData();
  }, []);

  const loadDemoData = async () => {
    try {
      // Load real delivery and partner data from the enhanced tracking endpoint
      const trackingRes = await fetch('/api/tracking/demo-data');

      if (trackingRes.ok) {
        const trackingData = await trackingRes.json();

        setDemoData({
          deliveries: trackingData.deliveries || [],
          deliveryPartners: trackingData.deliveryPartners || [],
          orders: trackingData.orders || []
        });

        // Auto-select first active delivery if available
        if (trackingData.deliveries && trackingData.deliveries.length > 0) {
          setSelectedDeliveryId(trackingData.deliveries[0].id);
        }

        console.log('Loaded real tracking data:', {
          deliveries: trackingData.deliveries?.length || 0,
          partners: trackingData.deliveryPartners?.length || 0,
          message: trackingData.message
        });
      } else {
        // Fallback to individual API calls if tracking endpoint fails
        const [deliveriesRes, partnersRes, ordersRes] = await Promise.all([
          fetch('/api/deliveries'),
          fetch('/api/delivery-partners'),
          fetch('/api/orders')
        ]);

        const deliveries = deliveriesRes.ok ? await deliveriesRes.json() : [];
        const partners = partnersRes.ok ? await partnersRes.json() : [];
        const orders = ordersRes.ok ? await ordersRes.json() : [];

        // Filter for active deliveries with assigned partners
        const activeDeliveries = deliveries.filter((delivery: any) => 
          delivery.deliveryPartnerId && 
          ['assigned', 'en_route_pickup', 'picked_up', 'en_route_delivery'].includes(delivery.status)
        );

        setDemoData({
          deliveries: activeDeliveries,
          deliveryPartners: partners.map((partner: any) => ({
            ...partner,
            name: partner.fullName || partner.name,
            realData: true
          })),
          orders: orders
        });

        if (activeDeliveries.length > 0) {
          setSelectedDeliveryId(activeDeliveries[0].id);
        }
      }

      setIsLoading(false);
    } catch (error) {
      console.error('Error loading tracking data:', error);
      setIsLoading(false);
    }
  };

  const createDemoDelivery = async () => {
    try {
      // Create a demo delivery with test data
      const demoDelivery = {
        orderId: Math.floor(Math.random() * 1000) + 100,
        deliveryPartnerId: null,
        deliveryFee: '50.00',
        pickupAddress: 'Tech World Electronics, Mall Road, Kanpur, UP 208001',
        deliveryAddress: 'IIT Kanpur, Kalyanpur, Kanpur, UP 208016',
        estimatedDistance: 12.5,
        estimatedTime: 25,
        specialInstructions: 'Handle with care - Electronics item'
      };

      const response = await fetch('/api/deliveries', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(demoDelivery)
      });

      if (response.ok) {
        const newDelivery = await response.json();
        setDemoData(prev => ({
          ...prev,
          deliveries: [newDelivery, ...prev.deliveries]
        }));
        setSelectedDeliveryId(newDelivery.id);

        // Initialize tracking for the new delivery
        await fetch(`/api/tracking/initialize/${newDelivery.id}`, {
          method: 'POST'
        });
      }
    } catch (error) {
      console.error('Error creating demo delivery:', error);
    }
  };

  const assignDeliveryPartner = async (deliveryId: number, partnerId: number) => {
    try {
      const response = await fetch(`/api/deliveries/${deliveryId}/status`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          status: 'assigned',
          partnerId: partnerId
        })
      });

      if (response.ok) {
        loadDemoData();
      }
    } catch (error) {
      console.error('Error assigning delivery partner:', error);
    }
  };

  const simulateLocationUpdate = async (deliveryId: number) => {
    // Simulate delivery partner movement
    const locations = [
      { lat: 26.4499, lng: 80.3319 }, // Starting point
      { lat: 26.4520, lng: 80.3350 }, // Moving towards pickup
      { lat: 26.4545, lng: 80.3380 }, // At pickup location
      { lat: 26.4580, lng: 80.3420 }, // Moving towards delivery
      { lat: 26.5144, lng: 80.2329 }  // At delivery location (IIT Kanpur)
    ];

    for (let i = 0; i < locations.length; i++) {
      await new Promise(resolve => setTimeout(resolve, 2000)); // 2 second delay

      await fetch('/api/tracking/location', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          deliveryId,
          latitude: locations[i].lat,
          longitude: locations[i].lng,
          speed: Math.random() * 30 + 10, // 10-40 km/h
          heading: Math.random() * 360
        })
      });

      // Update status at key points
      if (i === 2) {
        await fetch('/api/tracking/status', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            deliveryId,
            status: 'picked_up',
            description: 'Order picked up from store'
          })
        });
      } else if (i === 4) {
        await fetch('/api/tracking/status', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            deliveryId,
            status: 'delivered',
            description: 'Order delivered successfully'
          })
        });
      }
    }
  };

  if (isLoading) {
    return (
      <div className="container mx-auto p-6">
        <div className="flex items-center justify-center h-64">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto mb-4"></div>
            <p>Loading tracking demo...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 space-y-6">
      {/* Header */}
      <div className="text-center mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-4">
          Real-Time Order Tracking System
        </h1>
        <p className="text-lg text-gray-600 mb-6">
          Experience live delivery tracking with HERE Maps integration, WebSocket notifications, and comprehensive dashboard views
        </p>

        {/* Feature Highlights */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
          <Card className="p-4">
            <div className="flex items-center space-x-3">
              <MapPin className="h-8 w-8 text-blue-500" />
              <div>
                <h3 className="font-semibold">HERE Maps</h3>
                <p className="text-sm text-gray-600">Live route calculation</p>
              </div>
            </div>
          </Card>
          <Card className="p-4">
            <div className="flex items-center space-x-3">
              <Truck className="h-8 w-8 text-green-500" />
              <div>
                <h3 className="font-semibold">Real-Time Tracking</h3>
                <p className="text-sm text-gray-600">Live location updates</p>
              </div>
            </div>
          </Card>
          <Card className="p-4">
            <div className="flex items-center space-x-3">
              <Users className="h-8 w-8 text-purple-500" />
              <div>
                <h3 className="font-semibold">Multi-User Views</h3>
                <p className="text-sm text-gray-600">Customer, shop, partner</p>
              </div>
            </div>
          </Card>
          <Card className="p-4">
            <div className="flex items-center space-x-3">
              <Clock className="h-8 w-8 text-orange-500" />
              <div>
                <h3 className="font-semibold">Live Notifications</h3>
                <p className="text-sm text-gray-600">WebSocket updates</p>
              </div>
            </div>
          </Card>
        </div>
      </div>

      {/* Demo Controls */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center justify-between">
            <span>Demo Controls</span>
            <div className="flex space-x-2">
              <Button onClick={createDemoDelivery} size="sm">
                Create Demo Delivery
              </Button>
              {selectedDeliveryId && (
                <Button 
                  onClick={() => simulateLocationUpdate(selectedDeliveryId)}
                  variant="outline"
                  size="sm"
                >
                  Simulate Movement
                </Button>
              )}
            </div>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <Label>View Mode</Label>
              <div className="flex space-x-2 mt-2">
                {(['customer', 'shopkeeper', 'delivery_partner'] as const).map((mode) => (
                  <Button
                    key={mode}
                    variant={demoMode === mode ? 'default' : 'outline'}
                    size="sm"
                    onClick={() => setDemoMode(mode)}
                  >
                    {mode.replace('_', ' ').toUpperCase()}
                  </Button>
                ))}
              </div>
            </div>
            <div>
              <Label>Active Deliveries</Label>
              <Badge variant="outline" className="ml-2">
                {demoData.deliveries.length} total
              </Badge>
            </div>
            <div>
              <Label>Available Partners</Label>
              <Badge variant="outline" className="ml-2">
                {demoData.deliveryPartners.length} online
              </Badge>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Main Content Tabs */}
      <Tabs value={activeTab} onValueChange={setActiveTab}>
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="overview">System Overview</TabsTrigger>
          <TabsTrigger value="tracking">Live Tracking</TabsTrigger>
          <TabsTrigger value="partner">Partner Dashboard</TabsTrigger>
          <TabsTrigger value="admin">Admin Panel</TabsTrigger>
        </TabsList>

        <TabsContent value="overview" className="space-y-6">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Current Deliveries */}
            <Card>
              <CardHeader>
                <CardTitle>Active Deliveries</CardTitle>
              </CardHeader>
              <CardContent>
                {demoData.deliveries.length === 0 ? (
                  <div className="text-center py-8">
                    <Package className="h-12 w-12 text-gray-300 mx-auto mb-4" />
                    <p className="text-gray-500">No active deliveries</p>
                    <Button onClick={createDemoDelivery} className="mt-4">
                      Create Demo Delivery
                    </Button>
                  </div>
                ) : (
                  <div className="space-y-3">
                    {demoData.deliveries.slice(0, 5).map((delivery: any) => (
                      <div 
                        key={delivery.id}
                        className={`p-3 border rounded-lg cursor-pointer transition-colors ${
                          selectedDeliveryId === delivery.id 
                            ? 'border-blue-500 bg-blue-50' 
                            : 'border-gray-200 hover:border-gray-300'
                        }`}
                        onClick={() => setSelectedDeliveryId(delivery.id)}
                      >
                        <div className="flex items-center justify-between mb-2">
                          <span className="font-medium">Delivery #{delivery.id}</span>
                          <Badge variant="outline">{delivery.status || 'pending'}</Badge>
                        </div>
                        <p className="text-sm text-gray-600">{delivery.pickupAddress}</p>
                        <p className="text-sm text-gray-600">→ {delivery.deliveryAddress}</p>
                      </div>
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>

            {/* System Status */}
            <Card>
              <CardHeader>
                <CardTitle>System Status</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <span className="text-sm">HERE Maps API</span>
                    <Badge className="bg-green-500 text-white">Connected</Badge>
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-sm">WebSocket Server</span>
                    <Badge className="bg-green-500 text-white">Active</Badge>
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-sm">Real-time Tracking</span>
                    <Badge className="bg-green-500 text-white">Enabled</Badge>
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-sm">Push Notifications</span>
                    <Badge className="bg-green-500 text-white">Ready</Badge>
                  </div>
                </div>

                <div className="mt-6">
                  <h4 className="font-medium mb-3">Key Features</h4>
                  <ul className="text-sm text-gray-600 space-y-1">
                    <li>• Real-time GPS tracking with HERE Maps</li>
                    <li>• WebSocket-based live updates</li>
                    <li>• Multi-user role support (customer, shopkeeper, delivery partner)</li>
                    <li>• Route optimization and ETA calculations</li>
                    <li>• Push notifications for status changes</li>
                    <li>• Google Maps integration for navigation</li>
                    <li>• Comprehensive delivery history</li>
                  </ul>
                </div>
              </CardContent>
            </Card>
          </div>
        </TabsContent>

        <TabsContent value="tracking" className="space-y-6">
          {selectedDeliveryId ? (
            <DeliveryTrackingMap
              deliveryId={selectedDeliveryId}
              userType={demoMode}
              onStatusUpdate={(status) => {
                console.log('Status updated:', status);
              }}
            />
          ) : (
            <Card>
              <CardContent className="p-12 text-center">
                <MapPin className="h-16 w-16 text-gray-300 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">No Delivery Selected</h3>
                <p className="text-gray-500 mb-4">Create a demo delivery to see live tracking in action</p>
                <Button onClick={createDemoDelivery}>
                  Create Demo Delivery
                </Button>
              </CardContent>
            </Card>
          )}
        </TabsContent>

        <TabsContent value="partner" className="space-y-6">
          {demoData.deliveryPartners.length > 0 ? (
            <DeliveryTrackingDashboard partnerId={demoData.deliveryPartners[0].id} />
          ) : (
            <Card>
              <CardContent className="p-12 text-center">
                <Truck className="h-16 w-16 text-gray-300 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">No Delivery Partners Available</h3>
                <p className="text-gray-500">Partner dashboard will show once delivery partners are registered</p>
              </CardContent>
            </Card>
          )}
        </TabsContent>

        <TabsContent value="admin" className="space-y-6">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Delivery Management */}
            <Card>
              <CardHeader>
                <CardTitle>Delivery Management</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {demoData.deliveries.map((delivery: any) => (
                    <div key={delivery.id} className="p-3 border rounded-lg">
                      <div className="flex items-center justify-between mb-2">
                        <span className="font-medium">Delivery #{delivery.id}</span>
                        <Badge variant="outline">{delivery.status || 'pending'}</Badge>
                      </div>

                      {!delivery.deliveryPartnerId && demoData.deliveryPartners.length > 0 && (
                        <Button
                          size="sm"
                          onClick={() => assignDeliveryPartner(delivery.id, demoData.deliveryPartners[0].id)}
                        >
                          Assign Partner
                        </Button>
                      )}
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>

            {/* Analytics */}
            <Card>
              <CardHeader>
                <CardTitle>Analytics Overview</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-2 gap-4">
                  <div className="text-center">
                    <div className="text-2xl font-bold text-blue-600">{demoData.deliveries.length}</div>
                    <div className="text-sm text-gray-600">Total Deliveries</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-green-600">{demoData.deliveryPartners.length}</div>
                    <div className="text-sm text-gray-600">Active Partners</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-purple-600">
                      {demoData.deliveries.filter((d: any) => d.status === 'delivered').length}
                    </div>
                    <div className="text-sm text-gray-600">Completed</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-orange-600">
                      {demoData.deliveries.filter((d: any) => ['assigned', 'en_route_pickup', 'picked_up', 'en_route_delivery'].includes(d.status)).length}
                    </div>
                    <div className="text-sm text-gray-600">In Progress</div>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/pages/Wishlist.tsx.content:
import { Link } from "wouter";
import { Heart, ShoppingCart, Trash2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { useWishlist } from "@/hooks/useWishlist";
import { useCart } from "@/hooks/useCart";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";

export default function Wishlist() {
  const { user } = useAuth();
  const { wishlistItems, removeFromWishlist, isLoading } = useWishlist();
  const { addToCart } = useCart();
  const { toast } = useToast();

  const handleAddToCart = async (productId: number, productName: string) => {
    try {
      await addToCart(productId, 1);
      toast({
        title: "Added to cart",
        description: `${productName} has been added to your cart.`,
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to add item to cart. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleRemoveFromWishlist = async (wishlistItemId: number, productName: string) => {
    try {
      await removeFromWishlist(wishlistItemId);
      toast({
        title: "Removed from wishlist",
        description: `${productName} has been removed from your wishlist.`,
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to remove item from wishlist. Please try again.",
        variant: "destructive",
      });
    }
  };

  if (!user) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
          <div className="text-center">
            <Heart className="h-16 w-16 text-gray-400 mx-auto mb-4" />
            <h1 className="text-2xl font-bold text-gray-900 dark:text-white mb-4">
              Login Required
            </h1>
            <p className="text-gray-600 dark:text-gray-300 mb-6">
              Please login to view your wishlist.
            </p>
            <Link href="/login">
              <Button>Login</Button>
            </Link>
          </div>
        </div>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto"></div>
            <p className="mt-4 text-gray-600 dark:text-gray-300">Loading your wishlist...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">My Wishlist</h1>
          <p className="text-gray-600 dark:text-gray-300 mt-2">
            {wishlistItems.length} {wishlistItems.length === 1 ? 'item' : 'items'} saved for later
