/**
 * Create Sample Store Reviews
 * Creates sample store reviews to test the compact review system
 */

import { Pool } from 'pg';
import dotenv from 'dotenv';

dotenv.config();

const pool = new Pool({
  connectionString: process.env.DATABASE_URL,
  ssl: process.env.NODE_ENV === 'production' ? { rejectUnauthorized: false } : false
});

async function createSampleReviews() {
  const client = await pool.connect();

  try {
    console.log('🌟 Creating sample store reviews...');

    // First, create basic tables if they don't exist
    await client.query(`
      CREATE TABLE IF NOT EXISTS users (
        id SERIAL PRIMARY KEY,
        username VARCHAR(50) UNIQUE NOT NULL,
        email VARCHAR(100) UNIQUE NOT NULL,
        full_name VARCHAR(100),
        role VARCHAR(20) DEFAULT 'customer',
        created_at TIMESTAMP DEFAULT NOW()
      );
    `);

    await client.query(`
      CREATE TABLE IF NOT EXISTS stores (
        id SERIAL PRIMARY KEY,
        name VARCHAR(100) NOT NULL,
        owner_id INTEGER REFERENCES users(id),
        rating VARCHAR(10) DEFAULT '0.0',
        total_reviews INTEGER DEFAULT 0,
        created_at TIMESTAMP DEFAULT NOW()
      );
    `);

    await client.query(`
      CREATE TABLE IF NOT EXISTS store_reviews (
        id SERIAL PRIMARY KEY,
        store_id INTEGER REFERENCES stores(id),
        customer_id INTEGER REFERENCES users(id),
        rating INTEGER CHECK (rating >= 1 AND rating <= 5),
        title TEXT,
        comment TEXT,
        is_verified_purchase BOOLEAN DEFAULT FALSE,
        is_approved BOOLEAN DEFAULT TRUE,
        helpful_count INTEGER DEFAULT 0,
        created_at TIMESTAMP DEFAULT NOW()
      );
    `);

    // Insert sample users
    await client.query(`
      INSERT INTO users (username, email, full_name, role) VALUES
      ('admin', 'admin@sirahabazaar.com', 'Admin User', 'admin'),
      ('john_doe', 'john@example.com', 'John Doe', 'customer'),
      ('jane_smith', 'jane@example.com', 'Jane Smith', 'customer'),
      ('mike_wilson', 'mike@example.com', 'Mike Wilson', 'customer'),
      ('sarah_jones', 'sarah@example.com', 'Sarah Jones', 'customer'),
      ('store_owner', 'owner@example.com', 'Store Owner', 'shopkeeper')
      ON CONFLICT (email) DO NOTHING;
    `);

    // Insert sample stores
    await client.query(`
      INSERT INTO stores (name, owner_id, rating, total_reviews) VALUES
      ('Electronics World', 6, '4.2', 8),
      ('Fashion Hub', 6, '4.5', 12),
      ('Home & Garden', 6, '3.8', 5)
      ON CONFLICT DO NOTHING;
    `);

    // Insert sample store reviews
    await client.query(`
      INSERT INTO store_reviews (store_id, customer_id, rating, title, comment, is_verified_purchase, helpful_count) VALUES
      (1, 2, 5, 'Excellent service!', 'Great selection of electronics and fast delivery. Very satisfied with my purchase.', true, 3),
      (1, 3, 4, 'Good quality products', 'Found exactly what I was looking for. Prices are reasonable and staff is helpful.', true, 2),
      (1, 4, 4, 'Reliable store', 'Have been shopping here for months. Always good experience.', false, 1),
      (1, 5, 5, 'Highly recommended', 'Amazing customer service and product quality. Will definitely shop again!', true, 4),
      (2, 2, 5, 'Love the variety', 'Great fashion collection with latest trends. Fast shipping too!', true, 2),
      (2, 3, 4, 'Good value for money', 'Nice clothes at reasonable prices. Good quality materials.', true, 3),
      (2, 4, 5, 'Trendy and affordable', 'Perfect place for fashion lovers. Great deals and discounts.', false, 1),
      (3, 2, 4, 'Good for home items', 'Nice collection of home decor and garden tools.', true, 2),
      (3, 5, 3, 'Average experience', 'Products are okay but delivery took longer than expected.', false, 0)
      ON CONFLICT DO NOTHING;
    `);

    // Update store ratings based on reviews
    await client.query(`
      UPDATE stores 
      SET rating = (
        SELECT ROUND(AVG(rating)::numeric, 1)::text
        FROM store_reviews 
        WHERE store_id = stores.id AND is_approved = true
      ),
      total_reviews = (
        SELECT COUNT(*)
        FROM store_reviews 
        WHERE store_id = stores.id AND is_approved = true
      );
    `);

    console.log('✅ Sample store reviews created successfully!');

    // Show what was created
    const stores = await client.query('SELECT * FROM stores ORDER BY id');
    console.log('\n📊 Stores created:');
    stores.rows.forEach(store => {
      console.log(`- ${store.name}: ${store.rating} stars (${store.total_reviews} reviews)`);
    });

    const reviews = await client.query(`
      SELECT sr.*, u.full_name, s.name as store_name 
      FROM store_reviews sr
      JOIN users u ON sr.customer_id = u.id
      JOIN stores s ON sr.store_id = s.id
      ORDER BY sr.store_id, sr.id
    `);

    console.log('\n📝 Reviews created:');
    reviews.rows.forEach(review => {
      console.log(`- ${review.store_name}: ${review.rating} stars by ${review.full_name} - "${review.title}"`);
    });

  } catch (error) {
    console.error('❌ Error creating sample reviews:', error);
  } finally {
    client.release();
    await pool.end();
  }
}

createSampleReviews().catch(console.error);

create-sample-store-reviews.js.content:
/**
 * Create Sample Store Reviews Script
 * Adds sample store reviews to demonstrate the review system functionality
 */

import { Pool } from 'pg';
import dotenv from 'dotenv';

// Load environment variables
dotenv.config();

const pool = new Pool({
  connectionString: process.env.DATABASE_URL,
  ssl: process.env.NODE_ENV === 'production' ? { rejectUnauthorized: false } : false
});

async function createSampleStoreReviews() {
  try {
    console.log("🔄 Creating sample store reviews...");

    // Sample store reviews for different stores
    const sampleReviews = [
      // Reviews for Store ID 2 (Taj Restaurant)
      {
        storeId: 2,
        customerId: 3, // Customer user
        rating: 5,
        title: "Excellent food and service!",
        comment: "The biryani was absolutely delicious and the delivery was fast. Highly recommended!",
        isVerifiedPurchase: true,
        isApproved: true,
        helpfulCount: 8
      },
      {
        storeId: 2,
        customerId: 1, // Another customer
        rating: 4,
        title: "Good taste, could be better",
        comment: "Food quality is good but the packaging could be improved. Overall satisfied with the order.",
        isVerifiedPurchase: true,
        isApproved: true,
        helpfulCount: 3
      },
      {
        storeId: 2,
        customerId: 5, // Another customer
        rating: 5,
        title: "Amazing flavors!",
        comment: "The spices and taste are perfect. This has become my go-to restaurant for authentic food.",
        isVerifiedPurchase: true,
        isApproved: true,
        helpfulCount: 12
      },

      // Reviews for Store ID 1 (Another store)
      {
        storeId: 1,
        customerId: 3,
        rating: 4,
        title: "Great products, fast delivery",
        comment: "Quality products and quick delivery. Will order again.",
        isVerifiedPurchase: true,
        isApproved: true,
        helpfulCount: 5
      },
      {
        storeId: 1,
        customerId: 2,
        rating: 5,
        title: "Excellent store!",
        comment: "Amazing collection and very professional service. Highly recommend this store.",
        isVerifiedPurchase: true,
        isApproved: true,
        helpfulCount: 7
      },

      // Reviews for Store ID 4
      {
        storeId: 4,
        customerId: 3,
        rating: 4,
        title: "Good variety",
        comment: "Nice collection of products with reasonable prices. Good shopping experience.",
        isVerifiedPurchase: true,
        isApproved: true,
        helpfulCount: 4
      },
      {
        storeId: 4,
        customerId: 1,
        rating: 3,
        title: "Average experience",
        comment: "Products are okay but delivery took longer than expected. Room for improvement.",
        isVerifiedPurchase: true,
        isApproved: true,
        helpfulCount: 2
      },

      // Reviews for Store ID 5
      {
        storeId: 5,
        customerId: 2,
        rating: 5,
        title: "Outstanding service!",
        comment: "Excellent customer service and high-quality products. Very professional store.",
        isVerifiedPurchase: true,
        isApproved: true,
        helpfulCount: 9
      },
      {
        storeId: 5,
        customerId: 3,
        rating: 4,
        title: "Good store",
        comment: "Reliable store with good products. Happy with my purchases.",
        isVerifiedPurchase: true,
        isApproved: true,
        helpfulCount: 6
      }
    ];

    // Check if store_reviews table exists
    const tableCheck = await pool.query(`
      SELECT EXISTS (
        SELECT FROM information_schema.tables 
        WHERE table_schema = 'public' 
        AND table_name = 'store_reviews'
      );
    `);

    if (!tableCheck.rows[0].exists) {
      console.log("❌ store_reviews table does not exist. Creating it...");

      await pool.query(`
        CREATE TABLE store_reviews (
          id SERIAL PRIMARY KEY,
          store_id INTEGER NOT NULL,
          customer_id INTEGER NOT NULL,
          rating INTEGER NOT NULL CHECK (rating >= 1 AND rating <= 5),
          title VARCHAR(255),
          comment TEXT,
          is_verified_purchase BOOLEAN DEFAULT false,
          is_approved BOOLEAN DEFAULT true,
          helpful_count INTEGER DEFAULT 0,
          order_id INTEGER,
          images TEXT[],
          created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
          updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
        );
      `);

      await pool.query(`
        CREATE INDEX idx_store_reviews_store_id ON store_reviews(store_id);
      `);

      await pool.query(`
        CREATE INDEX idx_store_reviews_customer_id ON store_reviews(customer_id);
      `);

      console.log("✅ store_reviews table created successfully");
    }

    // Clear existing sample reviews (optional - only if we want fresh data)
    // await pool.query('DELETE FROM store_reviews WHERE customer_id IN (1, 2, 3, 5)');

    // Check for existing reviews to avoid duplicates
    const existingReviews = await pool.query('SELECT store_id, customer_id FROM store_reviews');
    const existingCombos = new Set(existingReviews.rows.map(row => `${row.store_id}-${row.customer_id}`));

    let addedCount = 0;
    let skippedCount = 0;

    // Insert sample reviews
    for (const review of sampleReviews) {
      const combo = `${review.storeId}-${review.customerId}`;

      if (existingCombos.has(combo)) {
        console.log(`⚠️  Skipping review for Store ${review.storeId}, Customer ${review.customerId} - already exists`);
        skippedCount++;
        continue;
      }

      try {
        await pool.query(`
          INSERT INTO store_reviews (
            store_id, customer_id, rating, title, comment, 
            is_verified_purchase, is_approved, helpful_count
          ) VALUES ($1, $2, $3, $4, $5, $6, $7, $8)
        `, [
          review.storeId,
          review.customerId,
          review.rating,
          review.title,
          review.comment,
          review.isVerifiedPurchase,
          review.isApproved,
          review.helpfulCount
        ]);

        console.log(`✅ Added review for Store ${review.storeId} by Customer ${review.customerId} (${review.rating}⭐)`);
        addedCount++;
      } catch (error) {
        console.error(`❌ Failed to add review for Store ${review.storeId}:`, error.message);
      }
    }

    // Update store ratings based on reviews
    console.log("\n🔄 Updating store ratings...");

    const stores = await pool.query('SELECT DISTINCT store_id FROM store_reviews');

    for (const store of stores.rows) {
      const storeId = store.store_id;

      // Calculate average rating and total reviews
      const stats = await pool.query(`
        SELECT 
          AVG(rating)::NUMERIC(3,2) as avg_rating,
          COUNT(*) as total_reviews
        FROM store_reviews 
        WHERE store_id = $1 AND is_approved = true
      `, [storeId]);

      const avgRating = stats.rows[0].avg_rating || 0;
      const totalReviews = stats.rows[0].total_reviews || 0;

      // Update stores table
      await pool.query(`
        UPDATE stores 
        SET rating = $1, total_reviews = $2 
        WHERE id = $3
      `, [avgRating, totalReviews, storeId]);

      console.log(`✅ Store ${storeId}: ${avgRating}⭐ (${totalReviews} reviews)`);
    }

    console.log(`\n🎉 Sample store reviews creation completed!`);
    console.log(`📊 Results: ${addedCount} reviews added, ${skippedCount} skipped (already exist)`);

    // Display summary
    const totalReviews = await pool.query('SELECT COUNT(*) as count FROM store_reviews');
    console.log(`📈 Total store reviews in database: ${totalReviews.rows[0].count}`);

  } catch (error) {
    console.error("❌ Error creating sample store reviews:", error);
  } finally {
    await pool.end();
  }
}

createSampleStoreReviews();

