fix-broken-image.js.content:
// Fix broken Unsplash image script
import fetch from 'node-fetch';

async function fixBrokenImage() {
  try {
    console.log('🔍 Finding products with broken image...');

    // Get all products
    const response = await fetch('http://localhost:5000/api/products');
    const products = await response.json();

    // Find products with the broken image
    const brokenImageUrl = 'https://images.unsplash.com/photo-1583391733956-6c78276477e2?w=400&h=400&fit=crop';
    const brokenProducts = products.filter(product => 
      product.images && product.images.some(img => img.includes('photo-1583391733956-6c78276477e2'))
    );

    console.log(`Found ${brokenProducts.length} products with broken image:`, brokenProducts.map(p => p.name));

    // Fix each product
    for (const product of brokenProducts) {
      console.log(`Fixing product: ${product.name} (ID: ${product.id})`);

      // Replace broken image with working one
      const fixedImages = product.images.map(img => {
        if (img.includes('photo-1583391733956-6c78276477e2')) {
          return 'https://images.unsplash.com/photo-1594633313593-bab3825d0caf?w=400&h=400&fit=crop&auto=format';
        }
        return img;
      });

      // Update product
      const updateResponse = await fetch(`http://localhost:5000/api/products/${product.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          ...product,
          images: fixedImages
        })
      });

      if (updateResponse.ok) {
        console.log(`✅ Fixed product: ${product.name}`);
      } else {
        console.log(`❌ Failed to fix product: ${product.name}`);
      }
    }

    console.log('🎉 All broken images fixed!');

  } catch (error) {
    console.error('Error fixing broken images:', error);
  }
}

fixBrokenImage();

fix-specific-image.js.content:
// Fix specific broken image in database
import fetch from 'node-fetch';

async function fixSpecificImage() {
  try {
    console.log('🔍 Getting all products to check images...');

    const response = await fetch('http://localhost:5000/api/products');
    const products = await response.json();

    const brokenImageId = 'photo-1583391733956-6c78276477e2';
    const workingImageUrl = 'https://images.unsplash.com/photo-1594633313593-bab3825d0caf?w=400&h=400&fit=crop&auto=format';

    for (const product of products) {
      if (product.images && product.images.some(img => img.includes(brokenImageId))) {
        console.log(`🔧 Fixing product: ${product.name} (ID: ${product.id})`);

        // Replace all instances of broken image
        const fixedImages = product.images.map(img => 
          img.includes(brokenImageId) ? workingImageUrl : img
        );

        // Also fix imageUrl if it exists
        let fixedImageUrl = product.imageUrl;
        if (fixedImageUrl && fixedImageUrl.includes(brokenImageId)) {
          fixedImageUrl = workingImageUrl;
        }

        const updateData = {
          name: product.name,
          description: product.description,
          price: product.price,
          category: product.category,
          storeId: product.storeId,
          images: fixedImages,
          imageUrl: fixedImageUrl || product.imageUrl,
          stock: product.stock || 100,
          isActive: product.isActive !== false
        };

        console.log('Update data:', { id: product.id, images: fixedImages, imageUrl: fixedImageUrl });

        const updateResponse = await fetch(`http://localhost:5000/api/products/${product.id}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(updateData)
        });

        if (updateResponse.ok) {
          console.log(`✅ Successfully fixed product: ${product.name}`);
        } else {
          const errorText = await updateResponse.text();
          console.log(`❌ Failed to fix product: ${product.name} - ${errorText}`);
        }
      }
    }

    console.log('🎉 Image fix process completed!');

  } catch (error) {
    console.error('Error:', error);
  }
}

fixSpecificImage();

client/src/hooks/useSoundEffects.tsx.content:
import { createContext, useContext, useEffect, useState } from 'react';
import { soundManager, playSound } from '@/lib/soundEffects';

interface SoundContextType {
  isEnabled: boolean;
  volume: number;
  setEnabled: (enabled: boolean) => void;
  setVolume: (volume: number) => void;
  play: (soundName: string, options?: { force?: boolean }) => void;
  playCartAdd: () => void;
  playCartRemove: () => void;
  playCartClear: () => void;
  playOrderPlaced: () => void;
  playOrderConfirmed: () => void;
  playOrderReady: () => void;
  playNotification: () => void;
  playMessage: () => void;
  playAlert: () => void;
  playSuccess: () => void;
  playError: () => void;
  playButtonClick: () => void;
  playToggle: () => void;
  playTabSwitch: () => void;
  playModalOpen: () => void;
  playModalClose: () => void;
  playProductLike: () => void;
  playReviewSubmit: () => void;
  playPaymentSuccess: () => void;
  playDeliveryUpdate: () => void;
  testAllSounds: () => void;
}

const SoundContext = createContext<SoundContextType | undefined>(undefined);

export function SoundProvider({ children }: { children: React.ReactNode }) {
  const [isEnabled, setIsEnabled] = useState(soundManager.isEnabled());
  const [volume, setVolume] = useState(soundManager.getVolume());

  useEffect(() => {
    // Sync with sound manager when component mounts
    setIsEnabled(soundManager.isEnabled());
    setVolume(soundManager.getVolume());
  }, []);

  const setEnabled = (enabled: boolean) => {
    setIsEnabled(enabled);
    soundManager.setEnabled(enabled);
  };

  const setVolumeValue = (vol: number) => {
    setVolume(vol);
    soundManager.setVolume(vol);
  };

  const contextValue: SoundContextType = {
    isEnabled,
    volume,
    setEnabled,
    setVolume: setVolumeValue,
    play: soundManager.play.bind(soundManager),
    playCartAdd: playSound.cartAdd,
    playCartRemove: playSound.cartRemove,
    playCartClear: playSound.cartClear,
    playOrderPlaced: playSound.orderPlaced,
    playOrderConfirmed: playSound.orderConfirmed,
    playOrderReady: playSound.orderReady,
    playNotification: playSound.notification,
    playMessage: playSound.message,
    playAlert: playSound.alert,
    playSuccess: playSound.success,
    playError: playSound.error,
    playButtonClick: playSound.buttonClick,
    playToggle: playSound.toggle,
    playTabSwitch: playSound.tabSwitch,
    playModalOpen: playSound.modalOpen,
    playModalClose: playSound.modalClose,
    playProductLike: playSound.productLike,
    playReviewSubmit: playSound.reviewSubmit,
    playPaymentSuccess: playSound.paymentSuccess,
    playDeliveryUpdate: playSound.deliveryUpdate,
    testAllSounds: soundManager.test.bind(soundManager),
  };

  return (
    <SoundContext.Provider value={contextValue}>
      {children}
    </SoundContext.Provider>
  );
}

export function useSoundEffects(): SoundContextType {
  const context = useContext(SoundContext);
  if (!context) {
    throw new Error('useSoundEffects must be used within a SoundProvider');
  }
  return context;
}

// Convenience hook for common actions
export function useAppSounds() {
  const sound = useSoundEffects();

  return {
    // Cart actions
    onCartAdd: sound.playCartAdd,
    onCartRemove: sound.playCartRemove,
    onCartClear: sound.playCartClear,

    // Order actions
    onOrderPlaced: sound.playOrderPlaced,
    onOrderConfirmed: sound.playOrderConfirmed,
    onOrderReady: sound.playOrderReady,

    // UI feedback
    onSuccess: sound.playSuccess,
    onError: sound.playError,
    onButtonClick: sound.playButtonClick,
    onNotification: sound.playNotification,
    onAlert: sound.playAlert,

    // Product interactions
    onProductLike: sound.playProductLike,
    onReviewSubmit: sound.playReviewSubmit,
    onPaymentSuccess: sound.playPaymentSuccess,

    // Admin actions
    onApprove: sound.playSuccess,
    onReject: sound.playError,
    onModalOpen: sound.playModalOpen,
    onModalClose: sound.playModalClose,
    onTabSwitch: sound.playTabSwitch,

    // Settings
    settings: {
      isEnabled: sound.isEnabled,
      volume: sound.volume,
      setEnabled: sound.setEnabled,
      setVolume: sound.setVolume,
    },

    // Test functions
    testAll: sound.testAllSounds,
    play: sound.play,
  };
}

